INSERT INTO `dress_data` VALUES (/*id*/7017000, /*name*/"てるてる珠美To You !", /*description*/"梅雨をテーマにした、珠美の衣装。\n振袖にレインブーツの組み合わせがキュート。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7018000, /*name*/"サンライトオブフラワー", /*description*/"太陽を浴びて輝くドレス。\nあたたかな陽光によって織られている。\nこのドレスと共に、少女たちの笑顔も花開く。", /*open_type*/2, /*dress_type*/0, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7019000, /*name*/"Cheese ! Cheese !", /*description*/"ねずみをテーマにした、肇の衣装。\n愛らしい髪型と、チーズなフリルがポイント。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7020000, /*name*/"宵の花衣", /*description*/"宵の花を彩る衣装。\nいずれ散りゆく運命なれど、\n命を燃やして咲く恋は美しい。\nその艶姿は、見る者の心に永久に残る。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7021000, /*name*/"春纏う霞衣", /*description*/"桜のように咲き誇る、少女たちの衣装。\n芽吹き、咲き、散ってゆく。\nそれでも桜はまた咲き、皆を惹きつける。\nそしてまた、満開の笑顔が花開く。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7022000, /*name*/"スクールメイト・ブレザー", /*description*/"少女たちの青春を包む制服ドレス。\nBakuBakuのハートにしっかり封をして、\nステキな恋を届けよう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7023000, /*name*/"セーシュン・チアーズ", /*description*/"青春を応援する少女たちの制服ドレス。\n上手くいかない時も、つまづいてしまう時も\nある。それでも、今という時を全力で走ろう。\nその情熱は、輝く明日へと繋がっている。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7024000, /*name*/"プレイ・イット・アンサー", /*description*/"嵐を巻き起こす少女たちのドレス。\n個性と個性がぶつかり合い、やがてひとつの\n形となる。だが、それが唯一の答えではない。\n無限の答えが、少女たちの前に広がっている。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7025000, /*name*/"セクシー・フォー・ジャスティス", /*description*/"ギルティを許さない少女たちのドレス。\nギルティあるところに姿あり、姿あるところに\nセクシーあり ! この世の悪事にお仕置きを !\n行け行け、セクシーギルティ !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7026000, /*name*/"マイディア・ドリーム", /*description*/"夢を見続ける、少女たちのドレス。\n輝きたい気持ちを持ち続ける限り、\nアイドルの物語が終わることはない。\nいつまでもどこまでも、夢は続いていく。", /*open_type*/2, /*dress_type*/0, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7027000, /*name*/"スターリー・フューチャーズ", /*description*/"未来に向かって星のように輝き続ける\nアイドルたちの特別な衣装。\nさあ笑って、\n「これからも、アイドル ! ! ! ! ! 」", /*open_type*/6, /*dress_type*/0, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7028000, /*name*/"メイク・マイ・スマイル", /*description*/"笑顔をみんなに届ける、少女たちのドレス。\n変わりたいと願うのは、憧れがあるから。\n昨日までの自分から受け取ったバトンが、\n明日からもっと輝く自分にしてくれる。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7029000, /*name*/"トリニティ・スパークル", /*description*/"磨かれることで輝きを増す少女たちのドレス。\n仲間でライバルだから、譲れない想いがある。\n見ている方向はそれぞれ違っていても、\n本気のぶつかり合いは、頂へと到達する。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7030000, /*name*/"パレット・カラー・ステアー", /*description*/"ときめく未来を描く、少女たちのドレス。\n目の前に広がる真っ白なキャンバスに、\nどんな未来を彩っていくのかは自分次第。\n夢を信じながら、少女たちは階段を上がる。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7031000, /*name*/"スパイシー・デリバリー", /*description*/"ピリッとした刺激をくれる少女たちのドレス。\nスパイスの効いたカレーが美味しいように、\n人生には刺激が欠かせない。隠し味の\nスマイルが最高のポジティブを届けるよ !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7032000, /*name*/"花咲舞衣", /*description*/"月夜に咲き綻ぶ、少女たちの衣装。\n巡り合えた糸は固く結ばれ、互いに引き合う\nことで、幻想的なまでの光景を作り出す。\n花々が舞う姿は、人々を夢心地へと誘う。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7033000, /*name*/"ファースト・ステップ", /*description*/"踏み出す勇気を知る、少女たちのドレス。\n失敗は怖いし、苦手から逃げ出したくもなる。\nそれでも、チャレンジを諦めたくない。\n勇気を持って、一歩を踏み出そう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7034000, /*name*/"スキャンダラス・ナイト", /*description*/"ゴシップすら味方にするギャルたちのドレス。\n派手なウワサを着飾ることで、少女はさらに\nセクシーになる。最強のギャルは今日も、\n世界中を振り回していく。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7035000, /*name*/"オタク・マッシグラ", /*description*/"オタク愛を叫ぶ、少女たちのドレス。\n人に理解されないのは、苦しいかもしれない。\nそれでも「好き」を貫くあなたの姿は、\nきっと誰よりも輝いている。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7036000, /*name*/"おひさまたより", /*description*/"太陽のようにあたたかい、少女たちのドレス。\n心地よい音が心を撫でていき、ぽかぽかとした\n気持ちにしてくれる。さぁ、想いのままに\n翼を広げて、大空に夢を描きにいこう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7037000, /*name*/"フェイク・ラヴ", /*description*/"心を燃やす、少女たちのドレス。\n灰になると解っていても構わない。\n胸に抱えた想いを激しい炎に変えて、\n自らの在り方を最後の瞬間まで貫き通そう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7038000, /*name*/"リトル・ユーフォリア", /*description*/"胸の内に想いを秘めた少女たちのドレス。\n移り変わっていく景色の中で、\n小さな勇気を力に一歩踏み出そうとする。\nそんな彼女たちを、優しく包んでくれる。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7039000, /*name*/"タイニー・デビル", /*description*/"イタズラが大好きな少女たちのドレス。\n身にまとえば、たちまちワルい子に変身。\n心の枷を解き放って、刺激に満ちた夜へと\n少女たちを連れ出してくれるだろう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7040000, /*name*/"あなたのトナカイさん", /*description*/"クリスマスをテーマにした、智絵里の衣装。\nモコモコ衣装とトナカイ耳がチャーミング。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7041000, /*name*/"ハートフル・トッピング", /*description*/"甘い幸せをお届けする、少女たちのドレス。\nこだわりの一品を、心を込めて作ってくれる。\nそのスイーツは美味しいだけではなく、\nたくさんの笑顔と幸せをも生み出していく。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7042000, /*name*/"ゾンビィ・ハート", /*description*/"死んでも夢を叶えたい少女たちのドレス。\n時代を超えて伝説は集う。己のサガを再び\nこの世界に刻むために。生きる屍になったと\nしても、その瑞々しい輝きは曇ることはない。", /*open_type*/2, /*dress_type*/1, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7043000, /*name*/"県立北高校制服", /*description*/"世界を大いに盛り上げる少女達の制服ドレス。\n世の中がつまらないなら、自分たちで面白く\nしてしまおう。不思議な力はなくても、\n仲間と一緒なら変えていけるはずだ。", /*open_type*/2, /*dress_type*/1, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7044000, /*name*/"カメラしょうじょ", /*description*/"夏をテーマにした、凪の衣装。\nユニークなシャツの柄が、夏を感じさせる。\nカメラと一緒に、最高の景色を探しに行こう。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7045000, /*name*/"ノトーリアス・ガール", /*description*/"既成の価値観に抗う少女たちのドレス。\n燃えるような色は、彼女たちの決意の証。\n他の何にも縛られることなく、己の意志を\n貫き通し、この世界を生き抜いていく。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7046000, /*name*/"ヒーロースピリッツ", /*description*/"己の正義を信じる少女たちのスーツ。\nどんな逆境の中にいても、人々の笑顔を\n守るため、仲間たちと共に立ち上がる。\nヒーローたちのそんな覚悟を象徴している。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7047000, /*name*/"ハングリー・デザイア", /*description*/"大人の束縛を拒む少女たちのドレス。\n彼女たちが身にまとうのは、大胆でしたたかな\n揺るぎない意志の証。それは危険な闇の中でも\n子どもたちの心を奮い立たせるだろう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7048000, /*name*/"スウィート・テラー", /*description*/"恐ろしい物語を紡ぐ少女たちのドレス。\nひとりでは心細くても、みんなと一緒なら、\nホラーでも楽しい思い出に変えられる。\nそんな仲間たちの心を繋ぐ、舞台衣装。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7049000, /*name*/"パッショネイト・フラワー", /*description*/"夏の情熱に身を任せる少女たちのドレス。\n鮮やかな花柄は、日差しを受けて咲き誇る\n南国の花のよう。そして彼女たちも、舞台上で\n熱く舞い踊り、魅惑の花を咲かせるだろう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7050000, /*name*/"リレイト・ストーリー", /*description*/"自分たちの物語をつづる少女たちのドレス。\n舞台できらめくこの衣装も、数々の出会いから\n始まったキセキの証。積み重ねてきた思い出と\n描き続けていく未来の景色を、彩ってくれる。", /*open_type*/2, /*dress_type*/0, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7051000, /*name*/"プリンセス・オブ・テン", /*description*/"輝く10人の少女たちのドレス。\n数多の人を魅了し、高みに至った栄光の証。\n散りばめられた星々の輝きが、彼女たちの\n気高き姿を煌びやかに彩っている。", /*open_type*/3, /*dress_type*/1, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7052000, /*name*/"ピュア・クレイドル", /*description*/"管理された箱庭で生きる少女たちのドレス。\n子どもたちを優しく包み育む、大人たちの愛。\nだがそれは時として、未来に芽吹く可能性を\n抑圧する檻となるのかもしれない。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7053000, /*name*/"あんず餅", /*description*/"お正月をテーマにした、杏の衣装。\n正月休みは何もせず、ずっと寝て過ごしたい。\nついでに不労所得も欲しい。そんな心の表れ。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7054000, /*name*/"アイディール・カラー", /*description*/"理想を実現する少女たちのドレス。\n少女たちが思い描き、追い求めた気高さ。\nその輝きはいま、鮮烈な真紅の衣と靴となり\n舞台に立つ彼女たちを美しく飾り上げている。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7055000, /*name*/"レイジング・ソウル", /*description*/"世界に声を響かせる少女たちの衣装。\n鮮烈な姿と激情の旋律を、彼女たちは\nその身にまとう。残酷な世界に、\n自分たちの存在を刻みつけるために。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7056000, /*name*/"しろくま♡", /*description*/"夏をテーマにした、かな子の衣装。\nしっかりした着ぐるみだが、中は意外と快適。\nしろくまの気分で夏も涼しく過ごせそう。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7057000, /*name*/"ミラクル・キラパス", /*description*/"時空を超えてめぐり逢った少女たちのドレス。\nいまできる全力で、みんなに笑顔を。\nこれは、そんな願いで形作られた、\n幸せを届ける魔法。", /*open_type*/3, /*dress_type*/1, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7059000, /*name*/"ビビッド・マイ・ワールド", /*description*/"自分の「好き」を貫く少女たちのドレス。\nそれぞれ個性があって、違うからこそ面白い。\nそんな想いを形にした一着は、身にまとう者を\nより色鮮やかな世界へと連れてゆく。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7060000, /*name*/"フォール・イン・ハート", /*description*/"恋に恋する少女たちのドレス。\nライバルたくさんで困っちゃう？\nいいえ。みんな、ピュアな恋の同士です。\nお揃いの服を着て、素敵な恋を楽しもう !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7061000, /*name*/"ベル・エポック", /*description*/"美しくしなやかに咲く少女たちのドレス。\n軽やかなデザインは春風を纏い、着る者を\n優しく未来へと誘う。さぁ、勇気を出して。\n何度でも、自分だけの花を咲かせよう。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7062000, /*name*/"アンノウン・フォーミュラ", /*description*/"駆け引きを楽しむ少女たちのドレス。\n闇夜に溶けるように、密やかに、かつ大胆に。\n奥底に眠る魅力まで引き出してくれる。\n秘密の香りを纏い、狙った獲物は逃さない。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7063000, /*name*/"星合いの願い衣", /*description*/"星を迎える少女たちの衣装。\nさらりと身に纏って、同じ空を見上げれば、\n伝えたかった想いが言の葉となり流れだす。\n肩を並べる大切な人と、心が重なる一着。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7064000, /*name*/"まりん・ふぁんたじー", /*description*/"海の楽しさを伝える少女たちの衣装。\nどんな荒波が来ても、大物が待ち構えていても\nこの衣装があれば大丈夫 !\n明るく元気に、美味しい時間を楽しもう♪", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7065000, /*name*/"黒ネコサンタ", /*description*/"冬とクリスマスをテーマにした、茄子の衣装。\n縁起のいい黒ネコサンタが、\n癒しと幸せをみんなにプレゼント。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7066000, /*name*/"ピンクのふわふわワンピース", /*description*/"春とお花見をテーマにした、あきらの衣装。\n春らしい柔らかなピンク色で気分を変えて。\nシンプルながら、こだわりも光る一着。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7067000, /*name*/"リトルマイシンデレラ", /*description*/"明日に飛び込む少女たちのドレス。\n両手いっぱいに夢と希望を抱えて、\n輝くステージへと舞い降りる。\n私たちの未来がここから始まっていく。", /*open_type*/2, /*dress_type*/1, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7068000, /*name*/"#ユニーク・ライクス", /*description*/"自分らしく在ることを選んだ少女たちの衣装。\nバラバラな個性はそのままに、ありのまま。\nなんでもない日常こそ、居心地がいいから。\n楽しいことも、ずっと #募集中 !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7069000, /*name*/"ファブ＋モア", /*description*/"自らの可能性を信じる少女たちのドレス。\n変わりたいけど変われない…そんな悩みは\n脱ぎ捨てて、この服と共に生まれ変わろう !\n新しい自分が待ってるはず☆", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7070000, /*name*/"ガンズ・ソウル", /*description*/"愉快な非日常を生きる者たちの衣装。\nジャケットに袖を通せば、退屈な日常も\nがらりと変化する。このイカれた世界で、\n想いを弾丸に込めて。信念を撃ち貫け !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7071000, /*name*/"レイジング・リベレイター", /*description*/"世界を変えるため全力で叫ぶ者たちの衣装。\n残酷な現実に屈してしまいそうなときは\nこの服を身に纏い、ありったけの想いを叫べ。\n革命は立ち向かう意志から始まる。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7072000, /*name*/"アオハルモラトリアム", /*description*/"遠い未来に想いを馳せる少女たちの制服。\nそれは過ぎ去った日々の証。役目を終えた\nあとも心に残り、永遠に寄り添い続ける。\nそんな日々に、親愛と敬意をこめて。", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7073000, /*name*/"ブリリアント・ハピネス", /*description*/"自由を求めつづける少女たちの制服。\n勇気が湧いてくるお揃いの服をまとったら、\n立ちはだかるどんな壁だって、打ち壊せる。\n自分らしく、笑って生きていこう !", /*open_type*/5, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
INSERT INTO `dress_data` VALUES (/*id*/7074000, /*name*/"とうもろこし〜♡", /*description*/"夏らしい食べ物をテーマにした、志希の衣装。\nとうもろこしをまるごと身に纏い、\n裸足のまま、弾けるように駆け出そう。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3, /*closet_type*/0, /*brand_id*/0, /*view_chara_id*/0);
