INSERT INTO `dress_data` VALUES (/*id*/7001000, /*name*/"プレゼントオブスノウ", /*description*/"赤や緑のリボンで包んだ雪のようなドレス。\n星の光が煌めく夜、季節の扉が開いて届いた\n真っ白なプレゼント。そのドレスを翼にして\nファンの元へ駆けだす少女たちは天使のよう。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7002000, /*name*/"フレグランスオブナイト", /*description*/"忘れられない夜を彩るドレス。\n夜のあいだにだけ咲く花たちがある。\n花びらから溢れ出すのは、魅惑の香り。\nその芳しい蜜は人を惑わし、口づけを誘う。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7003000, /*name*/"マーチング☆メロディーズ", /*description*/"マーチングバンド風の可愛らしいドレス。\nリズムが聞こえたら、小さな楽団が大集合。\nカラフルでゆかいな行進が始まるよ !", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7004000, /*name*/"ヴァルキュリア・オース", /*description*/"未来を掴みとるための、戦いのドレス。\n汚れなき純白の制服に身を包み、\n己が意志を高らかに歌う姿は、\n神話に謳われた戦乙女のよう。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7005000, /*name*/"雷舞弩麗守－炎－", /*description*/"己の信じる道を\n真っ直ぐに駆け抜ける者の弩麗守。\n彼女たちの炎のような生き様に、\n見る者の心にもアツい火が灯る。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7006000, /*name*/"スカーレット・ローズ", /*description*/"永遠を夢見て、愛を捧げる乙女のドレス。\n真紅の薔薇には棘があり、危うさ故に美しい。\n一途な想いと運命が編み込まれている。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7007000, /*name*/"マリンガール・カーニバル", /*description*/"太陽が映える、海に舞う者たちのドレス。\n祭りの陽気と爽やかな潮風が、\nアツい夏を盛り上げる。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7008000, /*name*/"スウィート・パティシエール", /*description*/"甘美な夢を見せる少女たちのドレス。\n彼女たちがつくりだす幻想は、\nどんなお菓子よりも甘く優しく、\n見る者の心をとらえて離さない。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7009000, /*name*/"シンセサイズ・ハーモニー", /*description*/"スタイリッシュに魅せる、クールなドレス。\n鮮やかに舞う姿は、電脳世界の歌姫のよう。\n計算されつくしたフォルムから覗く\n生身の感情が、見る者を惹きつける。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7010000, /*name*/"ウィル・フォーエバー", /*description*/"愛を祝福する少女たちのドレス。\n純真な乙女たちの幸福な願いと、\n永遠の誓いが込められている。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7011000, /*name*/"デイバイデイ・プレゼント", /*description*/"積み重ねた日々を讃えるドレス。\n少女たちを祝う贈り物。\n育んできた絆が、彼女たちを輝かせる。", /*open_type*/2, /*dress_type*/0);
INSERT INTO `dress_data` VALUES (/*id*/7012000, /*name*/"リトル・カラフル", /*description*/"少女たちの主張が詰まった、ポップな衣装。\nカラフルなライムの中に、\nひとりひとりの色が主張している。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7013000, /*name*/"お伽衣装・不思議の国のアリス", /*description*/"一日の幻を形にした衣装。\n『不思議の国のアリス』がモチーフ。\n様々な役柄が、少女たちを楽しく彩る。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7014000, /*name*/"お伽衣装・ヘンゼルとグレーテル", /*description*/"一日の幻を形にした衣装。\n『ヘンゼルとグレーテル』がモチーフ。\n様々な役柄が、少女たちを楽しく彩る。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7015000, /*name*/"お伽衣装・白雪姫", /*description*/"一日の幻を形にした衣装。\n『白雪姫』がモチーフ。\n様々な役柄が、少女たちを楽しく彩る。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7016000, /*name*/"お伽衣装・魔法の司書", /*description*/"一日の幻を形にした衣装。\n魔法図書館の司書がモチーフ。\n様々な役柄が、少女たちを楽しく彩る。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7017000, /*name*/"てるてる珠美To You !", /*description*/"梅雨をテーマにした、珠美の衣装。\n振袖にレインブーツの組み合わせがキュート。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7018000, /*name*/"サンライトオブフラワー", /*description*/"太陽を浴びて輝くドレス。\nあたたかな陽光によって織られている。\nこのドレスと共に、少女たちの笑顔も花開く。", /*open_type*/2, /*dress_type*/0);
INSERT INTO `dress_data` VALUES (/*id*/7019000, /*name*/"Cheese ! Cheese !", /*description*/"ねずみをテーマにした、肇の衣装。\n愛らしい髪型と、チーズなフリルがポイント。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7020000, /*name*/"宵の花衣", /*description*/"宵の花を彩る衣装。\nいずれ散りゆく運命なれど、\n命を燃やして咲く恋は美しい。\nその艶姿は、見る者の心に永久に残る。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7021000, /*name*/"春纏う霞衣", /*description*/"桜のように咲き誇る、少女たちの衣装。\n芽吹き、咲き、散ってゆく。\nそれでも桜はまた咲き、皆を惹きつける。\nそしてまた、満開の笑顔が花開く。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7022000, /*name*/"スクールメイト・ブレザー", /*description*/"少女たちの青春を包む制服ドレス。\nBakuBakuのハートにしっかり封をして、\nステキな恋を届けよう。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7023000, /*name*/"セーシュン・チアーズ", /*description*/"青春を応援する少女たちの制服ドレス。\n上手くいかない時も、つまづいてしまう時も\nある。それでも、今という時を全力で走ろう。\nその情熱は、輝く明日へと繋がっている。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7024000, /*name*/"プレイ・イット・アンサー", /*description*/"嵐を巻き起こす少女たちのドレス。\n個性と個性がぶつかり合い、やがてひとつの\n形となる。だが、それが唯一の答えではない。\n無限の答えが、少女たちの前に広がっている。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7025000, /*name*/"セクシー・フォー・ジャスティス", /*description*/"ギルティを許さない少女たちのドレス。\nギルティあるところに姿あり、姿あるところに\nセクシーあり ! この世の悪事にお仕置きを !\n行け行け、セクシーギルティ !", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7026000, /*name*/"マイディア・ドリーム", /*description*/"夢を見続ける、少女たちのドレス。\n輝きたい気持ちを持ち続ける限り、\nアイドルの物語が終わることはない。\nいつまでもどこまでも、夢は続いていく。", /*open_type*/3, /*dress_type*/0);
INSERT INTO `dress_data` VALUES (/*id*/7027000, /*name*/"スターリー・フューチャーズ", /*description*/"未来に向かって星のように輝き続ける\nアイドルたちの特別な衣装。\nさあ笑って、\n「これからも、アイドル ! ! ! ! ! 」", /*open_type*/6, /*dress_type*/0);
INSERT INTO `dress_data` VALUES (/*id*/7028000, /*name*/"メイク・マイ・スマイル", /*description*/"笑顔をみんなに届ける、少女たちのドレス。\n変わりたいと願うのは、憧れがあるから。\n昨日までの自分から受け取ったバトンが、\n明日からもっと輝く自分にしてくれる。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7029000, /*name*/"トリニティ・スパークル", /*description*/"磨かれることで輝きを増す少女たちのドレス。\n仲間でライバルだから、譲れない想いがある。\n見ている方向はそれぞれ違っていても、\n本気のぶつかり合いは、頂へと到達する。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7030000, /*name*/"パレット・カラー・ステアー", /*description*/"ときめく未来を描く、少女たちのドレス。\n目の前に広がる真っ白なキャンバスに、\nどんな未来を彩っていくのかは自分次第。\n夢を信じながら、少女たちは階段を上がる。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7031000, /*name*/"スパイシー・デリバリー", /*description*/"ピリッとした刺激をくれる少女たちのドレス。\nスパイスの効いたカレーが美味しいように、\n人生には刺激が欠かせない。隠し味の\nスマイルが最高のポジティブを届けるよ !", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7032000, /*name*/"花咲舞衣", /*description*/"月夜に咲き綻ぶ、少女たちの衣装。\n巡り合えた糸は固く結ばれ、互いに引き合う\nことで、幻想的なまでの光景を作り出す。\n花々が舞う姿は、人々を夢心地へと誘う。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7033000, /*name*/"ファースト・ステップ", /*description*/"踏み出す勇気を知る、少女たちのドレス。\n失敗は怖いし、苦手から逃げ出したくもなる。\nそれでも、チャレンジを諦めたくない。\n勇気を持って、一歩を踏み出そう。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7034000, /*name*/"スキャンダラス・ナイト", /*description*/"ゴシップすら味方にするギャルたちのドレス。\n派手なウワサを着飾ることで、少女はさらに\nセクシーになる。最強のギャルは今日も、\n世界中を振り回していく。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7035000, /*name*/"オタク・マッシグラ", /*description*/"オタク愛を叫ぶ、少女たちのドレス。\n人に理解されないのは、苦しいかもしれない。\nそれでも「好き」を貫くあなたの姿は、\nきっと誰よりも輝いている。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7036000, /*name*/"おひさまたより", /*description*/"太陽のようにあたたかい、少女たちのドレス。\n心地よい音が心を撫でていき、ぽかぽかとした\n気持ちにしてくれる。さぁ、想いのままに\n翼を広げて、大空に夢を描きにいこう。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7037000, /*name*/"フェイク・ラヴ", /*description*/"心を燃やす、少女たちのドレス。\n灰になると解っていても構わない。\n胸に抱えた想いを激しい炎に変えて、\n自らの在り方を最後の瞬間まで貫き通そう。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7038000, /*name*/"リトル・ユーフォリア", /*description*/"胸の内に想いを秘めた少女たちのドレス。\n移り変わっていく景色の中で、\n小さな勇気を力に一歩踏み出そうとする。\nそんな彼女たちを、優しく包んでくれる。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7039000, /*name*/"タイニー・デビル", /*description*/"イタズラが大好きな少女たちのドレス。\n身にまとえば、たちまちワルい子に変身。\n心の枷を解き放って、刺激に満ちた夜へと\n少女たちを連れ出してくれるだろう。", /*open_type*/5, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7040000, /*name*/"あなたのトナカイさん", /*description*/"クリスマスをテーマにした、智絵里の衣装。\nモコモコ衣装とトナカイ耳がチャーミング。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7041000, /*name*/"ハートフル・トッピング", /*description*/"甘い幸せをお届けする、少女たちのドレス。\nこだわりの一品を、心を込めて作ってくれる。\nそのスイーツは美味しいだけではなく、\nたくさんの笑顔と幸せをも生み出していく。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7042000, /*name*/"ゾンビィ・ハート", /*description*/"死んでも夢を叶えたい少女たちのドレス。\n時代を超えて伝説は集う。己のサガを再び\nこの世界に刻むために。生きる屍になったと\nしても、その瑞々しい輝きは曇ることはない。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7043000, /*name*/"県立北高校制服", /*description*/"世界を大いに盛り上げる少女達の制服ドレス。\n世の中がつまらないなら、自分たちで面白く\nしてしまおう。不思議な力はなくても、\n仲間と一緒なら変えていけるはずだ。", /*open_type*/2, /*dress_type*/1);
INSERT INTO `dress_data` VALUES (/*id*/7044000, /*name*/"カメラしょうじょ", /*description*/"夏をテーマにした、凪の衣装。\nユニークなシャツの柄が、夏を感じさせる。\nカメラと一緒に、最高の景色を探しに行こう。\nデザインした者の想いがこもっている。", /*open_type*/3, /*dress_type*/3);
INSERT INTO `dress_data` VALUES (/*id*/7045000, /*name*/"ノトーリアス・ガール", /*description*/"既成の価値観に抗う少女たちのドレス。\n燃えるような色は、彼女たちの決意の証。\n他の何にも縛られることなく、己の意志を\n貫き通し、この世界を生き抜いていく。", /*open_type*/5, /*dress_type*/3);
