INSERT INTO `garden_package` VALUES (/*package_id*/1, /*name*/"ひろえ ! うさぎロボ !", /*image*/"ひろえ ! うさぎロボ !", /*story_text*/"ひろえ ! うさぎロボ !\n\n説明しよう !\n愛と希望の街、モロボシティ。\nかつて、誰もがはぴはぴに暮らす\n世界一たのすぃー街だったそこはいまや、\nグラッシー帝国の手により壊滅していた。\nグラッシー帝国を打ち倒すことができるのは\n正義の科学者きらり博士がつくりあげた、\n「きらりんロボ」だけ !\nしかし !\nきらりんロボを動かすためには、\nエネルギーである「ハピネシウム」を\n集めなければならない。\nうさぎロボを操り、\nグラッシー帝国の大幹部「拘束のまゆ」の\n愛のリボンをかいくぐり、\nハピネシウムを集めるのだ !", /*tutorial_id*/21001, /*mission_name*/"ひろえ ! うさぎロボ !", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/2, /*name*/"はしれ ! うさぎロボ !", /*image*/"はしれ ! うさぎロボ !", /*story_text*/"はしれ ! うさぎロボ !\n\n説明しよう !\n愛と希望の街、モロボシティ。\nかつて、誰もがはぴはぴに暮らす\n世界一たのすぃー街だったそこはいまや、\nグラッシー帝国の手により壊滅していた。\nグラッシー帝国を打ち倒すことができるのは\n正義の科学者きらり博士がつくりあげた、\n「きらりんロボ」だけ !\nしかし !\nきらりんロボを動かすためには、\nエネルギーである「ハピネシウム」を\n集めなければならない。\nうさぎロボを操り、\nグラッシー帝国の大幹部「魅惑の奏」の\n誘惑を振り切って、\nハピネシウムを集めるのだ !", /*tutorial_id*/21002, /*mission_name*/"はしれ ! うさぎロボ !", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/3, /*name*/"はじけ ! うさぎロボ !", /*image*/"はじけ ! うさぎロボ !", /*story_text*/"はじけ ! うさぎロボ\n\n説明しよう !\n愛と希望の街、モロボシティ。\nかつて、誰もがはぴはぴに暮らす\n世界一たのすぃー街だったそこはいまや、\nグラッシー帝国の手により壊滅していた。\nグラッシー帝国を打ち倒すことができるのは\n正義の科学者きらり博士がつくりあげた、\n「きらりんロボ」だけ !\nしかし !\nきらりんロボを動かすためには、\nエネルギーである「ハピネシウム」を\n集めなければならない。\nうさぎロボを操り、\nグラッシー帝国の大幹部\n「グラスザニンジャあやめ」の隙をつき、\nハピネシウムを集めるのだ !", /*tutorial_id*/21003, /*mission_name*/"はじけ ! うさぎロボ !", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/4, /*name*/"決戦 ! グラッシーロボ", /*image*/"決戦 ! グラッシーロボ", /*story_text*/"決戦 ! グラッシーロボ\n\n説明しよう !\n愛と希望の街、モロボシティ。\nかつて、誰もがはぴはぴに暮らす\n世界一たのすぃー街だったそこはいまや、\nグラッシー帝国の手により壊滅していた。\nうさぎロボの活躍により、\nついに起動した「きらりんロボ」。\nしかし ! 迫りくるのは、\n全人類にメガネをかけさせることを目論む、\nグラッシー帝国総帥「グラッシーハルナ」と\n彼女が操る巨大ロボ「グラッシーロボ」。\nさぁ、説明はもう不要だ !\nきらりんロボを操って\nモロボシティに平和を取り戻してくれ !", /*tutorial_id*/21004, /*mission_name*/"決戦 ! グラッシーロボ", /*mission_target*/0);
INSERT INTO `garden_package` VALUES (/*package_id*/5, /*name*/"乃々を探せ ! レベル41 ! ～ののと不思議な絵本～", /*image*/"乃々を探せ ! レベル41 ! ～ののと不思議な絵本～", /*story_text*/"乃々を探せ !\n\n乃々がいなくなってしまった !\n魔法の図書館の司書に導かれた先は、\n不思議な不思議なお伽話の世界。\n乃々はその中に隠れてしまったらしい。\n「不思議の国のアリス」\n「白雪姫」\n「ヘンゼルとグレーテル」\nたくさんの仲間たちと一緒に、\nいろいろなお伽話の世界を旅して、\n乃々を探してあげよう。", /*tutorial_id*/21005, /*mission_name*/"乃々を探せ !", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/7, /*name*/"Cinderella Versus", /*image*/"Cinderella Versus", /*story_text*/"Cinderella Versus\n\nLIVEバトル ! !\nそれは、あまたのアイドルたちが\n歌と踊りで観客を魅了する、輝きの舞台 !\n\nそして同時に、\nプロデューサーの手腕が試される、\n戦いの舞台でもある !\n\nさまざまな個性と絆を持つ\nたくさんのアイドルたち !\n\nPパワーをやりくりしながら、\n自分だけのユニットを組み\nLIVEバトルを勝ち抜こう !\n\n舞踏会が、幕を開ける !", /*tutorial_id*/21006, /*mission_name*/"Cinderella Versus", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/8, /*name*/"ようこそ ! シンデレラキッチン !", /*image*/"ようこそ ! シンデレラキッチン !", /*story_text*/"アイドルたちと、\nお菓子屋さんを開こう !\n\nかわいいお店と、素敵な香りに誘われて、\n今日もたくさんのお客さんが、\nお菓子を楽しみにやってきます !\n\nだから、お店はいつも大忙し !\nでも、慌てなくても大丈夫。\n\nお客さんたちの注文をよーく見ながら、\nお菓子作りの順番を考えて。\n\nアイドルたちといっしょに、\nあまーいお菓子で、\nきてくれたみんなを笑顔にしよう !", /*tutorial_id*/21007, /*mission_name*/"シンデレラキッチン !", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/9, /*name*/"クイズ de シンデレラ", /*image*/"クイズ de シンデレラ", /*story_text*/"クイズ de シンデレラ\nぴにゃこら太は願った。\nもっとアイドルのみんなと遊びたいと。\nぴにゃこら太は考えた。\nもっとアイドルのみんなと\n仲良くなるためには？\n……ひらめいた ! クイズをしよう !\nほかのアイドルたちと一緒に\nどしどしクイズに答えていって\n優勝を目指そう♪", /*tutorial_id*/21008, /*mission_name*/"クイズ de シンデレラ", /*mission_target*/1);
INSERT INTO `garden_package` VALUES (/*package_id*/10, /*name*/"ゆるっとふわっと シンデレラ×フォト", /*image*/"ゆるっとふわっと シンデレラ×フォト", /*story_text*/"ゆるっとふわっと　シンデレラ×フォト\n\nアイドルたちの輝きは、\n宝石みたいにとってもキラキラ !\n無限の可能性を秘めたキャンバスで\n世界に一枚だけのキラメキを作ろう☆\n\nゆるっと手軽に♪\nふわっと可愛く☆\n\n君とアイドルの思い出を\n思う存分デコっちゃおう♪\n\nレッツ・シンデレラ×フォト !", /*tutorial_id*/21009, /*mission_name*/"ゆるっとふわっと シンデレラ×フォト", /*mission_target*/0);
