INSERT INTO `idel_vote_idol` VALUES (/*id*/67, /*campaign_id*/10005, /*card_id*/200668, /*use_type*/0, /*index*/1, /*description*/"{0}さんの想い、届いたわ。\nどんなに強い雨に打たれたって、\n立ち続けてみせる。気高く、折れない花のように。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/68, /*campaign_id*/10005, /*card_id*/200792, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーのご注文は\nア・タ・シ、ね？わかってたわ♪\n全身にぎゅっと詰まった魅力、届けに行くわよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/69, /*campaign_id*/10005, /*card_id*/200830, /*use_type*/0, /*index*/1, /*description*/"どんなレスラーも、応援の力で強くなれるんだ。\nアタシも同じさ。{0}の声が、\nアタシをアツく燃え上がらせてくれるっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/70, /*campaign_id*/10005, /*card_id*/200846, /*use_type*/0, /*index*/1, /*description*/"挑戦に期限はない……{0}くんが\n教えてくれたのよ。さぁ、重ねた年月分\n熟した私を、味わってもらいに行きましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/71, /*campaign_id*/10005, /*card_id*/200758, /*use_type*/0, /*index*/1, /*description*/"{0}さんは信じてくれているのね、\n私が輝くルミエールになれるって。\nええ、至上の煌めきを届けるわ。見守っていて。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/72, /*campaign_id*/10005, /*card_id*/200430, /*use_type*/0, /*index*/1, /*description*/"あったかいエール、ありがと♪\n{0}君がそばにいるだけで百人力ね。\n相性ピッタリなふたりの力で、夢を叶えましょ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/73, /*campaign_id*/10005, /*card_id*/200158, /*use_type*/0, /*index*/1, /*description*/"{0}さんの期待に応えたい……\nううん、応えるよ。任せとけって♪\n教えてくれた、あたしなりの輝き方でさ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/74, /*campaign_id*/10005, /*card_id*/200230, /*use_type*/0, /*index*/1, /*description*/"{0}さんの応援は、\nお店に並んだ眼鏡のように、上を向かせてくれます。\nその高みを目指して、進みますよっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/75, /*campaign_id*/10005, /*card_id*/200344, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーの熱い想い……\n俄然やる気出るっスね。\nパワーを得たアタシが見せる胸熱展開、お楽しみにっス !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/76, /*campaign_id*/10005, /*card_id*/200532, /*use_type*/0, /*index*/1, /*description*/"{0}くんの声は、熱いな。\n何度でも求めたくなる……私もそう在らなければね。\n誰しもが魅入られる姿を、披露しよう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/77, /*campaign_id*/10005, /*card_id*/200366, /*use_type*/0, /*index*/1, /*description*/"誰にも負けない、熱いロック魂 !\n{0}さんのと合わさって……\nふたりで奏でるセッションなら、もっと熱くなります !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/78, /*campaign_id*/10005, /*card_id*/200514, /*use_type*/0, /*index*/1, /*description*/"{0}さんの応援、もらったよ !\nアタシの一番魅力的なステップで、\nみんなが夢中になるようなステージ、届けるから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/79, /*campaign_id*/10005, /*card_id*/200398, /*use_type*/0, /*index*/1, /*description*/"夢に向かって成長していく千枝のこと\n{0}さんも見ていてくださいね。\n優しい応援を力にして、頑張りますからっ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/80, /*campaign_id*/10005, /*card_id*/200258, /*use_type*/0, /*index*/1, /*description*/"{0}さんが、背中を押してくれて……\nほんの少し、調子に乗ってしまいそうです。\n私の中に、勇気が芽生えるから……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/81, /*campaign_id*/10005, /*card_id*/200788, /*use_type*/0, /*index*/1, /*description*/"{0}さんが私の光になってくれるように、\n私も誰かの光になれるかしら。\n今の私なら……それを望んでも、いいわよね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/82, /*campaign_id*/10005, /*card_id*/200820, /*use_type*/0, /*index*/1, /*description*/"{0}の期待は、心地の良い重みだ。\n次なる景色を見たいと、\n想いを共にしてくれているのだからね。さぁ、いこうか。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/83, /*campaign_id*/10005, /*card_id*/200308, /*use_type*/0, /*index*/1, /*description*/"{0}さんが誇ってくださる器には、\nあらゆる軌跡が注がれています。\nどれも零せない……大切なひとしずくです。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/84, /*campaign_id*/10005, /*card_id*/200096, /*use_type*/0, /*index*/1, /*description*/"{0}さんがくれる想いを胸に、\n美波、もっと輝きます !\n人の心を照らすような、女神であり続けられるように。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/85, /*campaign_id*/10005, /*card_id*/200890, /*use_type*/0, /*index*/1, /*description*/"その応援に感謝いたします。\n{0}さんが示す先は私の目指す場所。\n美しい弦音を響かせ、共に向かいましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/86, /*campaign_id*/10005, /*card_id*/200850, /*use_type*/0, /*index*/1, /*description*/"まだ真っ白なキャンバスを、\n{0}さんと一緒に彩り続けます。\nこの煌めきを……額の外へと届けに行きましょうね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/87, /*campaign_id*/10005, /*card_id*/200206, /*use_type*/0, /*index*/1, /*description*/"積み重ねてきた経験があるから、堂々と立っていられます。\nそして{0}さんの声が、\n未来へ進む原動力なんですっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/88, /*campaign_id*/10005, /*card_id*/200108, /*use_type*/0, /*index*/1, /*description*/"{0}さんの言の葉は、\n私の物語を彩り、新たな頁を生んでくれますね。\nそこに広がる景色はきっと、輝きに満ちています。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/89, /*campaign_id*/10005, /*card_id*/200354, /*use_type*/0, /*index*/1, /*description*/"{0}はいつも\n予測を超えてくるわね……ふふっ、嬉しい誤算だわ。\nだから私も、未知数の夢を見られるの。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/90, /*campaign_id*/10005, /*card_id*/200468, /*use_type*/0, /*index*/1, /*description*/"{0}殿の応援を胸に、\nライラさん、いっぱいがんばりますです。\nそしてご褒美のアイスを、一緒にいただきましょー。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/91, /*campaign_id*/10005, /*card_id*/200562, /*use_type*/0, /*index*/1, /*description*/"ひろーい海のようなアイドル界で、\n七海はとっても立派に成長しました !\nプロデューサーのお墨付き、みんなもやみつきれすね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/92, /*campaign_id*/10005, /*card_id*/200892, /*use_type*/0, /*index*/1, /*description*/"ヘレンの名を冠し、ヘレンとしてここに在る。\nそして{0}の想いが重なれば、\n新たな境地も見えるはずよ。いきましょう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/93, /*campaign_id*/10005, /*card_id*/200374, /*use_type*/0, /*index*/1, /*description*/"{0}サンの応援は、腹の底に響いてくるな。\nだから、思いっきり声を出せる。\nどこまでも響くような、アタシの歌声を。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/94, /*campaign_id*/10005, /*card_id*/200868, /*use_type*/0, /*index*/1, /*description*/"{0}さんの真っすぐな声のように、\n私もひたむきに貫くわ。\n高みを目指し続けるっていう、信念をね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/95, /*campaign_id*/10005, /*card_id*/200550, /*use_type*/0, /*index*/1, /*description*/"彼方から観測するだけだった星も、今では……\n掌握も不可能ではないわ。\n{0}、貴方が導を与えてくれるのならね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/96, /*campaign_id*/10005, /*card_id*/200442, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーのエールで、\n燃えーる気分になってきました。\nこの勢いのまま、高みを目指して進みましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/97, /*campaign_id*/10005, /*card_id*/200074, /*use_type*/0, /*index*/1, /*description*/"月満ちる時、遥か天へのきざはしが建つ。\nともに頂へと至ろうぞ、我が友よ !\n（{0}プロデューサーと一緒に頑張ります ! ）", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/98, /*campaign_id*/10005, /*card_id*/200834, /*use_type*/0, /*index*/1, /*description*/"{0}さんの言葉はまるで、\n私たちのふたり旅のパスポートみたいね。\nそう、あなたがいれば、どこへだって行けるわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/99, /*campaign_id*/10005, /*card_id*/200772, /*use_type*/0, /*index*/1, /*description*/"ふふ、酔わせてくれるのね……{0}さん。\n甘くて芳しい声が、私を貪欲にさせてくれるの。\nもっと味わいたいって……ね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/100, /*campaign_id*/10005, /*card_id*/200184, /*use_type*/0, /*index*/1, /*description*/"{0}さんがくれたんだ。\nキラキラ輝くきっかけを。\nでも、まだ満足なんてしないんだから♪もっと上を目指すよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/101, /*campaign_id*/10005, /*card_id*/200836, /*use_type*/0, /*index*/1, /*description*/"言葉にできない気持ちが、胸イッパイ溢れてマス。\n{0}プロデューサーに貰った愛、\n伝えてきマスネ。Ever more !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/102, /*campaign_id*/10005, /*card_id*/200708, /*use_type*/0, /*index*/1, /*description*/"茫洋たる場所でも、{0}さんの声が\n行くべき道を示してくれるわ……。\n灯台のように、先を照らし続けていてね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/103, /*campaign_id*/10005, /*card_id*/200678, /*use_type*/0, /*index*/1, /*description*/"{0}さんの応援を力にして、\n思いっきり楽しみます。\nアイドルは、一番私らしくいられるものだから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/104, /*campaign_id*/10005, /*card_id*/200382, /*use_type*/0, /*index*/1, /*description*/"応援……好き……こころ、あったかい……。\n{0}のお願い……一緒だから……\nいっぱい、がんばる……見ててね……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/105, /*campaign_id*/10005, /*card_id*/200878, /*use_type*/0, /*index*/1, /*description*/"{0}君の情熱的な声、視線、想い……\nそのすべてが私を突き動かすの。\n人間の、とっても強い欲を満たそうと、ね……♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/106, /*campaign_id*/10005, /*card_id*/200702, /*use_type*/0, /*index*/1, /*description*/"{0}さんと私の想いが重なると、\n身も心も燃え上がってくるわね。\nふふっ……この熱は、貴方が教えてくれたのよ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/107, /*campaign_id*/10005, /*card_id*/200764, /*use_type*/0, /*index*/1, /*description*/"アタシの中の色、重なり合って随分鮮やかになったっす。\n{0}さんの声を差し色にして\n最高のアートを作り上げるっすよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/108, /*campaign_id*/10005, /*card_id*/200872, /*use_type*/0, /*index*/1, /*description*/"私、{0}さん、そしてみなさん……\n胸に広がる色とりどりの音を重ねて、\n美しいコンチェルトを奏でましょう……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/109, /*campaign_id*/10005, /*card_id*/200214, /*use_type*/0, /*index*/1, /*description*/"{0}さんが私を見てくれるように、\n私も、いつも見てるよ。だからその気持ちが\n本当に本物だって、知ってるの……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/110, /*campaign_id*/10005, /*card_id*/200806, /*use_type*/0, /*index*/1, /*description*/"ありがとぉ、{0}さん♪\nその声に乗って、すっごいとこまで羽ばたけそぉ !\n一緒に気分もアゲてったら、無敵だねぇ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/111, /*campaign_id*/10005, /*card_id*/200490, /*use_type*/0, /*index*/1, /*description*/"扉の先は、いつも素敵な大冒険 !\n{0}さんと一緒なら、\nどこへだって漕ぎ出せます ! ねっ、私の相棒さん♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/112, /*campaign_id*/10005, /*card_id*/200752, /*use_type*/0, /*index*/1, /*description*/"{0}さんに勇気をもらえたわ。\n一歩一歩高みへと近づけば、\nディーヴァとして輝く日が来る。そう信じているから。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/113, /*campaign_id*/10005, /*card_id*/200636, /*use_type*/0, /*index*/1, /*description*/"見るだけだった夢も、{0}さんとなら\n叶えられる……だから、前を向きます。\n次に描きたいものが、この先にあるから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/114, /*campaign_id*/10005, /*card_id*/200734, /*use_type*/0, /*index*/1, /*description*/"今日の運勢チェックは、必要ないわね。\n{0}に応援をもらって幸せだもの♪\nこの調子で最高のラッキーをつかむわよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/115, /*campaign_id*/10005, /*card_id*/200222, /*use_type*/0, /*index*/1, /*description*/"{0}さんは化かされたわけでもなく、\nちゃーんとあたしを見てくれるんだよね。\nふふ、ありがと。背中は任せたよー♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/116, /*campaign_id*/10005, /*card_id*/200420, /*use_type*/0, /*index*/1, /*description*/"{0}殿の力添え、痛み入ります !\n珠美の武士道は、ふたりの力あってこそ。\n如何なる壁も、切り拓いてみせますとも !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/117, /*campaign_id*/10005, /*card_id*/200786, /*use_type*/0, /*index*/1, /*description*/"明るい未来を目指して進みます。\n{0}さんが背中を押してくれるなら、\nまっすぐ前を見ていられますから。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/118, /*campaign_id*/10005, /*card_id*/200138, /*use_type*/0, /*index*/1, /*description*/"ありがとう、{0}さん。\n素直なお礼も、たまにはいいでしょう？\nそれとも、口づけをご所望？今はお預けよ。今は、ね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/119, /*campaign_id*/10005, /*card_id*/200496, /*use_type*/0, /*index*/1, /*description*/"{0}Pはいつも教えてくれるね。\n可能性は、数式で数えられるだけじゃない……\n無限って思えるぐらい、広がってるって。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/120, /*campaign_id*/10005, /*card_id*/200822, /*use_type*/0, /*index*/1, /*description*/"いろんな気持ちが、私の心にあって……\n{0}プロデューサーも同じですよね。\nだから、怖いものなんてありませんっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/121, /*campaign_id*/10005, /*card_id*/200332, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーさん、本気ですか？\nうぅ、わかってます、本気だって……。\nそんなの、やるくぼになるしか……あぅ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/122, /*campaign_id*/10005, /*card_id*/200292, /*use_type*/0, /*index*/1, /*description*/"ヴェーリチ……{0}プロデューサーの\n信じる心、伝わりました。\nみなさんを見守る、あたたかい光に、なりますね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/123, /*campaign_id*/10005, /*card_id*/200414, /*use_type*/0, /*index*/1, /*description*/"委細承知 ! {0}殿の激励、\nしかとこの身に刻まれましたぞ !\n大砲の如く力強い想いを胸に、突き進むでありますっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/124, /*campaign_id*/10005, /*card_id*/200524, /*use_type*/0, /*index*/1, /*description*/"{0}からつないだボール、\nオレが決めなくて誰が決めるってんだよ !\n任せとけ、世界中を沸かせてやるからな !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/125, /*campaign_id*/10005, /*card_id*/200198, /*use_type*/0, /*index*/1, /*description*/"天の光は、見かけよりも彼方に在る\nそれでも手を伸ばすのは、人の性か。\nやはりボクらの波長は同じ……それも悪くない。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/126, /*campaign_id*/10005, /*card_id*/200474, /*use_type*/0, /*index*/1, /*description*/"信じてくれる相手は、何より大事だぜ。\nそ、お前のこと。ひとりじゃ厳しい状況でも、\nふたりなら勝ち目が見えてくるからな。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/127, /*campaign_id*/10005, /*card_id*/200778, /*use_type*/0, /*index*/1, /*description*/"幸せなこと……たくさん増えちゃいました。\n{0}さんがくれるもの全部が、\n私の……幸せのメロディーなんです。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/128, /*campaign_id*/10005, /*card_id*/200482, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーがそばにいること、\nそれは幸運を超える、最上の幸福なんです。\nそう、運命の女神が微笑むくらい♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/129, /*campaign_id*/10005, /*card_id*/200720, /*use_type*/0, /*index*/1, /*description*/"すご……熱いね、Pサン。それだけ期待されてるってコト？\nま、いいデスけど。乗ってあげる。\nステージの熱さ、もっと教えてよ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/130, /*campaign_id*/10005, /*card_id*/200676, /*use_type*/0, /*index*/1, /*description*/"Pちゃん、応援ありがとー♪\nはーを知ってる人はもちろん、知らない人にも\nはーの魅力、届けようね ! ぜーったい、約束だよっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/131, /*campaign_id*/10005, /*card_id*/300078, /*use_type*/0, /*index*/1, /*description*/"未央ちゃんのパワーはまだまだ未知数 !\n{0}プロデューサーも同じ気持ちだよね☆\nよーし ! 全速力でいってみよーっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/132, /*campaign_id*/10005, /*card_id*/300164, /*use_type*/0, /*index*/1, /*description*/"{0}さんがくれるものは、\n嬉しくなるものばかりです。なので私もお返しで……\nあなたを幸せにしますからね♪", /*insert_word_type*/0);
