INSERT INTO `idel_vote_idol` VALUES (/*id*/200, /*campaign_id*/10006, /*card_id*/100730, /*use_type*/0, /*index*/1, /*description*/"あはっ♪ちゃーんと伝わってるよ、\n{0}くんの気持ち☆\nそんなに期待されたら、期待以上のことがしたくなっちゃう♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/201, /*campaign_id*/10006, /*card_id*/100536, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーと食べるパンは\nとても美味しいです !\n夢を叶えられたら、もっと美味しくなりますよね。あははー !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/202, /*campaign_id*/10006, /*card_id*/100644, /*use_type*/0, /*index*/1, /*description*/"{0}ぷろでゅーしゃーにはね、\nもっともっと笑ってほしいの。\nそのためにもくるみ、もっとアイドルがんばるからね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/203, /*campaign_id*/10006, /*card_id*/100742, /*use_type*/0, /*index*/1, /*description*/"瑛梨華ちんが狙えば、どんな相手でもI・CHI・KO・RO♪\n{0}ちゃんは特等席で、\nちゃーんと見ていてね☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/204, /*campaign_id*/10006, /*card_id*/100794, /*use_type*/0, /*index*/1, /*description*/"{0}さんに今よりも褒められたいんです。\nだってもっとカワイイ早耶になったら、\nもっと好きになるでしょ？うふっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/205, /*campaign_id*/10006, /*card_id*/100684, /*use_type*/0, /*index*/1, /*description*/"私と{0}さんなら、\n新しい風味を生み出すこともできますわ。\n私たちの可能性は、限りなく広がっていますもの。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/206, /*campaign_id*/10006, /*card_id*/100446, /*use_type*/0, /*index*/1, /*description*/"{0}様とともに進むのであれば、\n怖いものなど何ひとつありませんわ。\n遥かな高みを目指していきましょうね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/207, /*campaign_id*/10006, /*card_id*/100756, /*use_type*/0, /*index*/1, /*description*/"{0}さんの優しさに、\nふぇいふぇいはいつも助けられてるヨ。\nだから、精いっぱいの恩返し、期待しててネ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/208, /*campaign_id*/10006, /*card_id*/100568, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーは、作戦成功のカギ !\nあずきはとにかく頑張って走るから、\nサポート役はよろしくね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/209, /*campaign_id*/10006, /*card_id*/100680, /*use_type*/0, /*index*/1, /*description*/"{0}様と手を取り合って、\n新たなメロディーを生み出していきたいのです。\nそして、みなさんにお届けしましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/210, /*campaign_id*/10006, /*card_id*/100814, /*use_type*/0, /*index*/1, /*description*/"夢を見るだけじゃなくって、叶えられる場所まで\n{0}Pと来たんだね。\nよーし、みやびぃやっちゃうよー ! えへへ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/211, /*campaign_id*/10006, /*card_id*/100620, /*use_type*/0, /*index*/1, /*description*/"ここ一番の勝負所だからこそ、\n慎重に、でも大胆な手でいきましょう。大丈夫。\n私と{0}さんならきっと勝てるわ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/212, /*campaign_id*/10006, /*card_id*/100744, /*use_type*/0, /*index*/1, /*description*/"好敵手がたくさんいるからこそ面白いの !\nいくわよ、{0}プロデューサー。\nこの戦で目いっぱい傾いてやりましょう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/213, /*campaign_id*/10006, /*card_id*/100832, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーとなら、\nもっとたくさんの癒しを届けられます。\n私の活躍を、一番そばで見ていてくださいね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/214, /*campaign_id*/10006, /*card_id*/100766, /*use_type*/0, /*index*/1, /*description*/"私のこと、もっと大変身させてください !\n{0}さんとなら\n誰よりキラキラ輝く私になれちゃいますからぁ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/215, /*campaign_id*/10006, /*card_id*/100828, /*use_type*/0, /*index*/1, /*description*/"大きな願い事を、{0}さんと\n叶えていきますよ～。\n小さな一歩も、大きな夢に繋がっていますから～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/216, /*campaign_id*/10006, /*card_id*/100690, /*use_type*/0, /*index*/1, /*description*/"{0}さんの優しい声は、\n甘くてステキですぅ。私も負けずにあまあまになって、\nみんなをもっとメロメロにしますよ～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/217, /*campaign_id*/10006, /*card_id*/100838, /*use_type*/0, /*index*/1, /*description*/"いきましょう、{0}さん !\n今こそ、アイドルの謎を解き明かして、\n伝説のアイドル探偵になってみせますから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/218, /*campaign_id*/10006, /*card_id*/100728, /*use_type*/0, /*index*/1, /*description*/"{0}さんと紡いできた物語は、\nまだまだ終わりません。\n新たな1ページを、一緒に綴っていってください。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/219, /*campaign_id*/10006, /*card_id*/100714, /*use_type*/0, /*index*/1, /*description*/"腐っても、ユリユリはアイドル !\n{0}さんに推してもらえたなら、\nそれ以上の力で応えるじぇ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/220, /*campaign_id*/10006, /*card_id*/100462, /*use_type*/0, /*index*/1, /*description*/"不安を自信に ! 自信を確信に !\n{0}さんがいてくれたからアタシ、\n胸を張って自分はアイドルだって言えるんだよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/221, /*campaign_id*/10006, /*card_id*/100496, /*use_type*/0, /*index*/1, /*description*/"想いを胸に抱えるだけじゃなくて、言葉にして、\n行動で示します。{0}さんとなら\n願いは叶えられるってことを !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/222, /*campaign_id*/10006, /*card_id*/100456, /*use_type*/0, /*index*/1, /*description*/"小春がお姫様になった時、{0}さんは\n小春の隣で、一緒に笑っていてほしいです～♪\n頑張って叶えますから～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/223, /*campaign_id*/10006, /*card_id*/100476, /*use_type*/0, /*index*/1, /*description*/"新たな舞台へ歩み続けることを、誓います……。\nさぁいきましょう、{0}様。\n光が道を照らしてくださいますわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/224, /*campaign_id*/10006, /*card_id*/100506, /*use_type*/0, /*index*/1, /*description*/"さくらを、ず～っと見ててくださいねぇ。\n{0}プロデューサーさんのこと、\nも～っとドキドキワクワクさせますから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/225, /*campaign_id*/10006, /*card_id*/100804, /*use_type*/0, /*index*/1, /*description*/"今こそ、ラブ＆ピースを世界に届ける時です !\n{0}さん、私の想いを込めた歌、\n一緒に響かせてください !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/226, /*campaign_id*/10006, /*card_id*/100594, /*use_type*/0, /*index*/1, /*description*/"最高速度で走っていくから、\n{0}さんはナビをお願い !\nふたりでなら、この景色の向こう側にだって行けるよね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/227, /*campaign_id*/10006, /*card_id*/100546, /*use_type*/0, /*index*/1, /*description*/"ふっふっふ、私には聞こえるぞ、{0} !\nこの天才を呼ぶ、みんなの声が !\nさぁ、いくぞ ! 私の才能を知らしめに…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/228, /*campaign_id*/10006, /*card_id*/200668, /*use_type*/0, /*index*/1, /*description*/"{0}さんとなら、\nどんなに困難なことも成し遂げられると思えるの。\n信じているわ。きっと、光をつかめると。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/229, /*campaign_id*/10006, /*card_id*/200792, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーのオーダーは\n期待以上にこなさなきゃね♪\nだからそのアツい眼差し……もっと頂戴♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/230, /*campaign_id*/10006, /*card_id*/200830, /*use_type*/0, /*index*/1, /*description*/"もっと上を目指したいってアツい想いが、胸にあるんだ。\n{0}も同じアツさを持ってるんだろ？\nなら、やってやろうぜッ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/231, /*campaign_id*/10006, /*card_id*/200846, /*use_type*/0, /*index*/1, /*description*/"{0}くんがそんなに熱く求めてくれるなら、\n応えないわけにはいかないわ。\nオトナの深い味わい……感じてもらわなきゃ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/232, /*campaign_id*/10006, /*card_id*/200758, /*use_type*/0, /*index*/1, /*description*/"大きな夢を勝ち取るには、本気でなきゃね。\n{0}さんも同じ気持ちかしら？\nふふ……その熱さ、何より心強いわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/233, /*campaign_id*/10006, /*card_id*/200532, /*use_type*/0, /*index*/1, /*description*/"私と{0}くんの目的地は同じ。\nこの道は希望に続いている……そうだろう？\nさぁ、未だ見ぬ景色を見に行こうか。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/234, /*campaign_id*/10006, /*card_id*/200514, /*use_type*/0, /*index*/1, /*description*/"{0}さんが大きな夢を見せてくれる。\nだから、もっと上を目指して跳ぶんだ !\n今よりずっと、高いところへっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/235, /*campaign_id*/10006, /*card_id*/200788, /*use_type*/0, /*index*/1, /*description*/"ひとりでも大丈夫、なんて強がりはやめておくわ。\n{0}さんがそばにいる……\nそれは何よりも、力強い支えなの。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/236, /*campaign_id*/10006, /*card_id*/200820, /*use_type*/0, /*index*/1, /*description*/"君は信じているんだな、より高みを目指す私を。\nなら、{0}。背中は預けたぞ。\n次の景色を、ともに見ようじゃないか。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/237, /*campaign_id*/10006, /*card_id*/200890, /*use_type*/0, /*index*/1, /*description*/"この鼓動の高鳴りは、今の私に最も必要なものです。\n{0}さんとともに、\n目指す的を射るための……力となります。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/238, /*campaign_id*/10006, /*card_id*/200850, /*use_type*/0, /*index*/1, /*description*/"{0}さんと一緒に\n色とりどりの景色を描き、心に飾ってきました。\nそして今、新たな色が生まれる予感がします……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/239, /*campaign_id*/10006, /*card_id*/200354, /*use_type*/0, /*index*/1, /*description*/"{0}と目指す未来……\n予測不能だからこそ、ガラにもなく熱くなるのかしら。\nフフ、いいわ。この熱に身を任せましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/240, /*campaign_id*/10006, /*card_id*/200468, /*use_type*/0, /*index*/1, /*description*/"アイスも幸せも、大きいほうが嬉しいですねー。\n{0}殿には一番大きいものを\nお届けしたいです。見ててくださいませー。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/241, /*campaign_id*/10006, /*card_id*/200562, /*use_type*/0, /*index*/1, /*description*/"七海もビビッと感じたのれすよ、大物の気配を !\nさぁ、沖に向かって出発れす !\nプロデューサーと一緒なら、どこまでも泳げますから～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/242, /*campaign_id*/10006, /*card_id*/200892, /*use_type*/0, /*index*/1, /*description*/"{0}の期待は大きいわね。\nそれくらいでなくてはヘレンの名が廃るわ。\nさぁ、響かせに行きましょう。夜明けの一声を !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/243, /*campaign_id*/10006, /*card_id*/200868, /*use_type*/0, /*index*/1, /*description*/"必ずこのチャンスをつかんでみせるわ。\n今はひとりじゃない。{0}さんもいる。\n私たちが揃えば、怖いものなしよ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/244, /*campaign_id*/10006, /*card_id*/200550, /*use_type*/0, /*index*/1, /*description*/"刹那の煌めきではなく、輝き続ける……\n{0}の声には、それを実現する力があるわ。\nいきましょう、遥かな高みへ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/245, /*campaign_id*/10006, /*card_id*/200834, /*use_type*/0, /*index*/1, /*description*/"目的地がずっと遠い場所にあっても、\n私たちの足でならきっとたどり着けるわ。\n{0}さんとの旅程は、楽しいもの。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/246, /*campaign_id*/10006, /*card_id*/200772, /*use_type*/0, /*index*/1, /*description*/"声援を受けた私の度数は、高めね……。\nこの心地いい酔いは、まだ醒めそうにないわ。\n{0}さん、期待していて……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/247, /*campaign_id*/10006, /*card_id*/200836, /*use_type*/0, /*index*/1, /*description*/"遥かな場所へ、向かっていきまショウ !\n{0}プロデューサーの支えがあれば\n私の歌も、Cross the frontierデス♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/248, /*campaign_id*/10006, /*card_id*/200708, /*use_type*/0, /*index*/1, /*description*/"私が行く潮路は、{0}さんが\nつくってくれる、唯一の道。\nともに進めるなら、どんな海鳴りも恐ろしくないわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/249, /*campaign_id*/10006, /*card_id*/200678, /*use_type*/0, /*index*/1, /*description*/"{0}さんは、とても高くて\nまぶしい輝きを私に見せてくれます。\nそこに未知の体験があるなら、全力で目指します !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/250, /*campaign_id*/10006, /*card_id*/200878, /*use_type*/0, /*index*/1, /*description*/"ひとつになると、ますます熱くなっちゃうもの、なーんだ？\n{0}君と私の想い、よ。\nさぁ、期待の声に応えなきゃね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/251, /*campaign_id*/10006, /*card_id*/200702, /*use_type*/0, /*index*/1, /*description*/"{0}さんが隣にいるから、\nもっと先を目指したくなるの。\nふたりで一緒に未来を掴めるよう、全力で臨むわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/252, /*campaign_id*/10006, /*card_id*/200764, /*use_type*/0, /*index*/1, /*description*/"もっと多くの人に、アタシの色を知ってほしいっす。\n世界を塗り替えるくらい、大胆にっ !\n{0}さんとなら、きっと…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/253, /*campaign_id*/10006, /*card_id*/200872, /*use_type*/0, /*index*/1, /*description*/"{0}さんの後押しを受けて、\n心の底から願いましょう。風に乗せ、空を越え……\nどこまでも響く音を、歌いたいと。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/254, /*campaign_id*/10006, /*card_id*/200806, /*use_type*/0, /*index*/1, /*description*/"あはっ、最高の気分って感じぃ♪\n{0}さんが推してくれるなら、\n今よりもっとすごいコト、できちゃうかもぉ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/255, /*campaign_id*/10006, /*card_id*/200490, /*use_type*/0, /*index*/1, /*description*/"{0}さんと一緒に、\n未だ見ぬ世界へ辿り着くことができたら……\nそれはきっと、私史上最大の冒険になりますっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/256, /*campaign_id*/10006, /*card_id*/200752, /*use_type*/0, /*index*/1, /*description*/"いきましょう、{0}さん。\n未来へのチケットは、ここにあるわ。\n新たなステージで、祝福のアリアを歌うために……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/257, /*campaign_id*/10006, /*card_id*/200636, /*use_type*/0, /*index*/1, /*description*/"誰かと一緒に絵を描くのは、とても楽しいです。\nだから{0}さんと描く\n大きな夢も、いっぱいの楽しさに溢れてます !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/258, /*campaign_id*/10006, /*card_id*/200734, /*use_type*/0, /*index*/1, /*description*/"幸運の先にある幸せ、つかんでやろうじゃない。\n占いとも奇跡とも違う、あたしたちの力で !\nいけるわよね、{0} !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/259, /*campaign_id*/10006, /*card_id*/200786, /*use_type*/0, /*index*/1, /*description*/"やりたいって、叶えたいって思うんです。\nここまで来たから、この先も必ず……\n{0}さんの応援を背に、進みます。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/260, /*campaign_id*/10006, /*card_id*/200496, /*use_type*/0, /*index*/1, /*description*/"理論とか根拠とか、そういう話はなし。\nだって、{0}Pとなら\nどんな難しい壁も越えられるって知ってるからね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/261, /*campaign_id*/10006, /*card_id*/200822, /*use_type*/0, /*index*/1, /*description*/"自信をもって、挑むことにします。\n背中を押してくれる{0}プロデューサーの\nためにも……私らしく、輝きたいから。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/262, /*campaign_id*/10006, /*card_id*/200778, /*use_type*/0, /*index*/1, /*description*/"{0}さんも一緒なら、\n私の歌が、遠くの誰かへ届くかもしれません。\nそうしたい……この幸せの歌を、響かせたいです !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/263, /*campaign_id*/10006, /*card_id*/300518, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーの声を聞くと、\n新しい旅が始まる予感がするっ。\nどんな景色が見られるか……楽しみだなっ♪", /*insert_word_type*/0);
