INSERT INTO `idel_vote_idol` VALUES (/*id*/68, /*campaign_id*/10002, /*card_id*/200005, /*use_type*/0, /*index*/1, /*description*/"私と{0}さんで、\nまた新たな一歩を踏み出すのね。行ける所まで行きましょうか。\nセラヴィー……それが、アイドルだもの。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/69, /*campaign_id*/10002, /*card_id*/200007, /*use_type*/0, /*index*/1, /*description*/"そうね、遠慮は禁物よね。まだまだいけるわ !\n{0}君が頼もしい言葉をくれるから、\n私はずっと輝いていられるの !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/70, /*campaign_id*/10002, /*card_id*/200009, /*use_type*/0, /*index*/1, /*description*/"{0}さん、\nいつも背中を押してくれて……ありがとな。\n言葉じゃ全然伝えきれないからさ……見ててくれよ、アタシの姿 !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/71, /*campaign_id*/10002, /*card_id*/200011, /*use_type*/0, /*index*/1, /*description*/"視界をクリアにして、魅力をさらに引き出す……\nふふ、{0}さんのことですよ。\nこの先も、一緒に歩いていきましょうね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/72, /*campaign_id*/10002, /*card_id*/200013, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーと描く物語は、\nキラキラ輝いてて……最高にドキドキするっス !\nこの調子で、たくさんの人の心を掴むっスよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/73, /*campaign_id*/10002, /*card_id*/200171, /*use_type*/0, /*index*/1, /*description*/"私にも、胸に秘めた情熱がある……生み出したのは、君さ。\nここに誓うよ、{0}くん。\n人々を魅了するアイドルになるとね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/74, /*campaign_id*/10002, /*card_id*/200015, /*use_type*/0, /*index*/1, /*description*/"{0}さんと肩を並べて、\n未来に向かって突き進むアイドルリーナ……最高にロックです !\n魂の叫び、どこまでも響かせますっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/75, /*campaign_id*/10002, /*card_id*/200155, /*use_type*/0, /*index*/1, /*description*/"一瞬一瞬が、アタシの大切なステージ……だから、いつも全力で !\n{0}さんが見守ってくれてるなら、\nそりゃもう尚更にね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/76, /*campaign_id*/10002, /*card_id*/200017, /*use_type*/0, /*index*/1, /*description*/"{0}さんと一緒だから、\n千枝はステキなアイドルになれるんです。\nだから、これからも千枝を見守っててくださいね……♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/77, /*campaign_id*/10002, /*card_id*/200123, /*use_type*/0, /*index*/1, /*description*/"ステージに立ち続ける強さを、貴方がくれました……。\nこの手に掴んだ運命の、その先を……\n{0}さんと確かめられたら……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/78, /*campaign_id*/10002, /*card_id*/200289, /*use_type*/0, /*index*/1, /*description*/"{0}さんが見つけてくれた眩しい光……\nあの頃の私に、見せてあげたいの。\n人は何度でも、美しく花開けるって…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/79, /*campaign_id*/10002, /*card_id*/220231, /*use_type*/0, /*index*/1, /*description*/"フフッ……なに、喜びを噛み締めているのさ。\n{0}が期待している。\nつまり、私はまた、新たな挑戦ができるということだ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/80, /*campaign_id*/10002, /*card_id*/200135, /*use_type*/0, /*index*/1, /*description*/"{0}さんと歩み続けて……\n私という器が彩られていきます。\nこの先も……どうかその手を、優しく添えていてください。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/81, /*campaign_id*/10002, /*card_id*/200019, /*use_type*/0, /*index*/1, /*description*/"美しく誠実で、時に可愛く……。\nそんな素敵なアイドルに近づけていますか？\n{0}さん、私を成長させてくださいね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/82, /*campaign_id*/10002, /*card_id*/200185, /*use_type*/0, /*index*/1, /*description*/"一意専心……あとは心を静め、矢を射るだけです。\n{0}さんと磨いた私を、\n必ずみなさんの心に届けてみせます !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/83, /*campaign_id*/10002, /*card_id*/200267, /*use_type*/0, /*index*/1, /*description*/"惑うことなく、美しい夢を描けるのは……\n{0}さんがいてくれるから。\nこの先も……美しい作品を、見せていきますね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/84, /*campaign_id*/10002, /*card_id*/200067, /*use_type*/0, /*index*/1, /*description*/"怖がらずに、前を向いて歩きます。\nたとえ、つまずいても……{0}さんが\n受け止めてくれると、信じていますから。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/85, /*campaign_id*/10002, /*card_id*/200091, /*use_type*/0, /*index*/1, /*description*/"……多くの人の手を借り、私の物語は刻まれてきました。\n……その頁の行き着く先を、\n{0}さんと見届けられたらと。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/86, /*campaign_id*/10002, /*card_id*/200127, /*use_type*/0, /*index*/1, /*description*/"予測不可能よ。{0}と私……\n二人の思惑が交差する先に、何があるのかなんてね。\nフッ、好奇心を煽られるわ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/87, /*campaign_id*/10002, /*card_id*/200161, /*use_type*/0, /*index*/1, /*description*/"ご飯もアイスも食べられて、うれしい毎日でございますよー。\n{0}殿へのご恩に、\nアイドルをがんばりますですねー。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/88, /*campaign_id*/10002, /*card_id*/220225, /*use_type*/0, /*index*/1, /*description*/"七海は大物になりますよ～。プロデューサーの手で\nスクスク育って、荒波もへっちゃられすから !\n活きの良さ、見せていきましょ～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/89, /*campaign_id*/10002, /*card_id*/220227, /*use_type*/0, /*index*/1, /*description*/"ヘレンは一翼でも世界を飛ぶ。\nでも、貴方がいれば？ザッツライ ! 双翼となるの !\n{0}と私の飛翔は、宇宙をも超えるわ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/90, /*campaign_id*/10002, /*card_id*/200021, /*use_type*/0, /*index*/1, /*description*/"やる気満々って顔だね、{0}サン。\nアンタの想いは受け取ったよ。\n二人分の気持ち、必ず遠くまで響かせるから、見てな !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/91, /*campaign_id*/10002, /*card_id*/200327, /*use_type*/0, /*index*/1, /*description*/"貴方と共に磨いてきた美しさだけれど、\nまだ果てはないと感じるわ。\n{0}さん、これからも高みを目指しましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/92, /*campaign_id*/10002, /*card_id*/200303, /*use_type*/0, /*index*/1, /*description*/"未来へ刻まれる、二筋の光……。\n其処に輝くのは、夢か……あるいは、現実か……。\n{0}ならば、到達すべき場所は一つね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/93, /*campaign_id*/10002, /*card_id*/200023, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーの\nご期待にそって、動きたい……いえ、せっかくなら期待以上に。\n私って意外と欲張りだったんですね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/94, /*campaign_id*/10002, /*card_id*/200025, /*use_type*/0, /*index*/1, /*description*/"我が身に魔力が宿る…… ! 歓喜に湧く魂の輝きを見よ !\n（{0}プロデューサーとなら、\n何だってできちゃいますよねっ♪）", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/95, /*campaign_id*/10002, /*card_id*/200325, /*use_type*/0, /*index*/1, /*description*/"私と{0}さん、二人の旅はまだ途中……。\n先の読めない道程だからこそ楽しめる。\n信頼している相手となら、特にね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/96, /*campaign_id*/10002, /*card_id*/200101, /*use_type*/0, /*index*/1, /*description*/"貴方から見た私が、\n極上の陶酔をもたらすアイドルでありますように……。\nふふ、{0}さんの期待って、心地よいのよ……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/97, /*campaign_id*/10002, /*card_id*/200027, /*use_type*/0, /*index*/1, /*description*/"{0}さんは、\nいつだって寄り添って力になってくれた。\nだから今度は、私が手を引く番……いこう、きらめくステージへ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/98, /*campaign_id*/10002, /*card_id*/200029, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーのいる場所……\nそこは自由に羽ばたける、私のhome groundデス !\nどこまでも飛んでみせマスネ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/99, /*campaign_id*/10002, /*card_id*/200287, /*use_type*/0, /*index*/1, /*description*/"潮風が波を呼ぶように……\n貴方は優しく手を取り、夢へと誘ってくれる……。\n{0}さんを信じて、私は進み続けるわ……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/100, /*campaign_id*/10002, /*card_id*/200077, /*use_type*/0, /*index*/1, /*description*/"アイドルとして、笑顔と楽しさを伝えるステージへ……。\n{0}さんとだから、羽ばたけます。\nどうか、見守っていてください !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/101, /*campaign_id*/10002, /*card_id*/200075, /*use_type*/0, /*index*/1, /*description*/"{0}のこころ、伝わってくる……。\nだから、私も……伝える……ね……。\nありがとう……。これからも、ずっと……一緒……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/102, /*campaign_id*/10002, /*card_id*/220249, /*use_type*/0, /*index*/1, /*description*/"貴方といると、昂ってしまうわ……。\n{0}君の優しい手と、熱い声で、\nお姉さんをもっともっと高めてちょうだい……♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/103, /*campaign_id*/10002, /*card_id*/200207, /*use_type*/0, /*index*/1, /*description*/"{0}さんからは、沢山のものを貰ってきたわ。\n貴方の優しさを受けて、\nアイドルとして歩む私を……傍で見ていてね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/104, /*campaign_id*/10002, /*card_id*/200255, /*use_type*/0, /*index*/1, /*description*/"型にハマらない魅せ方……\n教えてくれたのは、{0}さんっす。\n誰にも予想できないスタイルで、アタシだけの夢を描くっすよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/105, /*campaign_id*/10002, /*card_id*/200187, /*use_type*/0, /*index*/1, /*description*/"聞こえます……。{0}さんを信じて進めば……\nより穏やかで、より美しい音に出会えると……\nそう、風が囁いています……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/106, /*campaign_id*/10002, /*card_id*/200083, /*use_type*/0, /*index*/1, /*description*/"あのね……{0}さんと、\nあの子と……あと他にも……みんなが見ていてくれるから、\nたのしい夢が、見られるんだよ……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/107, /*campaign_id*/10002, /*card_id*/200301, /*use_type*/0, /*index*/1, /*description*/"蝶々は甘～い蜜を吸って育つでしょ？\nだから{0}さんが褒めてくれたら、\nあやかも高～く飛んでいけまぁす♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/108, /*campaign_id*/10002, /*card_id*/220211, /*use_type*/0, /*index*/1, /*description*/"一人の冒険も楽しいですけれど、仲間がいればもっと楽しいです。\n{0}さんと行く道は、\nきっとキラキラの宝物です !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/109, /*campaign_id*/10002, /*card_id*/200315, /*use_type*/0, /*index*/1, /*description*/"私の歌声は、どこまで行けるかしら。\n{0}さん、信じていて？\n私というアイドルはまだまだ、高みへ行けるから…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/110, /*campaign_id*/10002, /*card_id*/200195, /*use_type*/0, /*index*/1, /*description*/"アイドルって……迷っちゃいます。素敵な景色が、\nたくさんあるから。{0}さん……\nもっと、夢の景色を見せてください……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/111, /*campaign_id*/10002, /*card_id*/200177, /*use_type*/0, /*index*/1, /*description*/"{0}には、いろんなラッキーを\n貰ってきたけれど、やっぱ一番は頼もしい言葉ね。\nこれからも引っ張っていってよね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/112, /*campaign_id*/10002, /*card_id*/200085, /*use_type*/0, /*index*/1, /*description*/"いやー、{0}さんはすごいね。\nシューコちゃんのやる気に火を点けるなんてさ……\nあ、消えないように、薪足してー♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/113, /*campaign_id*/10002, /*card_id*/200111, /*use_type*/0, /*index*/1, /*description*/"守るものがあると、剣士は強くなるのです !\n{0}殿との絆を胸に、\n珠美はこの道を必ずや極めてみせますっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/114, /*campaign_id*/10002, /*card_id*/200153, /*use_type*/0, /*index*/1, /*description*/"{0}さんが見せてくれた夢は……\n少しずつ育って、とても大きくなりました。\nふたりの夢を、形にしましょうね…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/115, /*campaign_id*/10002, /*card_id*/200037, /*use_type*/0, /*index*/1, /*description*/"{0}さんの手で、ずっと輝いていたい。\nこの言葉は、私の本音よ。\nその証拠に……ほら。唇が、少し震えてるでしょう？", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/116, /*campaign_id*/10002, /*card_id*/200145, /*use_type*/0, /*index*/1, /*description*/"{0}Pといると、\nたくさんの喜びや感動でオーバーフローしそうだよ。\nこれからの計算できない未来……一緒に進もうね。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/117, /*campaign_id*/10002, /*card_id*/200285, /*use_type*/0, /*index*/1, /*description*/"い、いつもありがとう…… !\n私だって、素直にお礼くらい……。成長してるって、\n{0}プロデューサーに思ってほしいから…… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/118, /*campaign_id*/10002, /*card_id*/200119, /*use_type*/0, /*index*/1, /*description*/"逃げられないならせめて……\n{0}プロデューサーさんに、伝えます……。\nもりくぼ、静かに暮らせれば……むーりぃー？あぅ……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/119, /*campaign_id*/10002, /*card_id*/200039, /*use_type*/0, /*index*/1, /*description*/"勇気をくれる{0}プロデューサーは、\n心に輝く星、ですね。私もみんなを照らせるように、\nブリスチェーチ……たくさん輝きます !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/120, /*campaign_id*/10002, /*card_id*/200125, /*use_type*/0, /*index*/1, /*description*/"{0}殿の指揮の下、いざ前線へ !\nとうに覚悟は決まっています。\n私の為すべき事はただ一つ……声援に応えるのみ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/121, /*campaign_id*/10002, /*card_id*/200141, /*use_type*/0, /*index*/1, /*description*/"大事な一戦ってのは、いつだってワクワクしてくるよな。\n見てろよ、{0} !\nみんなを驚かせるカッコイイプレー、決めるぜ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/122, /*campaign_id*/10002, /*card_id*/200093, /*use_type*/0, /*index*/1, /*description*/"如何なる時も、キミはボクを照らす。\nいや、構わないよ。ただそれだけなのに……\n可笑しいね。セカイは光彩を放つ。悪くない気分さ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/123, /*campaign_id*/10002, /*card_id*/200279, /*use_type*/0, /*index*/1, /*description*/"行く先がわかってんなら、進む以外の選択肢ある？\nないね。なら、泥臭いことくらいやらなきゃな。\n上目指すのって楽しいし。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/124, /*campaign_id*/10002, /*card_id*/200219, /*use_type*/0, /*index*/1, /*description*/"{0}さんの想いは……\n心にすっと浸み込んで、満たしてくれます……。\n眩しい明日に向かって……歌い続けたいです……。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/125, /*campaign_id*/10002, /*card_id*/200337, /*use_type*/0, /*index*/1, /*description*/"幸運にも勝る、\n{0}プロデューサーとの絆の力……\n強く感じてます。ふたりでなら、どこまでも歩んでいけますよー♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/126, /*campaign_id*/10002, /*card_id*/300001, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーにパワーをもらえば、\nどこへだって飛んでいけるよ !\nアイドル本田未央を、その目でしっかり見ててね☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/127, /*campaign_id*/10002, /*card_id*/300003, /*use_type*/0, /*index*/1, /*description*/"のんびり歩いたり、ちょっぴり急いで走ったり……\n道は人それぞれ。\n私は、{0}さんと並んで歩く道が好きです♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/128, /*campaign_id*/10002, /*card_id*/300199, /*use_type*/0, /*index*/1, /*description*/"目的地がわからないからこそ、楽しめる旅もあるっ。\n{0}プロデューサーと、\nどこまで行けるかな？ワクワクするね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/129, /*campaign_id*/10002, /*card_id*/300007, /*use_type*/0, /*index*/1, /*description*/"{0}せんせぇの声は\nどこにいても聞こえるよ ! あったかくて、だーいすき♪\n背中をぐぐーって押してくれるの !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/130, /*campaign_id*/10002, /*card_id*/300009, /*use_type*/0, /*index*/1, /*description*/"心を通わせた相棒がいれば、\nどんな壁だって越えられる……だろ？\n{0}さんとアタシの力、見せつけてやろうぜっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/131, /*campaign_id*/10002, /*card_id*/300011, /*use_type*/0, /*index*/1, /*description*/"美しさって、ひとりで磨くものじゃないわ。\n見てくれる人、認めてくれる人がいるから輝ける。\nそうでしょ、{0}P♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/132, /*campaign_id*/10002, /*card_id*/320223, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーがくれるエールは、\n健康によし、美容にもよし !\nもっと元気に、キレイになって、次のステージを目指します♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/133, /*campaign_id*/10002, /*card_id*/300271, /*use_type*/0, /*index*/1, /*description*/"{0}くんの熱い想い、伝わってくるわ。\nあとはこの流れをビッグウェーブに変えるだけ !\n最高のライドを見せてあげる !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/134, /*campaign_id*/10002, /*card_id*/300103, /*use_type*/0, /*index*/1, /*description*/"{0}さんとアイドルできるって、\nすごい幸せなことですよね ! これからも矢口は全力で……\n矢口マックスで応えますよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/135, /*campaign_id*/10002, /*card_id*/300013, /*use_type*/0, /*index*/1, /*description*/"いつもドキドキとキラキラをいっぱいくれて、ありがとっ♪\n{0}プロデューサーへのお返しは、\nとびきり一番のニコニコ笑顔だよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/136, /*campaign_id*/10002, /*card_id*/300237, /*use_type*/0, /*index*/1, /*description*/"どこからでもまっすぐに届く、\n{0}プロデューサーの情熱……受け取ったよッ !\n力強いパスは、必ずゴールに繋げるからねッ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/137, /*campaign_id*/10002, /*card_id*/300319, /*use_type*/0, /*index*/1, /*description*/"ありがとうございます !\n{0}プロデューサーの期待を力に変えて、\nどんな道も気持ちよく、駆け抜けていきますよっ☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/138, /*campaign_id*/10002, /*card_id*/300015, /*use_type*/0, /*index*/1, /*description*/"キャンディみたいに甘くておいしい、\n{0}ちゃんのキモチ☆\nゆいを元気にしてくれる、一番のプレゼントなんだ♪アリガトっ☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/139, /*campaign_id*/10002, /*card_id*/300017, /*use_type*/0, /*index*/1, /*description*/"アイドルドラフト会議、\n{0}プロデューサーは姫川選手を指名 !\nオッケー、期待にお応えしましょう ! かっ飛ばせー、あ・た・し !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/140, /*campaign_id*/10002, /*card_id*/300127, /*use_type*/0, /*index*/1, /*description*/"お、{0}サン気合入ってるねー♪\n柚チャン大好きってキモチ、伝わってきた !\nアタシも本気度アップして、頑張っちゃおっかな♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/141, /*campaign_id*/10002, /*card_id*/300195, /*use_type*/0, /*index*/1, /*description*/"{0}しゃんの声は、\n太陽みたいにぬくいねー !\nウチも光をいっぱいもらって、お空のてっぺんまで伸びるばい !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/142, /*campaign_id*/10002, /*card_id*/300297, /*use_type*/0, /*index*/1, /*description*/"いつもプニョフワな私ですけど、\nしっかりとした芯もあるんですよ～。\n{0}さんは、知ってくれているんですね～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/143, /*campaign_id*/10002, /*card_id*/300071, /*use_type*/0, /*index*/1, /*description*/"{0}さんのまごころ、\nたっぷりいただきましたー♪\n私も、もぉーっとすくすく育って、たっぷり恩返ししますねー。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/144, /*campaign_id*/10002, /*card_id*/300177, /*use_type*/0, /*index*/1, /*description*/"勝利の高笑いを決めるレイナサマの姿、\n全人類に見せつけてやるわ ! \n{0}、ついてきなさい ! 頼りにしてるから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/145, /*campaign_id*/10002, /*card_id*/300257, /*use_type*/0, /*index*/1, /*description*/"いま、すっごく気合入りましたぁ♪プロデュース力抜群な\n{0}さんに褒められたら、\n自分磨きも熱が入りますよねー !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/146, /*campaign_id*/10002, /*card_id*/300069, /*use_type*/0, /*index*/1, /*description*/"キノコの成長に欠かせないのは、ほどよい温度とジメジメ感。\n私の場合は、それに加えて……\n{0}の声で、よく育つ。フヒヒッ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/147, /*campaign_id*/10002, /*card_id*/300073, /*use_type*/0, /*index*/1, /*description*/"ハイ、道を空けてくださーい、片桐早苗が通りまーす !\n{0}君が作ってくれた道だもの、\nノンストップで突っ走るわよ～ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/148, /*campaign_id*/10002, /*card_id*/300021, /*use_type*/0, /*index*/1, /*description*/"見えます、見えてますよ !\n{0}プロデューサーの心が、私のパワーと\n共鳴して……世界中にさいきっくの嵐を起こす未来が !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/149, /*campaign_id*/10002, /*card_id*/300231, /*use_type*/0, /*index*/1, /*description*/"がむしゃらに泳いでると、コースを見失うこともあるんだ。\nそんな時はさ、{0}プロデューサーの声が\n頼りになるんだよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/150, /*campaign_id*/10002, /*card_id*/300155, /*use_type*/0, /*index*/1, /*description*/"アタシらしさをアピールしてこそ、トップを目指せる ! でしょ？\nだから、{0}も全力で、\nアタシの魅力を引き出してよね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/151, /*campaign_id*/10002, /*card_id*/300283, /*use_type*/0, /*index*/1, /*description*/"二流以下の豚に用はないわ。\n認められたいのなら、私が高みに上るための踏み台くらい、\n言われずとも進んでなりなさいな。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/152, /*campaign_id*/10002, /*card_id*/300141, /*use_type*/0, /*index*/1, /*description*/"こころが多くの想いを生み、大きな流れとなりましてー。\nわたくしも流れに身を委ね、\nそなたが待つ地へと参りましょー。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/153, /*campaign_id*/10002, /*card_id*/300089, /*use_type*/0, /*index*/1, /*description*/"春は芽生えの季節だよねっ。\n私も精いっぱい咲かせるよ。{0}さんと\n一緒に育ててきた、大きな笑顔の花っ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/154, /*campaign_id*/10002, /*card_id*/300023, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーとなら、\nはっぴーたいむはえんどれす☆\nそらちんのべすとすまいる、みんなにも届けようねっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/155, /*campaign_id*/10002, /*card_id*/300025, /*use_type*/0, /*index*/1, /*description*/"気の強い子ほど、乗りこなし甲斐がある……\nフフッ、{0}プロデューサーもわかるでしょ？\nだからワタシも、全力でいくわ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/156, /*campaign_id*/10002, /*card_id*/300083, /*use_type*/0, /*index*/1, /*description*/"熱いエールを送ります ! 今は、アタシ自身に向けて…… !\n{0}さんの\n心強いエールと合わされば、もう怖いものなしですね☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/157, /*campaign_id*/10002, /*card_id*/300027, /*use_type*/0, /*index*/1, /*description*/"もっとステキな場所、行けるかな……\nドキドキしてるけど、心配は全然してないんだ♪\n{0}プロデューサーが隣にいるからね★", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/158, /*campaign_id*/10002, /*card_id*/300029, /*use_type*/0, /*index*/1, /*description*/"{0}くんはもう、アタシにメロメロだよね☆\nでも、まだ満足しないよ♪\nふたりの力で、世界中をドキドキさせちゃおーっ☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/159, /*campaign_id*/10002, /*card_id*/300081, /*use_type*/0, /*index*/1, /*description*/"心にバシッとくる最高のシャウト、もらったよ ! !\n次はアタシの番だ ! ! {0}プロデューサーに、\nアタシをガンガン響かせてやるぜ ! ! !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/160, /*campaign_id*/10002, /*card_id*/300031, /*use_type*/0, /*index*/1, /*description*/"いいですね、燃えてます ! {0}プロデューサー\nの目の奥で、魂の炎がメラメラと ! ! \n私も負けませんよ ! 一緒にバーニィィィング ! !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/161, /*campaign_id*/10002, /*card_id*/300033, /*use_type*/0, /*index*/1, /*description*/"うぇへへ ! {0}ちゃんのはぴはぴぱわ～、\nたっくさんもらったにぃ☆\nお礼のきらりんスマイル、い～っぱい見せゆっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/162, /*campaign_id*/10002, /*card_id*/300035, /*use_type*/0, /*index*/1, /*description*/"{0}さんは、\n焼きたてのお菓子みたいにアツアツですね～っ♪\n私を芯までポカポカにして、ステキに仕上げてくれますっ。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/163, /*campaign_id*/10002, /*card_id*/300075, /*use_type*/0, /*index*/1, /*description*/"大好きな{0}がずっと一緒だカラ、\nナターリアもいっぱいガンバれるゾ !\nだからガンバった分、い～っぱいホメてほしいナ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/164, /*campaign_id*/10002, /*card_id*/300261, /*use_type*/0, /*index*/1, /*description*/"{0}さんとのアイドルフライトは、絶好調 !\nどんな向かい風も越えて、高く飛んで、\n夢の目的地にたどり着いてみせるわ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/165, /*campaign_id*/10002, /*card_id*/300255, /*use_type*/0, /*index*/1, /*description*/"今の私、{0}さんのオーダー通り、\n美味しく仕上がってますよ♪みんなが夢中になる\nスペシャルメニュー・志保をどうぞっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/166, /*campaign_id*/10002, /*card_id*/300105, /*use_type*/0, /*index*/1, /*description*/"熱いじゃねーか、{0}。\nいつも以上に気合が入ってんな ! ま、当然アタシも同じだ !\nこのままカッ飛ばしていくぜ、オラァ ! !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/167, /*campaign_id*/10002, /*card_id*/300113, /*use_type*/0, /*index*/1, /*description*/"仁奈はせーちょーきでやがりますからね。\n一番すげー仁奈になるですよ。\nそれで{0}に、すげーホメられちまいます !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/168, /*campaign_id*/10002, /*card_id*/300121, /*use_type*/0, /*index*/1, /*description*/"アイドルとしてステージに立つ日菜子は……\n{0}さんの手を取って、\n待ち受ける運命の舞台へ……そして大胆に……むふふ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/169, /*campaign_id*/10002, /*card_id*/320209, /*use_type*/0, /*index*/1, /*description*/"波が高いほど、越えた先でいい眺めが見られるってもんさ。\nさぁ、{0}さん、\nウチに気持ちいい風を吹かせておくれよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/170, /*campaign_id*/10002, /*card_id*/300211, /*use_type*/0, /*index*/1, /*description*/"皆勤賞に一等賞、いろいろもらってきたけどさ。\n一番の自慢はやっぱ、\n{0}Pさんのアイドルってことかな♪へへっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/171, /*campaign_id*/10002, /*card_id*/300099, /*use_type*/0, /*index*/1, /*description*/"ファンのみんなに注目されて、アタシはキラキラ輝くノ♪\n目指すは一番セクシーなレディ !\n{0}の情熱的な視線にも、応えるワ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/172, /*campaign_id*/10002, /*card_id*/300189, /*use_type*/0, /*index*/1, /*description*/"今のアタシ、最高に高まってるんだ !\n{0}がくれた、輝く舞台へ続くストリート……\nアタシが信じるスタイルで、走り抜けるよっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/173, /*campaign_id*/10002, /*card_id*/300157, /*use_type*/0, /*index*/1, /*description*/"{0}さんの支援きたーっ !\nどんなに手ごわいボスでも、もう負ける気がしないよ !\n一気にレベルアップしちゃおう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/174, /*campaign_id*/10002, /*card_id*/300307, /*use_type*/0, /*index*/1, /*description*/"お祭り騒ぎ、いいもんだね。江戸っ子の血が騒ぐってもんさ♪\nあたしの晴れ舞台、\n{0}さんも楽しみにしててよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/175, /*campaign_id*/10002, /*card_id*/300145, /*use_type*/0, /*index*/1, /*description*/"世の中、笑ったもん勝ちや ! ふたりで息合わせてこ !\nウチの力で、{0}はんをトップアイドルに……\nって逆やないかーい !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/176, /*campaign_id*/10002, /*card_id*/300093, /*use_type*/0, /*index*/1, /*description*/"使命を果たすのが、忍びの役目。\nしかし、{0}殿の恩に報いるのは\n使命ではなく、わたくし自身の望みです !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/177, /*campaign_id*/10002, /*card_id*/300129, /*use_type*/0, /*index*/1, /*description*/"勝負に挑むには、勢いと度胸が必要じゃけぇの。\n{0}がうちの背を押してくれるなら、\n頼もしいことこの上ない !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/178, /*campaign_id*/10002, /*card_id*/300153, /*use_type*/0, /*index*/1, /*description*/"{0}Pちゃんに\nもらったモンが多すぎて、キモチが溢れとるわ～。\nギランギランのアタシを見せて、きっちりお礼するからね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/179, /*campaign_id*/10002, /*card_id*/300167, /*use_type*/0, /*index*/1, /*description*/"食材は、旬を知るのが大事っちゃ !\nでも{0}プロデューサーにかかれば、\nあたしはいつでも旬真っ盛りっちゃね☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/180, /*campaign_id*/10002, /*card_id*/300291, /*use_type*/0, /*index*/1, /*description*/"清く正しく、まっすぐに……その努力は必ず報われるものです !\n{0}プロデューサーと共に、\n私は高みを目指しますよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/181, /*campaign_id*/10002, /*card_id*/300241, /*use_type*/0, /*index*/1, /*description*/"プロデューサーったら、『はぁとが大好き』ってダダ漏れだぞ☆\nえ、そもそも隠してない？\nやぁん、素直でよろしい♪アリガトな☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/182, /*campaign_id*/10002, /*card_id*/300245, /*use_type*/0, /*index*/1, /*description*/"みんなを照らす、正義の光になる……。\nそんな願いも、{0}Pとなら叶えられる。\n進もう ! アタシたちの、次の未来へ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/183, /*campaign_id*/10002, /*card_id*/300305, /*use_type*/0, /*index*/1, /*description*/"サンタのプレゼントは、クリスマス限定じゃないんです～。\n{0}さんの特別な贈り物、\nいただきましたよ～。うふふ、幸せ～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/304, /*campaign_id*/10002, /*card_id*/100671, /*use_type*/0, /*index*/1, /*description*/"あかりんご、\nプロデューサーさんのアイドルになれて良かったんご !\n……ん？これを言うのはまだ早い？でも、本当ですし !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/305, /*campaign_id*/10002, /*card_id*/200671, /*use_type*/0, /*index*/1, /*description*/"今の感じ、わりと気に入ってるんで。\nPサンなら、面白そうなコトやってくれそうだし。\nま、そーいうことなんで、よろしくデス。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/306, /*campaign_id*/10002, /*card_id*/300657, /*use_type*/0, /*index*/1, /*description*/"ぼくの願いを叶えてくれるPサマ、お願い !\nもっと目立ちたい ! ! 燃え上がって叩かれてもいい !\n頼む ! ! ! ぼくを推して ! ! ! !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/307, /*campaign_id*/10002, /*card_id*/100637, /*use_type*/0, /*index*/1, /*description*/"お気に入りを仕舞い込まず、輝かせようなんて……\nあなたって面白い人。\n欲望に忠実なところは、とっても素敵だよ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/308, /*campaign_id*/10002, /*card_id*/100655, /*use_type*/0, /*index*/1, /*description*/"……私を？まったく。真に選ぶべき人がいるでしょう。\nお前に何をされようと、私は私。\nしかし……戯れには付き合いましょう。", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/309, /*campaign_id*/10002, /*card_id*/300643, /*use_type*/0, /*index*/1, /*description*/"Pは凪に期待していますね。まさに物好き。\n凪は物ではないので、正しくは凪好き。\n……なるほど、恥ずかしいヤツだな？", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/310, /*campaign_id*/10002, /*card_id*/200657, /*use_type*/0, /*index*/1, /*description*/"はーは、もっと可愛くなるよ !\n自信あるし、たぶんできるし、最高に可愛いはーを見せる !\nだから、Pちゃんも楽しんでこ♪", /*insert_word_type*/0);
