INSERT INTO `idel_vote_idol` VALUES (/*id*/1, /*campaign_id*/10001, /*card_id*/100001, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーさんがくれた力で、\n次は私が魔法をかける番ですねっ。\n輝く想いと最高の笑顔を、みんなに届けます !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/2, /*campaign_id*/10001, /*card_id*/100003, /*use_type*/0, /*index*/1, /*description*/"真の強さは、信頼の心から生まれます。\n今日も明日も明後日も、{0}さんの存在が\nあたしを成長させてくれるんです !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/7, /*campaign_id*/10001, /*card_id*/100005, /*use_type*/0, /*index*/1, /*description*/"はい、ぎゅ～っ♪あたたかい応援をくれた\n{0}くんに感謝を込めて、\nウサコちゃんのハグよ。（これからもよろしくウサー♪）", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/8, /*campaign_id*/10001, /*card_id*/100007, /*use_type*/0, /*index*/1, /*description*/"{0}さんは、パティシエさんみたいですね。\nおかげで私、もっと甘くてふわふわな、\nみんなを幸せにするアイドルになれます♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/9, /*campaign_id*/10001, /*card_id*/100009, /*use_type*/0, /*index*/1, /*description*/"稲っこは、命の根っこ。\nわだすも、人を元気にできるアイドルになりますからっ !\n{0}さん、温かく見守っててくだせぇ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/10, /*campaign_id*/10001, /*card_id*/100011, /*use_type*/0, /*index*/1, /*description*/"{0}さんの見せてくれる世界は、\nキラキラした出会いがた～くさん !\n私を夢中にさせてくれる景色、この先も見せてねぇ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/11, /*campaign_id*/10001, /*card_id*/100013, /*use_type*/0, /*index*/1, /*description*/"夢を見て、夢が叶って…\n次は私が、誰かの夢になれたらって思うんです♪\n{0}さんと一緒なら、この願いもきっと… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/12, /*campaign_id*/10001, /*card_id*/100015, /*use_type*/0, /*index*/1, /*description*/"私、見つけました…。\n四葉のクローバーみたいに、勇気をくれるもの。心に積もった、\n{0}さんの言葉…大切な思い出です…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/14, /*campaign_id*/10001, /*card_id*/100017, /*use_type*/0, /*index*/1, /*description*/"{0}さんが褒めてくれると、\nみゆきは嬉しくて元気百倍になれるんだ !\n今のみゆきなら、何でもできそうな気分っ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/27, /*campaign_id*/10001, /*card_id*/100019, /*use_type*/0, /*index*/1, /*description*/"気まぐれな猫チャンでも、恩は大切にするんだよ。\nみくも同じだから…猫チャンの恩返しに期待しててよね、\n{0}チャン♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/31, /*campaign_id*/10001, /*card_id*/100021, /*use_type*/0, /*index*/1, /*description*/"ゆる～く楽しむをモットーに、今日も健気に頑張るフレちゃん !\n予想できない活躍に、{0}プロデューサーも\n乞うご期待デリカ～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/32, /*campaign_id*/10001, /*card_id*/100023, /*use_type*/0, /*index*/1, /*description*/"{0}はんが見せてくれはる景色は、\nきらきらしてほんに美しゅうて。\nもっと見たいって、つい欲張ってまいますわぁ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/33, /*campaign_id*/10001, /*card_id*/100025, /*use_type*/0, /*index*/1, /*description*/"どんなに道が険しくとも、この胸は期待で満ちていますわ。\n{0}様との未来は、\nまばゆい希望に満ちていますから…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/34, /*campaign_id*/10001, /*card_id*/100027, /*use_type*/0, /*index*/1, /*description*/"飴いっぱい買って、毎日だらだらして、\n{0}プロデューサーと遊びたーい。\n楽しい印税ライフのためにも、もうちょい頑張るかー", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/50, /*campaign_id*/10001, /*card_id*/100037, /*use_type*/0, /*index*/1, /*description*/"ナナの夢を叶えてくれた、{0}さんのパワー…\n今まで以上に感じます !\nナナも精いっぱいの恩返しで、期待に応えますよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/3, /*campaign_id*/10001, /*card_id*/100065, /*use_type*/0, /*index*/1, /*description*/"{0}さんが教えてくれたんです。\n息の合った仲間たちや、ファンのみなさんと\n共に紡ぎ上げる調べ…その素晴らしさを", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/5, /*campaign_id*/10001, /*card_id*/100067, /*use_type*/0, /*index*/1, /*description*/"まん丸くて、甘くて、優しくて…\nいくら食べてもなくならない幸せ、知ってる？\nそれはね…{0}プロデューサーの応援だよ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/4, /*campaign_id*/10001, /*card_id*/100069, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーと一緒の毎日は、\nワクワクでいっぱいです !\n期待に応えて、もーっと立派なアイドルになってみせますねっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/22, /*campaign_id*/10001, /*card_id*/100071, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーがいれば、\nリナリナ無敵だし☆届かない夢なんてないっしょ !\n二人のヤババなコンビパワー、見せてくぽよ～☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/46, /*campaign_id*/10001, /*card_id*/100073, /*use_type*/0, /*index*/1, /*description*/"{0}さんは、このボクが選んだ人です。\nこれからも一緒に、全世界にアピールしていきましょう !\nボクの国宝級のカワイさを !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/41, /*campaign_id*/10001, /*card_id*/100079, /*use_type*/0, /*index*/1, /*description*/"{0}さんが見守ってくれてるって思うと、\nいつも心に余裕ができます !\nこれで、ドジっ子卒業みゃぢか…間近ですっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/36, /*campaign_id*/10001, /*card_id*/100081, /*use_type*/0, /*index*/1, /*description*/"セクシーでキュートなアイドル目指して ! あずきはこれからも\n突き進むよ ! {0}プロデューサーが一緒なら、\n作戦成功間違いなしっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/26, /*campaign_id*/10001, /*card_id*/100087, /*use_type*/0, /*index*/1, /*description*/"キミってホント、観察してて飽きないなー♪\nキミとの反応で生まれる熱量なら、どんな無茶でも実現できそう !\nさぁ張り切ってこー♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/55, /*campaign_id*/10001, /*card_id*/100089, /*use_type*/0, /*index*/1, /*description*/"ふふ、見えますか？\n出会った頃よりずっと強く、二人を結ぶ運命の糸…。\n{0}さんとなら、まゆは何でもできますから…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/13, /*campaign_id*/10001, /*card_id*/100099, /*use_type*/0, /*index*/1, /*description*/"{0}さんが隣にいると、温もりを感じて、\nほっとします。\nだからいつも安心して、新しいことにチャレンジできるんです♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/54, /*campaign_id*/10001, /*card_id*/100105, /*use_type*/0, /*index*/1, /*description*/"皆さまの心を、温かな光で満たすこと…それが私の理想です。\n{0}様のお力添えがあれば、\n必ず叶うと信じております…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/18, /*campaign_id*/10001, /*card_id*/100109, /*use_type*/0, /*index*/1, /*description*/"{0}くんがくれる魔法で、毎日楽しさいっぱい !\nだからあたしもパワーアップして、\nも～っとラブリーなアイドルを目指すよっ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/51, /*campaign_id*/10001, /*card_id*/100113, /*use_type*/0, /*index*/1, /*description*/"{0}さんが認めてくれて、\n歩き始めたアイドルの道。この先も進んでいくよ。\nもっとたくさんの夢を、叶えていきたいから !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/57, /*campaign_id*/10001, /*card_id*/100119, /*use_type*/0, /*index*/1, /*description*/"私が誰かを幸せにするなんて…思ってもいませんでした。\nでも、{0}さんと一緒なら…\n夢も奇跡も、本物にできます… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/15, /*campaign_id*/10001, /*card_id*/100127, /*use_type*/0, /*index*/1, /*description*/"{0}ちゃまの信頼が、\nわたくしの背を力強く押してくださいます。\nわたくしも、期待に応えずにはいられませんわね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/39, /*campaign_id*/10001, /*card_id*/100129, /*use_type*/0, /*index*/1, /*description*/"{0}さんと挑むなら、どんな勝負も大歓迎よ。\n私を飽きさせない、最高のスリルに満ちているものね。\nさぁ、熱くさせて♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/43, /*campaign_id*/10001, /*card_id*/100131, /*use_type*/0, /*index*/1, /*description*/"{0}さんの応援は、どんなメイクよりも\nキレイになれる魔法なんですよぉ♪\nどんどん可愛くなる私、見ててくださいねぇ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/61, /*campaign_id*/10001, /*card_id*/100133, /*use_type*/0, /*index*/1, /*description*/"これからも、あたしはこの道を走り続けるよ !\n全速力のストレートも、キツいコーナーの連続も、\n{0}さんのナビなら怖くないっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/6, /*campaign_id*/10001, /*card_id*/100141, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーにもらった言葉は、\n絶対に忘れませんっ !\nどれもステキな思い出が詰まった、大切な宝物ですから☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/58, /*campaign_id*/10001, /*card_id*/100143, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーとつるんでると、\nウチの強さにも磨きがかかる…。\nだから、ウチをもっともっと応援していいぞッ ! 喜べッ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/56, /*campaign_id*/10001, /*card_id*/100151, /*use_type*/0, /*index*/1, /*description*/"わたし、もっともっとステキなアイドルになりまぁす !\n{0}プロデューサーさんの期待に応えて、\n可愛いさくらを咲かせますよ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/21, /*campaign_id*/10001, /*card_id*/100159, /*use_type*/0, /*index*/1, /*description*/"そこに山があるから、登るんだ。頂があるから掴むんだ !\nだからあたしは止まらないよ。\n挑み続けよう、{0}プロデューサー !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/44, /*campaign_id*/10001, /*card_id*/100163, /*use_type*/0, /*index*/1, /*description*/"お姉さんらしさも可愛らしさも、ぜ～んぶ私の魅力です♪\n{0}さんと力を合わせて、\nもっといっぱい伝えていきます♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/37, /*campaign_id*/10001, /*card_id*/100171, /*use_type*/0, /*index*/1, /*description*/"楽器の音色は、奏者次第。\n{0}様の息吹を感じて、わたくしは\n輝いていますわ。この美しい旋律を、聞いてください…♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/38, /*campaign_id*/10001, /*card_id*/100183, /*use_type*/0, /*index*/1, /*description*/"ママと一緒に目指した、夢のアイドル。\n本当にできたのは、{0}Pがいたからだよぉ♪\nこれからもっと、キラキラ輝かせようねぇ～", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/45, /*campaign_id*/10001, /*card_id*/100195, /*use_type*/0, /*index*/1, /*description*/"甘いものには、幸せが詰まってます～。私も、\n{0}さんイチオシのあまあまアイドルとして、\nみんなに幸せお届けしますぅ～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/19, /*campaign_id*/10001, /*card_id*/100197, /*use_type*/0, /*index*/1, /*description*/"笑顔は幸せを引き寄せる、そんな力があるよね。\n{0}さんがくれた、この笑顔で…\n今度は私が、みんなを幸せにしたいな", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/35, /*campaign_id*/10001, /*card_id*/100207, /*use_type*/0, /*index*/1, /*description*/"色んなコトを勉強して、経験して、ふぇいふぇい\nイッパイ磨かれたヨ。もっとキレイなオトナになるの、\n楽しみにしててネ、{0}さん !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/60, /*campaign_id*/10001, /*card_id*/100213, /*use_type*/0, /*index*/1, /*description*/"力強くて頼もしい、{0}さんがくれる追い風…\n信じて、まだまだ進みますっ。\n私の憧れ、かわいいアイドルを目指してっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/29, /*campaign_id*/10001, /*card_id*/100217, /*use_type*/0, /*index*/1, /*description*/"{0}さんの応援で、\n早耶もやる気いっぱいになりますぅ。\n今よりもっとカワイくキレイに、お望み通りに輝きますねぇ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/59, /*campaign_id*/10001, /*card_id*/100221, /*use_type*/0, /*index*/1, /*description*/"{0}さんと育んだ、熱いハートを声に込めて…\nラブ＆ピースを歌います !\n二人の力で届けましょう。この想いを、世界中へ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/23, /*campaign_id*/10001, /*card_id*/100233, /*use_type*/0, /*index*/1, /*description*/"窯は任せましたよ、{0}プロデューサー !\nあたしを美味しく焼き上げてください !\nホカホカで、幸せの香りがするアイドルに♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/49, /*campaign_id*/10001, /*card_id*/100237, /*use_type*/0, /*index*/1, /*description*/"熱い想い、受け取ったじぇ !\n{0}さんと一緒に醸成した、めくるめく\nユリユリワールド…この調子でどんどん広めてこー !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/52, /*campaign_id*/10001, /*card_id*/100247, /*use_type*/0, /*index*/1, /*description*/"ファンの応援に、{0}さんのサポート…\n私を元気にしてくれる、宝物です。\nだから私も元気な笑顔で、お返ししますよっ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/20, /*campaign_id*/10001, /*card_id*/100257, /*use_type*/0, /*index*/1, /*description*/"{0}くんのお手並みで、\nあたしもグングン磨きがかかってる♪\nもっともっとカワイクなってくから、目ぇ離しちゃダ～メっ☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/25, /*campaign_id*/10001, /*card_id*/100265, /*use_type*/0, /*index*/1, /*description*/"ぐしゅっ…くるみ泣かないよ… !\n{0}ぷろでゅーしゃーといて、\n前より強い子になったくるみ…褒めてほしいもん… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/47, /*campaign_id*/10001, /*card_id*/100273, /*use_type*/0, /*index*/1, /*description*/"優秀な助手がいれば、名探偵に解けない謎はありません !\n名コンビとして、この先も真実を追い続けましょう、\n{0}さん !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/42, /*campaign_id*/10001, /*card_id*/100277, /*use_type*/0, /*index*/1, /*description*/"みなさんの幸せな笑顔を見ていると、私も癒されます♪\n{0}プロデューサーと一緒に、\nこの先も積み重ねていきたいですね", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/62, /*campaign_id*/10001, /*card_id*/100279, /*use_type*/0, /*index*/1, /*description*/"天才たる私の発明も、{0}の支えがあってこそ\n成り立つもの。今日は特に実感している…\nこれからも、よろしく頼む !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/24, /*campaign_id*/10001, /*card_id*/100283, /*use_type*/0, /*index*/1, /*description*/"ぷろでゅーさーのとなり…ぽかぽかして…きもちいいよー…。\nたのしいゆめ…いっしょにみようねー…\nきらきら…ふわぁ…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/53, /*campaign_id*/10001, /*card_id*/100287, /*use_type*/0, /*index*/1, /*description*/"{0}さん、見ててください～♪\nお日様みたくあたたかい応援の言葉をもらって、\n小春はキラキラのお姫様になります～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/17, /*campaign_id*/10001, /*card_id*/100303, /*use_type*/0, /*index*/1, /*description*/"色褪せることなく、みんなの心に残る…\n永遠の、アイドルの輝きを目指して !\n{0}さんと手を取って、歩み続けますよ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/48, /*campaign_id*/10001, /*card_id*/100313, /*use_type*/0, /*index*/1, /*description*/"{0}さんが見ていてくれると、\n昨日の私より自信がついて…毎日成長できてるって、\n実感してます。今この瞬間も… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/30, /*campaign_id*/10001, /*card_id*/100323, /*use_type*/0, /*index*/1, /*description*/"{0}さんの淹れ方次第で、多彩な味わいを…\n最高級の茶葉のように、私も香り高く、\n無限の可能性を見せたいですわ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/40, /*campaign_id*/10001, /*card_id*/100327, /*use_type*/0, /*index*/1, /*description*/"激動の世でも、揺るがないものがあるよ。それは、\n{0}プロデューサーと力を合わせて戦い抜く、\nアタシの信念 ! 傾いていこう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/16, /*campaign_id*/10001, /*card_id*/100331, /*use_type*/0, /*index*/1, /*description*/"私のアルバムには、{0}さんと築いた\n思い出のフォトがたくさん♪\n一枚ずつ、ゆっくりと…一緒に増やしていきたいです !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/28, /*campaign_id*/10001, /*card_id*/100341, /*use_type*/0, /*index*/1, /*description*/"{0}ちゃんに磨いてもらって、\n瑛梨華ちんの芸はもうPIKA☆PIKA !\nお茶の間にもっと大きなWA・RA・I届けてこうねっ☆", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/63, /*campaign_id*/10001, /*card_id*/200001, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーに託された思いが、\n私を強くしてくれるんだ。\n今の私ならどこまでも行けるよ。さぁ、一緒に進もう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/64, /*campaign_id*/10001, /*card_id*/200003, /*use_type*/0, /*index*/1, /*description*/"高みへの道は、決して楽ではないわ。\nでも、貴方となら挑みたくなるの。\n絆が生み出す力を、{0}さんに感じているから", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/68, /*campaign_id*/10001, /*card_id*/200005, /*use_type*/0, /*index*/1, /*description*/"どんなに遠い光でも、{0}さんと手を重ねれば、\n掴めるって信じているわ。\nさぁ、聞かせて。アナタが綴る、私の物語を…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/69, /*campaign_id*/10001, /*card_id*/200007, /*use_type*/0, /*index*/1, /*description*/"力強いエール、受け取ったわ !\n{0}君がくれる光は、私をステージに導く\n始まりの合図よ。今日も思う存分、輝いていくわ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/70, /*campaign_id*/10001, /*card_id*/200009, /*use_type*/0, /*index*/1, /*description*/"{0}さんの支えは、何より心強いんだ。\nアタシも勇気をもらったよ。\nいつだって自分らしさを忘れない、そんな強さを… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/71, /*campaign_id*/10001, /*card_id*/200011, /*use_type*/0, /*index*/1, /*description*/"どんな時も曇りなく在り続ける、眼鏡のように素敵なもの…\nそれは{0}さんとの絆です。\n二人の未来、視界良好ですよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/72, /*campaign_id*/10001, /*card_id*/200013, /*use_type*/0, /*index*/1, /*description*/"アタシだけじゃ想像もつかない展開を、\n{0}プロデューサーが形にしてくれたっス。\nこの先のお話も、二人三脚で描いてくっスよ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/74, /*campaign_id*/10001, /*card_id*/200015, /*use_type*/0, /*index*/1, /*description*/"自分を信じて、{0}さんと突っ走ってきた…\nそれが私のロックです !\nこの胸に宿る熱い魂、世界に発信していきましょう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/76, /*campaign_id*/10001, /*card_id*/200017, /*use_type*/0, /*index*/1, /*description*/"{0}さんとお仕事して、お話して、\n同じ時間を過ごして…。\nその一つ一つの思い出が、千枝を成長させてくれてますっ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/81, /*campaign_id*/10001, /*card_id*/200019, /*use_type*/0, /*index*/1, /*description*/"私、もっと輝くアイドルになってみせます。\n{0}さんがくれる刺激と、胸を満たす\nドキドキが…私の元気の源ですから♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/90, /*campaign_id*/10001, /*card_id*/200021, /*use_type*/0, /*index*/1, /*description*/"声援を受けて歌うのは、震えるほどに気持ちいいよ。\nそれが{0}サンのなら尚更だ…。\nアタシも全力の歌で、応えないとな !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/93, /*campaign_id*/10001, /*card_id*/200023, /*use_type*/0, /*index*/1, /*description*/"舞台の風に酔っていても、{0}プロデューサーの\n声が、私を導いてくれます。\n感謝の杯は、何度交わしても足りませんね…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/94, /*campaign_id*/10001, /*card_id*/200025, /*use_type*/0, /*index*/1, /*description*/"深淵の闇より深き盟友の力が、我を覇道の玉座に導かん !\n（{0}プロデューサーに声援をもらうと、\nとっても心強いです♪）", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/97, /*campaign_id*/10001, /*card_id*/200027, /*use_type*/0, /*index*/1, /*description*/"{0}さんに支えてもらえて、\n私は強くなれたんだ。…本当にありがとうね。\n心からの思いだもん、照れ臭くなんかないよ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/98, /*campaign_id*/10001, /*card_id*/200029, /*use_type*/0, /*index*/1, /*description*/"遠い海を越えて、{0}プロデューサーが描く\nfantasyに出会いマシタ。毎日がso happy…\nこれからはもっと、fantasticデスネ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/115, /*campaign_id*/10001, /*card_id*/200037, /*use_type*/0, /*index*/1, /*description*/"{0}さんの熱烈な応援、感じたわ。\nそれに熱い期待の視線も…。お礼はあっさりめがいい？\nそれとも…熱烈に？ ふふ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/119, /*campaign_id*/10001, /*card_id*/200039, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーに、星のように輝く\nシャースティエ…幸せ、たくさんもらっています。\n強く輝かせましょう…これからも、一緒に", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/84, /*campaign_id*/10001, /*card_id*/200067, /*use_type*/0, /*index*/1, /*description*/"どんな時でも、自分らしく…私なりの歩幅で、\n未来へ進み続けます。それが大事だって、\n{0}さんに教わりましたから…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/65, /*campaign_id*/10001, /*card_id*/200069, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーの熱い視線が、\nアタシを磨いてくれるんだ。みんなをトリコにする\nセクシーボディも、準備万端。いっぱい魅せてこ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/101, /*campaign_id*/10001, /*card_id*/200075, /*use_type*/0, /*index*/1, /*description*/"{0}と…がんばるの……楽しくて、好き……。\nこれからも…いっぱい、がんばって……\nもっと一緒に…笑いたい……", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/100, /*campaign_id*/10001, /*card_id*/200077, /*use_type*/0, /*index*/1, /*description*/"{0}さんがくれるあたたかな安らぎを、\n活力に変えて…昨日より輝く今日の私、\nそして明日の私へと、進み続けますっ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/106, /*campaign_id*/10001, /*card_id*/200083, /*use_type*/0, /*index*/1, /*description*/"{0}さんに応援してもらえると…\nドキドキして、ゾクゾクして、怖くなるくらい…。\nそれが、すごく幸せなんだよ…えへへ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/112, /*campaign_id*/10001, /*card_id*/200085, /*use_type*/0, /*index*/1, /*description*/"{0}さんの心意気、しかと受け取ったりー♪\nしゅーこちゃんも、マジメに応えたくなるね。\nま、どーんと構えてておくれ♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/85, /*campaign_id*/10001, /*card_id*/200091, /*use_type*/0, /*index*/1, /*description*/"…共に綴った記憶を懐かしむのは、まだ早いですね。\n二人で描く輝きの軌跡は、始まったばかり…\n貴方の笑顔が、物語っています", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/122, /*campaign_id*/10001, /*card_id*/200093, /*use_type*/0, /*index*/1, /*description*/"幾千の記憶と時が行き交うセカイで…\nキミの呼びかけも、ボクの存在を象るひとつのファクターさ。\n二人で描くミライは、まだ未知数だけどね", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/96, /*campaign_id*/10001, /*card_id*/200101, /*use_type*/0, /*index*/1, /*description*/"芳醇な香りは、{0}さんが丹精込めて\n育ててくれた証。\nでも、味わうのは少し待って。もっと美味しくなってみせるから", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/113, /*campaign_id*/10001, /*card_id*/200111, /*use_type*/0, /*index*/1, /*description*/"強く優雅な剣士を目指し、日々邁進する珠美です !\n{0}殿の声援も、そんな珠美を後押し\nしてくれますね ! 感謝です !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/118, /*campaign_id*/10001, /*card_id*/200119, /*use_type*/0, /*index*/1, /*description*/"非力なもりくぼも、{0}プロデューサーさんが\n笑ってくれるなら…やるくぼで、頑張ってみます…。\nできるだけ、ですけど…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/77, /*campaign_id*/10001, /*card_id*/200123, /*use_type*/0, /*index*/1, /*description*/"{0}さんの言葉ひとつで、\nこんなにも満たされるなんて…夢のようです。\nこれが、私たちを結んだ絆の証なんですね…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/120, /*campaign_id*/10001, /*card_id*/200125, /*use_type*/0, /*index*/1, /*description*/"{0}殿の信頼は、何よりの栄誉 !\n私を奮い立たせるその力…今、全身で感じています。\nいざ参りましょうか。戦場へ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/86, /*campaign_id*/10001, /*card_id*/200127, /*use_type*/0, /*index*/1, /*description*/"{0}の応援は想定内…\nでも、生まれる力は未知数よ。私も期待以上のパフォーマンスで\n応えてみせるわ。楽しみにしていて", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/80, /*campaign_id*/10001, /*card_id*/200135, /*use_type*/0, /*index*/1, /*description*/"{0}さんと共に、新たな光を創り続ける…。\n信じる道は変わりません。\nどれだけ時が経とうとも、この信念は、ずっと…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/121, /*campaign_id*/10001, /*card_id*/200141, /*use_type*/0, /*index*/1, /*description*/"{0}の期待を込めたパス、ビリビリくるぜ… !\nオレも本気のプレーで返さねーとな。\n最高にカッコイイ、アイドルってやつになってさ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/116, /*campaign_id*/10001, /*card_id*/200145, /*use_type*/0, /*index*/1, /*description*/"想いに応えるための、シミュレーションは不要だね。\n{0}Pと進む未来は、計算を超えていく…\n私自身が、そう感じてるから", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/114, /*campaign_id*/10001, /*card_id*/200153, /*use_type*/0, /*index*/1, /*description*/"前を向く自信を、{0}さんがくれたから、\n私はここにいます。\nそして、もっと輝く新しい未来へ、踏み出せるんです… !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/75, /*campaign_id*/10001, /*card_id*/200155, /*use_type*/0, /*index*/1, /*description*/"胸に響く{0}さんの声が、\nアタシに力をくれるんだ。もっと高く遠くまで、\n羽ばたいていける…新しいアタシになれる、力を !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/87, /*campaign_id*/10001, /*card_id*/200161, /*use_type*/0, /*index*/1, /*description*/"{0}殿の応援で、ライラさんの心は\nぽかぽかでございますよー。\nなんでもできそうな、つよーいぽかぽかでございますねー", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/73, /*campaign_id*/10001, /*card_id*/200171, /*use_type*/0, /*index*/1, /*description*/"{0}くんの言葉は、私を導いてくれる。\n輝く未来に通じる確かな道へと…。\nだからこそ迷わず進めるんだ。キミを信じて…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/111, /*campaign_id*/10001, /*card_id*/200177, /*use_type*/0, /*index*/1, /*description*/"{0}に幸せをもらって、心から笑顔になって…\nその笑顔が、次の幸運を呼ぶの。\nこれぞ幸運スパイラル、まさに敵なしね !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/82, /*campaign_id*/10001, /*card_id*/200185, /*use_type*/0, /*index*/1, /*description*/"{0}さんが、私の目指す未来を形にして\nくれました。あとは迷いなく進むだけ…。\n信じる気持ちを、この身に纏って !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/105, /*campaign_id*/10001, /*card_id*/200187, /*use_type*/0, /*index*/1, /*description*/"どこにいても、{0}さんの声は\nはっきりと聞こえます。\n私たちを結ぶ絆の糸が、安らかな音色を伝えてくれますから…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/110, /*campaign_id*/10001, /*card_id*/200195, /*use_type*/0, /*index*/1, /*description*/"{0}さんが見せてくれる景色は、\nみんな絵に残したいです。\nキラキラ光る夢の色と、みなさんの笑顔でいっぱいの絵を…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/103, /*campaign_id*/10001, /*card_id*/200207, /*use_type*/0, /*index*/1, /*description*/"{0}さんはいつも、私の心に優しい火を\n灯してくれるわ。今度は私が照らす番ね。\n情熱という名の、消えない光で…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/124, /*campaign_id*/10001, /*card_id*/200219, /*use_type*/0, /*index*/1, /*description*/"{0}さんの声は、安らぎと…勇気をくれる、\n優しい音色…。いつまでも、聞かせてください。\n隣に立って、温かな笑顔で…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/67, /*campaign_id*/10001, /*card_id*/200245, /*use_type*/0, /*index*/1, /*description*/"{0}くんに見つめられたら…\n体も心も熱くなってくるわ。\nこの熱を発散するなら、やっぱり…ステージの上が一番ね♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/104, /*campaign_id*/10001, /*card_id*/200255, /*use_type*/0, /*index*/1, /*description*/"目の前の、未来というキャンバス…\n何を描くかは未定だけど、土台の色は決まってるっすよ。\n{0}さんへの感謝の色っす !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/83, /*campaign_id*/10001, /*card_id*/200267, /*use_type*/0, /*index*/1, /*description*/"今は、{0}さんと持った絵筆で、\n未来を描いている最中です…。\n時と共に色を重ねて、世界はより美しく輝き始めます…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/66, /*campaign_id*/10001, /*card_id*/200273, /*use_type*/0, /*index*/1, /*description*/"アタシの背中…いつも押してくれるよな。\n{0}がついてくれるから、\nアタシらしく堂々と戦えるんだ。この先も、よろしくな !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/123, /*campaign_id*/10001, /*card_id*/200279, /*use_type*/0, /*index*/1, /*description*/"アタシがドコ目指すかなんて、お前にはわかってるっしょ。\n期待とプレッシャーに、最高のパフォーマンスで返すのは当然。\nいくぞ、その先にな", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/117, /*campaign_id*/10001, /*card_id*/200285, /*use_type*/0, /*index*/1, /*description*/"{0}プロデューサーの期待に応えて…\n少しずつでも、変わってる私を見せたいし。\nそれで喜んでもらえたら…いえ、何でも !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/99, /*campaign_id*/10001, /*card_id*/200287, /*use_type*/0, /*index*/1, /*description*/"決して凪ぐことのない、変わり続ける海…\n{0}さんとなら、恐れずに挑めます。\nどんな流れも、夢に繋がっていますから", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/78, /*campaign_id*/10001, /*card_id*/200289, /*use_type*/0, /*index*/1, /*description*/"誰もが輝く舞台に立てるわけじゃない…なんて、もう言わないわ。\n{0}さんがくれた、私のステージが…\nここにあるもの !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/107, /*campaign_id*/10001, /*card_id*/200301, /*use_type*/0, /*index*/1, /*description*/"熱烈な応援で元気をもらって、極上のあやかに変身ですぅ♪\n{0}さんが満足するくらい、\n全力で盛り上げますよぉ～", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/92, /*campaign_id*/10001, /*card_id*/200303, /*use_type*/0, /*index*/1, /*description*/"遥かな軌跡を{0}と辿り、高みへと近付く道。\n天を満たす星々を、身に纏う光に変えるために…\n歩みましょう、その先へ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/109, /*campaign_id*/10001, /*card_id*/200315, /*use_type*/0, /*index*/1, /*description*/"想いがこもった声は、心を動かす…そう学んだわ。\n{0}さんの声が、ステージに立つ私を、\n力強く支えてくれるように…", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/95, /*campaign_id*/10001, /*card_id*/200325, /*use_type*/0, /*index*/1, /*description*/"人生は、終わりの見えない旅のよう。でも退屈は感じないわ。\n{0}さんとの二人旅なら、\nいつまでも夢中でいられるもの", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/91, /*campaign_id*/10001, /*card_id*/200327, /*use_type*/0, /*index*/1, /*description*/"私の目指す美しさは、{0}さんと一緒にいてこそ\n磨かれるものよ。\n感謝の心は…ステージで見せる輝きにのせて、贈るわ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/125, /*campaign_id*/10001, /*card_id*/200337, /*use_type*/0, /*index*/1, /*description*/"ふり返れば、{0}プロデューサーと歩んだ道…\n幸運よりも力強く、私を支えてくれています。\nさぁ、また共に進みましょう♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/108, /*campaign_id*/10001, /*card_id*/220211, /*use_type*/0, /*index*/1, /*description*/"冒険は、いつだって{0}さんの言葉から\n始まります ! 私たちの力を合わせて、\n胸躍る夢とロマンをファンに届けましょう !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/88, /*campaign_id*/10001, /*card_id*/220225, /*use_type*/0, /*index*/1, /*description*/"ピチピチアイドル七海、\nプロデューサーのお墨付きいただきました～。\nおいしい魅力をもっと身につけて、みんなの心を一本釣りれす～♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/89, /*campaign_id*/10001, /*card_id*/220227, /*use_type*/0, /*index*/1, /*description*/"頂点は孤高の領域？ノー !\n{0}と私は魂のバディ。世界を見渡す景色も、\n二人のものよ ! 共に見ましょう、その果てを !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/79, /*campaign_id*/10001, /*card_id*/220231, /*use_type*/0, /*index*/1, /*description*/"{0}と過ごす日々で、多くを学んだよ。\nそしてこの先も、未だ見ぬ景色が待っているだろう。\nフフッ、血が滾るというものさ", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/102, /*campaign_id*/10001, /*card_id*/220249, /*use_type*/0, /*index*/1, /*description*/"お姉さんをその気にさせて…\n相変わらず、{0}君は大胆ね♪\nいいわ。今日は夜が深まるまで、付き合ってあげる…♪", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/126, /*campaign_id*/10001, /*card_id*/300001, /*use_type*/0, /*index*/1, /*description*/"おおっ ! ？胸の奥が熱い !\nこのエネルギーで目指せ、お空の特大一等星 ! それじゃ\n{0}プロデューサー、彼方まで飛んでくよっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/127, /*campaign_id*/10001, /*card_id*/300003, /*use_type*/0, /*index*/1, /*description*/"のんびりお散歩をして…\nふと、横を見ると、笑顔の{0}さんがいる。\n私の幸せって、そういうことなんだと思います", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/129, /*campaign_id*/10001, /*card_id*/300007, /*use_type*/0, /*index*/1, /*description*/"{0}せんせぇの『がんばれー』をもらうと、\nかおるは元気パワーいっぱいだよ !\nよーし、今日も笑顔で行ってきまーっ !", /*insert_word_type*/0);
INSERT INTO `idel_vote_idol` VALUES (/*id*/130, /*campaign_id*/10001, /*card_id*/300009, /*use_type*/0, /*index*/1, /*description*/"直球で来てくれるなぁ、{0}さん。\nけど、キライじゃないぜ。むしろ…火がついた。\nOK、アタシの限界、超えてやろうじゃんか !", /*insert_word_type*/0);
