INSERT INTO `individual_tutorial` VALUES (/*id*/20003, /*img*/2, /*discription*/"「設置」ボタンを押せば、倉庫内のルームアイテムを\nルームに設置できます。");
INSERT INTO `individual_tutorial` VALUES (/*id*/20003, /*img*/3, /*discription*/"いらなくなったルームアイテムは「売却」ボタンで、\n売却してマニーに変える事ができます。");
