INSERT INTO `meetup_memory_data` VALUES (/*id*/16, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(山中)②パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"繊細だけど強い子だから、ナナがしてあげられることは\nあんまりなくて。でも、一緒にいることはできます。\nプロデューサーさんがいつも、ナナに安心をくれるみたいに !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
