INSERT INTO `meetup_memory_data` VALUES (/*id*/46, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"村のみなさんの笑顔です。\n会う人会う人、びっくりするくらい優しくて…… !\n最初は避けられてたように感じましたけど、気のせいでしたねっ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/47, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/0, /*description*/"赤い鳥居が、山の奥のほうまで\nいくつもいくつも続いていました。\nプロデューサーさん、一緒に数えながらくぐりませんか？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/48, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/5, /*description*/"それは、プロデューサーさんが隣にいないことで……。\nどんなときも一緒なのが当たり前になっちゃってて。\nナナってば、甘えん坊ですよね。17歳ですから。えへへ☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/49, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"はぁとちゃん、村のみなさんに囲まれてますねぇ。\nあ、また大きな笑い声が……♪\nナナもはぁとちゃんみたいに、人の輪の中心になれたら……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/50, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)③ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"ナナはずっとメイド喫茶のメイドさんでしたし、\n接客は得意なんですけど……。\n「もえもえきゅん♪」は……引かれちゃいますかね……？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/51, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"はぁとちゃんとナナは、ぴったり凸凹の関係なんです。\nはぁとちゃんが開けてくれた突破口に、ナナも最高速で……\nいきますよぉ～ ! やりますよぉ～ ! !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/52, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(村)③パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/8, /*description*/"でも ! ナナだけのいいところ、\nプロデューサーさんにいっぱい教えてもらったので !\n引かれてもいいんです ! 何度でもアタック ! ミンッ !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/53, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"可愛い浴衣ですよね♪\n村の人が貸してくれたんです。\nえへへ、どうですか、プロデューサーさん？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
