INSERT INTO `meetup_memory_data` VALUES (/*id*/112, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(岬)②パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"はぐれないように、手を……ですか……？\nあ、あのっ、あんまりこっちを見ないでくださいね？\n暗くてよかったです。今、どんな顔してるかバレなくて……♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
