INSERT INTO `meetup_memory_data` VALUES (/*id*/115, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(岬)③グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/9, /*description*/"今年もやりたかった、プロデューサーさんと一緒のお花見。\nお仕事しながら叶っちゃいました♪\nアイドルの日々って、やっぱり素敵です☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/116, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(岬)③パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"ナナも、ちょっと変わった色のアイドルかもですけど……。\nだから、プロデューサーさんに見つけてもらえたんです。\n必死に咲こうとしたから、出会えたんです !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
