INSERT INTO `meetup_memory_data` VALUES (/*id*/121, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)②", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/9, /*description*/"秘境にある温泉も、ばっちりレポートできました !\nナナとプロデューサーさんのお仕事、\n今回も大成功ですね ! ミン☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/122, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)②ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/0, /*description*/"でも、ナナが知らない景色も、\nもっとたくさんあるって思うんです。\nまだまだいけますよぉ ! ピカピカのJKですし !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
