INSERT INTO `meetup_memory_data` VALUES (/*id*/124, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)②パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/1, /*description*/"誰にも内緒の、秘密のナナもいたりして……♪\nプロデューサーさんがいつか、探しにきてくれたら……\nそして、受けとめてくれたら……ありのままのナナを、いつか !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/125, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)③", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/1, /*description*/"ナナはきっと、\n「夢を叶えた人間」なんだと思います。\nでも、夢を叶えたそのあとも……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/126, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)③ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/0, /*description*/"あ、急にへんなこと言っちゃってすみません。\nでも、こんな月の綺麗な夜にしかしゃべれないことって、\nありますよね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/127, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)③グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/9, /*description*/"仲間と、プロデューサーさんと、いっぱい笑っていきたいです。\nアイドルとしての、当たり前の日々を、\nなにげない毎日を、今日も楽しかったなって。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/128, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(岬)③パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"今いるここが、「ずっと続く夢の中」で。\nやっと手に入れたキラキラの宝物なんです。\nナナはいつまでも放しませんよ。ねっ。プロデューサーさん♪ ぎゅっ☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/129, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"お仕事(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/6, /*description*/"え゛ぇ゛～ん゛ !\nナナは、人生17年で終わりたくないですよぉ～ !", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/130, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"お仕事(上空)②", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"はぁとちゃん……？\nえっ、念のため奪った斧で、橋を落としておく…… ! ？", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/131, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"お仕事(上空)③", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"そのとき空から、不思議な光と、ヘリの轟音が降りてきたのです…… !\nた、助かりましたぁぁ !", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/132, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/3, /*description*/"はぁ、はぁ、な、なんとか空に脱出しましたけど、\nもうダメです……膝が……腰が…… !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/133, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/6, /*description*/"ああっ、まだまだ諦めていない方たちが !\n熱心すぎるファンの方々でしょうかぁ !\nさよ～なら～ ! ま、またいつか会えますよぉ、たぶん ! キャ、キャハ !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/134, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"こ、ここはっ……火事場のウサミンパワー !\nナナは、激しいステージをいっぱい乗りこえてきたんです !\nベテランアイドル、17歳なんです !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/135, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"コミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/5, /*description*/"プロデューサーさんも火事場のウサミンパワーを……？\nそ、そんなっ、ふらふらのナナをぎゅっと支えて……\nでも、ナナがお姫様なのはそういう設定なだけで……ドキドキミン……。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/136, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/3, /*description*/"村のみなさん、ナナが「17歳の娘」ってところにこだわってました……。\nだから、ナナたちが全員、助かるには…… !\nナナが……ホントのことを言えば……っ !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/137, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/6, /*description*/"うっうっ、夢は今日で終わりです……。\nプロデューサーさん、ナナが本当は何歳でも……\nまだ、アイドルを名乗れますか……？", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/138, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"ナナがどうなっても、プロデューサーさんが助かるなら…… !\nプロデューサーさんのおかげで、ナナは今日まで来たんです。\n本望ですよぉ !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/139, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"スペシャルコミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/9, /*description*/"ナナも、ナナの秘密も、未来も、夢も、ぜんぶ守ってみせる ! ？\nうっうっうっ、プロデューサーさぁぁぁん !\nやっぱりプロデューサーさんは、ナナのプロデューサーさんですぅぅ !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/140, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/9, /*description*/"ピピッ☆ ウサミン星からプレゼントが届きました♪\n中身は……なんと ! ウサミンパワーたっぷりの、\nプロデューサーさんが元気になっちゃうアレです ! どうぞ♪", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/141, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"あ、あのっ ! プロデューサーさんに贈り物を用意してて。\nナナなりに、いろいろたくさん、考えましてですねっ……。\n喜んでくれたらいいなって。え、えへへ…………キャハッ☆", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/142, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/5, /*description*/"プロデューサーさんには、いつもい～っぱいもらってばっかりで。\nだから今日は、ナナからお返しをしたいんです。\nあげて、もらって。そんな毎日がずっと続いたら、楽しいですし♪", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/143, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/5, /*description*/"ひ～……ふ～……。今回もいろんなことがありますねぇ……。\nでも ! 一緒にもっと先の景色を見たいからっ ! パワー注入です !\nプロデューサーさんも、せ～のっ……ウーサミン、ハイッ☆", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/144, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"ううぅ、ナナってば、いつもバタバタしてばっかりで～ !\nプロデューサーさんには恥ずかしいところも見せちゃってますぅ\n……それでも、アイドルは、やっぱり楽しい……えへへ♪", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/145, /*event_id*/9001, /*memory_group_id*/1, /*chara_id*/154, /*title*/"安部菜々", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/18, /*description*/"いつのまにか、ずいぶん遠いところまで来ちゃいましたね。\nプロデューサーさんが隣にいるのも、気づいたら当たり前で。\nこれからも、ナナと一緒に歩いてくださいね♪", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"安部菜々", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
