INSERT INTO `meetup_memory_data` VALUES (/*id*/147, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"凸凹道もなんのその !\nでも今目の前にあるのは……\n道じゃなくて、崖じゃね？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/148, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"アイドルはときに体を張るし崖にも登るもの……\nはぁと知ってはいたんだけどね。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/149, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)④", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/4, /*description*/"おーいプロデューサー、\n崩れたメイクは、見ないふりをするんだぞ？ な？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
