INSERT INTO `meetup_memory_data` VALUES (/*id*/151, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)⑥", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"いくぜパイセン、気合注入☆\nいけるぞしゅがみん !\nやれるぞしゅがみん !", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/152, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)⑦", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"ふたりならどうにかなるし、\n3人だったら……\nなんとでもなる☆", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/153, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(山中)⑧", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"あ、あの木の実美味しそ～。\n……あ～ん、届かない☆\nちょ、見て見ぬふりすんな☆", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/154, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"しゅー……がー……はー……、しゅー……がー……はー……、\nあーん、あーん、早く温泉に入りたいよおおおおおおおお !\nオルルルルルルルルァッ☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/155, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"雄叫び？ 威嚇？ ノンノンノン☆\n声上げて気合入れようとしてんの !\n……あ、今やまびこ聞こえたな。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/156, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"ううん、だめよはぁと……現状を嘆いて吠えるんじゃなくて、\n突破することを考えるの ! そう、例えば可愛くおねだりして……\nプロデューサー、おんぶして？ ……おい、無視すんな☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/157, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"ま、ただの温泉ロケじゃないのはちょっと予想してたけど。\nプロデューサーは、獣道も突っ走るはぁとたちが好きだもんな♪\n期待はば～っちり超えてあげる。覚悟しとけ☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/158, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"な、菜々パイセン ! ？ どうした ! ？\n脚が小鹿のようにがくがくだぞ ! ？\n……え？ 脚よりも腰のがヤバめ？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/159, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"無茶振りは上等でも、無理はノンスウィーテイー !\nちょい休憩しよっか。はぁ……どっこいせっ☆\nプロデューサー、湿布あったらくれ♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/160, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"ピンチのときも、支え合うのがユニットだもん !\nパイセン、気合の掛け声いくぞ♪\nしゅがしゅが～？ み～ん ! !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/161, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/9, /*description*/"うんうん、キッツいときはスウィーティーな気分になりたいよね。\n休憩する今がチャンスだぞ、プロデューサー。ほら、言って？\n「頑張ってるふたりは、超々々々可愛いよ」って♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/162, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/6, /*description*/"見て見て、プロデューサー !\nはぁと、謎の祠発見しちゃった♪\nかなり昔のっぽいけど、ちゃんと果物とか供えてあるんだよね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/163, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"つまり～……近くに人が住んでるってこと♪\nよっしゃ、行くか ! 『しゅがしゅが☆み～ん』を\nここぞとばかりに売り込んで、熱烈なファンにしちゃる☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/164, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"おやつのチョコバーを供えて……\nしゅがーはぁとの可愛さを、全人類に届けてみせる☆\nお願いっていうより、宣誓になっちった♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/165, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"崖の近くでも、しっかり支えてくれる人がいる。\nどっかで聞いた話で、親近感わいちゃった♪\nふふっ、元気回復したし、このまま、山を制覇すっぞ☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/166, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"パイセンと……プロデューサーと……一緒だもん……。\nぜぇ……はぁ……これくらいのサバイバル……平気だっつうの !\n…………いっけなーい ! 乱暴な言葉出ちゃったメンゴ☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/167, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/4, /*description*/"今のはカット ! カットな？ こら、プロデューサー、\nカメラを回さない ! ちょっ、菜々パイセンまで ! ？\nいい撮れ高？ ダメダメ ! ノンスウィーティーだし !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/168, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/7, /*description*/"……たしかに、スウィーティー切れかもな。\n崖登ったり茂みかきわけるのに必死で気づかんかった。\nプロデューサーは、いつもはぁとのことをよく見てくれてるよね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/169, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"人間、必死になると本音出るっていうよねー。\nそ、今のはふたりへの素直な気持ちだから。\nちょい言葉は乱暴でも、忘れてとまでは言わないぞ♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/170, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"あーん、脚が棒になったー ! もう動けないかもー !\nそしたらずっとここにいて……\n3人でなっちゃうかー？ アダムとイヴ☆", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/171, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/7, /*description*/"でも、それはらめぇ……☆\nだって、はぁとはみんなのアイドルだから……♪\nよっし、あともうちょい根性出してけスウィーティー !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/172, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"そうそう、しゅがみんなら、どんな山の中でも\n生きていけるし ! ……ってそれ褒め言葉だよな？\n褒め言葉として受け取っとくぞ？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/173, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"ま、まずはアイドルとして、目の前のお仕事を\nスウィーティーにこなさないとだし？\n……アダムとイヴは、もうちょい先までお預けな？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/174, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/11, /*description*/"……っ ! あー、今、脳内ぶっ飛んでたわ。スウィーティー\nキングダムで水飴入り練乳がぶ飲みする白昼夢見てた……\n山を登るには、ちょーっとキツくなってきたかも……。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/175, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"はぁとは甘～い夢を見るだけじゃ満足しない……\n夢は現実で叶えてこそ、だもんな。どんなにキツくても、\nここは3人4脚で乗り越えてくとこ !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/176, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"そのとき、空から助けの手が伸ばされたのです……\nサンキュ、パイセン ! この恩はあとで絶対返すんで !\n手を掴んで……しゅがしゅが……ふんぬぅううううぅう !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/177, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/5, /*description*/"はは……プロデューサーってば、\nこの手でどこまでも引っ張り上げてくれるんだよな。\n佐藤ならまだやれるだろって、力強くさ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/178, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"わざわざ村長みたいな人が挨拶に来たんだよね。\n久々のお客さんだからって。律儀☆", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/179, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"ちょうど村祭りがあって、\nぴったりの着物もあって……\nタイミング良すぎない？", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/180, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"ここにいると、はぁとたちピンクと黄色で超目立つ !\n天性のアイドルだな♪", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/181, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)④", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"風きもちー、空気もうめー !\nパイセンも一緒に深呼吸♪\nすー、はー☆", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/182, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑤", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/1, /*description*/"お腹いっぱーい♪\n大皿いっぱい料理出てきて、\nちょい実家を思い出した☆", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/183, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑥", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"そっちの部屋、行かない方がいいかも。\nなんかお札がいっぱい貼ってあったし？", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/184, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑦", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"温泉への道は聞けたけどさ、\n立ち入り禁止の場所があるから、あとで案内するって。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/185, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑧", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"お祭りではお面つけるんだって。\nでも急だから、はぁとたちのはないみたい。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/186, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/0, /*description*/"びっくりしたな。村に入った途端、めっちゃもてなされたもん。\n村に若者が来たから安泰とか言われたし。\nなんか村の神様？ も満足するんだとか。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/187, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"やっぱ山の奥なだけあって、外から観光に来る人も\n少ないらしくってさ。久々のお客さんだからって\nめっちゃ美味しい料理用意してくれたわ。スウィーティー♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/188, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"聞いた？ プロデューサー、聞いた？ 若者だって !\n若くてきれいなお嬢さんだって ! やっぱわかる人には\nわかっちゃうなー。はぁともまだまだぴちぴちだって☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/189, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/12, /*description*/"最初は遠慮があったけど、今はいろんな人と仲良くなったし。\nおもてなしのお礼に、しゅがみんから一曲どーよ？\nよし、プロデューサーからOK出たし、歌うか、パイセン !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/190, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"さっき、この家の外れで看板見つけてさ。\nどこに案内する看板か気になったんだけど、\n文字がかすれてて読めなかったわ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/191, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"で、村の人に聞いたら、ここの名所は温泉と\nたくさんある祠くらいなんだってさ。\n小さい村だから、すぐ見て回れるらしいし、あとで行くか !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/192, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"はぁとにはわかる ! あの看板が指し示すのは……\nデートスポットになる絶景☆ ってことで～、\nプロデューサー、このあとふたりで行ってみる？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/193, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"村の人に聞いてみたんだけど、知らないって言われたんだよね。\n誰も知らない、まだ見ぬ場所……これ、番組的にチャンスじゃん？\nあとで菜々パイセンも誘って行ってみよーっと !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/194, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"事務所で配れるお土産、何かないかなって聞いたら\nこれもらっちった ! 村に代々伝わる工芸品らしくて、\nお守りにして持っとくといいんだって。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/195, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"んー、中身なんだろ？ もちろん取り出したりする気は\nないんだけどさ。なーんか甘い香りしてるし。\nドライフラワーでも入ってんのかなー？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/196, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"たくさんもらったから、事務所のみんなの分はあるよ。\n裁縫道具もちゃんと持ってきてるし、ひとりひとりの\n個性に合わせて、はぁとが可愛～くデコっちゃう♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/197, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"プロデューサーの分もおそろいでもらってきた☆\nもちろん、はぁとがちゃんと刺繍してあげたぞ !\n「あい らぶ しゅがーはぁと」って♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/198, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"秘境っていうだけあって、ちょっと変わった場所ではあるよね。\nここにしかない伝統とか、言い伝えとかいっぱいあって。\nレポも兼ねて、パイセンとばっちり調査してたの。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/199, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"村長さんに「あの祠に行ったんか」って言われたから、\nチョコバーお供えしてきたって答えといた。\nよくわからんけど感謝されちった♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/200, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"子どもたちに、村に伝わる歌を教えてもらったんだよね。\n何語かわかんない歌だったけど。お返しに、『凸凹スピードスター』を\n教えといた。もちろん、振りもつけて☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/201, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/12, /*description*/"ここがどんな村だったとしてもさ、3人でなら\nなんとかなるっしょって思ってる。今までもそうしてきたし。\nもしかしなくても、はぁとたちってば無敵かも♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/202, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/1, /*description*/"ふぁ～……ちょいねむぅ……。\nあれだけ頑張って山の中進んできたからかなー……。\nん……ちょっと気、抜けちゃったわ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/203, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/4, /*description*/"山で叫んで疲れたし、いっぱい食べたら眠くなってきて……\nちっがーう ! はぁとは赤ちゃんでも牛でもなーい !\nプロデューサーの可愛いアイドル☆ りぴーとあふたみー♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/204, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"プロデューサー、ちょっとここに座ってみて？\nそれで……ごろーん☆\nほれほれ、今こそはぁとを膝枕で甘やかして？ ね？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/205, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/5, /*description*/"でも、疲れてるのはプロデューサーも同じでしょ？\nほら、肩貸して？ はぁとはいつも鍛えてるから体力あるし。\n時間が来たら、優しく起こしてあげる……♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/206, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"んー、今ね、撮ってた映像見返してたとこ。\nプロデューサーも、可愛いはぁと、チェックしたい？\n……おい、首を横に振んな☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/207, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/4, /*description*/"おぉう、めっちゃブレてる映像あるなー。\nこれは泥に足を滑らせてパイセンと転んだときのね。\n水も泥も滴ってこそのいい女だけど……あんま見んな☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/208, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"ま、プロデューサーにとっては今さらかな。\nずっと近くではぁとたちのこと見てたわけだし？\nんもう、いいとこどりしちゃってこのこの～☆", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/209, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)③パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/5, /*description*/"とか言って、いざ見始めると誰よりも真剣な顔して\nチェックすんの、プロデューサーは。んで、その横顔\n見てると思うんだよね。すっごく愛されてるなって。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
