INSERT INTO `meetup_memory_data` VALUES (/*id*/246, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(岬)⑤", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"帰るまでがロケなんで。\nお互い、気を抜かずに駆け抜けましょ、パイセン☆", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/247, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(岬)⑥", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/12, /*description*/"しゅがみんは凸凹道を突っ走る……\nどころか、崖まで登れてパワーアップ !", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/248, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(岬)⑦", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"ひと通りレポも撮影もした !\nってことで、3人で……\nひとまずお疲れさまっした !", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/249, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(岬)⑧", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"ドキドキの非日常が、\nアイドルには日常だもんな。\nずっと楽しめるわ、これ♪", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/250, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)①", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"さっき？ ああ、村の人たちとちょっと話してたんだよね。\n村祭り用の供物がほしいって言ってたから、協力しといた。\nうん、あげたぞ？ しゅがーはぁとのブロマイド☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/251, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)①ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"「はぁとの魂がこもったとっておきの一枚 ! 」って\n言ったら受け取ってくれてさ。一番可愛～く\n映ってるのをあげて、大正解だったな♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/252, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)①グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"その顔……さてはプロデューサーも欲しかった？\n大丈夫、100枚でも1000枚でもあげるから、スウィーティーな\nはぁとアルバムにしまっとけ☆ そんなのない？ 作れ☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/253, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)①パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"いつでもはぁとのファンにあげられるように……ってワケじゃ\nなくて、お守り代わりに持ってたんだよね。デビューしたての\n頃に撮ったやつ。見るたびに、頑張ろうって思えるからさ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/254, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)②", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/10, /*description*/"この辺は村からちょい外れてるからかな？\nギリ電波も通じるっぽい。だからだと思うんだけど……\nはぁとのスマホ、さっきから通知来まくってるわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/255, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)②ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"プロデューサーの方もそうなんじゃね？ お仕事の連絡も\n来てそうだけど、お仕事とはいえ連絡とれなくなったら\n心配しそうなアイドル、けっこういると思うんだよね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/256, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)②グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"お、いつメンから連絡来てた !\n「温泉いいな、お土産話楽しみにしてる」だって !\n語っちゃうか、文字通り、山越え谷越えの道のりを♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/257, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)②パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"親からだな。「連絡通じないけどどこにいるの？\nさすがに心配する」……そんなに長い間連絡してなかったっけ？\n「今は、プロデューサーと温泉に来ています☆」っと。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/258, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)③", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"月も星空も綺麗……ロマンティックでスウィーティーな\n景色だな☆ 世界にプロデューサーとふたりだけみたい\n……って、なんか声しない？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/259, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)③ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"やっぱ、はぁとにアイドルのオーラがあるから？ どこにいても、\nファンに見つかっちゃう☆ 村の子かなー？ 隠れてないで、\n優しいはぁとお姉ちゃんの前に出ておいで～♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/260, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)③グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"逃げた、とか逃がすな、とか聞こえるけど……\n飼ってる鶏でも脱走した？ はぁとたちは、別に逃げてないし？\nこれはお忍びっていうの。お忍びで・ぇ・と☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/261, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(岬)③パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"うおっ、菜々パイセンじゃん ! びっくりした～。\n急に茂みから出てきてどしたん？\nいいよいいよ、このまま3人でデートしよっか♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/262, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)①", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/5, /*description*/"プロデューサー、どしたん？\nそんなに熱い瞳で、はぁとをじっと見つめて……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/263, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)①ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"このあと出発？ 相変わらずのハードスケジュールだけど、\nオッケー♪ ふたりきりで「逃げよう」なんて言うから、\nアブナイお誘いかと思ったぞ☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/264, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)①グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/1, /*description*/"ん……っ。\nあ、髪に花びらついてた？そっか……。\nびっくりした……思わず目つぶっちゃったわ……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/265, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)①パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/5, /*description*/"もしかして、湯上がりはぁとに見惚れて……ぶぇっくしょん☆\nあー、湯冷めしないように、上着かけてくれんの？\nありがとう……そんなに優しいと、惚れるぞ☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/266, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/12, /*description*/"村を出たいっていう子がいてさ、色々話したんだよね。\n夢を叶えたいからってことだったけど、不安そうな顔してて……\nはぁとを見てみろ、大丈夫だからって伝えといた。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/267, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"上京するときって、不安と期待で胸いっぱいになるんだよね。\n無邪気だった少女時代のはぁとだって……あ、今も\n無邪気で純粋な可愛い少女だったわ。てへっ☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/268, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"はぁとを見てたら、わかるはずだもん。いつでもどこでも、\n夢は突っ走るパワーをくれる。それがあれば、どんな壁も\nぶち壊せるって。物理的なパワーじゃなくてな？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/269, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"村を出た先で、あの子も出会えるといいよね。自分の夢を\n肯定してくれる、唯一無二の大切な人に……おい、\nプロデューサーの話をする真面目なはぁとだぞ。よ～く聞いとけ？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/270, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"夜、誰かと一緒にいる時間って好きなんだよね。\nいつメンと飲んでるときもそうなんだけどさ、\nこうやってプロデューサーといるときも、大好き☆", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/271, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"お祭り、けっこう静かだったなー。何かの儀式？ の準備でみんな\n忙しいらしくて。ま、綺麗な着物を着たスウィーティーな\nはぁとをプロデューサーにお披露目できたし、大満足♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/272, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"独りでめげそうな夜も、明日はどうしても頑張りたいって夜も、\nプロデューサーのことを思い出してパワーもらってんの。\nつまりー、なんていうかー、ずっと一緒☆ みたいな？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/273, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/5, /*description*/"毎晩、一緒にいる時間があるといいよね。\n出迎えたり出迎えられたり、家族みたいにさ。\nあ、ちょい待ち、実家から電話……プロデューサーも話す？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/274, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/9, /*description*/"くぅっ……プロデューサー、\nやっぱかっけぇ……。\nはぁともヘリで駆けつけたーい !", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/275, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(上空)②", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"簡単に人生諦められんなら、\n崖っぷちで粘ってない !\nパイセン、するよ、脱出 !", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/276, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(上空)③", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"独りだったら、思いきれてなかったかも。\nありがと、プロデューサー、パイセン !", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/277, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/9, /*description*/"ふふっ、あははっ、なにこれ♪ 菜々パイセンとダッシュで\n村を出て、そしたら迎えのヘリが来てさ、こんなとんでも展開、\n一周回って笑うしかないっしょ ! あーっ、楽しい☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/278, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/7, /*description*/"っていうかプロデューサー、これ、村人も込みで全部ドッキリだな？\nいや～、村の様子が変だなって途中から思ってたし。\nあ ! ドッキリだったら可愛～い反応見せつければよかった !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/279, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/12, /*description*/"マジで今、どうにかなるモードだわ !\n全速力で必死に駆けて、手を取り合って縄はしごのぼって !\nパイセン、加速するよ ! しゅがしゅが～？ み～ん ! !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/280, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"このまま3人でどこまで行ける？ どこまで行っちゃう？\nこの3人なら、たとえ世界の果てだって !\nあははっ、言っててすっげぇ青春感あるわ☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/281, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"あばよ、謎の村♪ 懐かしくなったらまた来るから、\nその日までに、全員がはぁとのファンになってること !\n約束な☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/282, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"ヘリに乗ってもまだ見送ってくれてるみんなには、\n投げキッスをプレゼント♪ どんなときでも\nファンを想って行動するのがはぁとだもん☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/283, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/6, /*description*/"めっちゃもてなされたし、綺麗なお嬢さんって言ってもらえて\n嬉しかったけど、はぁとはまだ、どこかに留まるつもりはないの。\nまだまだプロデューサーのアイドルしなきゃだから☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/284, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/12, /*description*/"引き留めたい気持ちもわかるんだけどね……でも、はぁとには\nやりたいことがまだまだいっぱいあるの ! プロデューサーや\n菜々パイセンと一緒に、アイドル道、全速力で駆けていくとかな☆", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/285, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"ほらぁ、待ってたんだろ～？ はぁとからのプレゼント☆\nんもう、遠慮すんなって☆ はぁとがプロデューサーを想って\n選んだんだから、もらって嬉しいこと間違いなし、だぞ♪", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/286, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"受け取ってね、はぁとの気・持・ち♪\n返品は受けつけてないけど、お返しなら大歓迎な？\nってコトで、プロデューサーの気持ちも、はぁとにちょーだい☆", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/287, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"これはね～、はぁとのビッグハートが詰まったプレゼント♪\nん？ 「嬉しいよ。でもはぁとがいるだけで幸せだから」？\nえ、そんなこと言ってない？ 言えよ☆", /*memory_category_type*/3, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/288, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/5, /*description*/"あ゛～……根性出した……限界超えたわぁ、マジで。\nでもこれって嬉しいことなんだよね。プロデューサーとなら\nなんでもできるって、はぁとを無敵にさせてくれるから♪", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/289, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"はぁとの可愛いとこも根性出すとこも、大人なとこも\n好きって言ってくれる人がいてさ。その人の前だと、\nいろんな自分を見せたくなんの。……あれ、聞いてる？", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/290, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/18, /*description*/"どんなにキツくても、ピンチでも、はぁとはアイドルでいる\n自分が好き ! 夢を諦めなくて、プロデューサーと出会って、\n素敵にプロデュースされた、今の自分に恥じない仕事をすんの♪", /*memory_category_type*/4, /*memory_category_value*/0, /*talk_chara_name*/"佐藤心", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/291, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/7, /*description*/"この斧……不思議。持つと\n力が湧いてくるの。声も\n聞こえてくるような……？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/292, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"えいっ ! ふふ、虫が\n止まっていたのでつい……。\nあら、顔色が悪いですよ？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/293, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/4, /*description*/"汗と埃でべたべただわ。\n海にいれば流せたのに。\nいえ、弱音はいけませんね。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/294, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(廃ビル)④", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"足元に気をつけてください。\nところどころ崩れています\nから。はい、手を♪", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
