INSERT INTO `meetup_memory_data` VALUES (/*id*/331, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/1, /*description*/"遊園地……近くにあったんですね。\nそれほど大きくはないけれど、\n黒服たちをやり過ごすにはちょうどいいかもしれません。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/332, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/8, /*description*/"あら、ふふっ、彼ら、着ぐるみに絡まれているわ。\n力ずくで振り切れないみたい……\nこれは使えそうです。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/333, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)①グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/3, /*description*/"さっき、斧を「どこの売店で買いました？」\nなんて聞かれてしまいました。\nお化け屋敷のグッズで、たしかにありそうですね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/334, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)①パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/5, /*description*/"……っ !\n一般のお客さんも、まだいたのね。\n驚かせないよう、斧はパレードの小道具に……できるかしら？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/335, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/6, /*description*/"斧は今は何も……いえ、少し軽い？\n遊園地に来られて、喜んでいるのかしら。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/336, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)②ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/3, /*description*/"でも、斧を持ってアトラクションに\n乗ってはいけないのよ。\nパレード参加くらいに留めましょうね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/337, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)②グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"「斧も遊園地に来られて、嬉しい ! 」\nだそうですよ、よかったわ♪\n……ふふ、少しは和みました？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/338, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)②パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"「あの人ときた、思い出の……」\n……？ 今、どこからか声が聞こえたような？\nほかのお客さんの会話かしら。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/339, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/4, /*description*/"ねぇ、愛海ちゃん、疲れてな……愛海ちゃん？\n嘘でしょう ! ？ いないわ…… !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/340, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)③ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/1, /*description*/"ごめんなさい、取り乱して。\nあの子は、ここで捕まるような柔な子じゃないわね。\n信じないと、無事を…… !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/341, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)③グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"よかった、キャストのお姉さんと一緒ね。\n…………不埒な手を、しているみたい。\nふふふふ、迎えに行きましょう。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/342, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(逃走、遊園地)③パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/3, /*description*/"愛海ちゃん、よかった ! 黒服は……見逃してくれた？\nや、優しいのね……追ってきているのは、\n斧が目当てということ？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/343, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/3, /*description*/"子どもの頃、夜の遊園地に憧れていたんです。\nほら、夕方には帰ろうってなるじゃないですか？\nだから、最後まで残りたいってダダをこねていて……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/344, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/6, /*description*/"愛海ちゃんの楽しみようを見ていたら、\nふと思い出してしまったんです。\nふふ、可愛いですよね……少し落ち着いてはほしいけれど。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/345, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/7, /*description*/"追いかけられて、逃げている状況なのに……\nだからこそ、少しわくわくしているのかしら？\n憧れた、ドキドキする夜の遊園地だわって。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/346, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"ありますよ、ダダをこねること。\nこれでも私、結構わがままなんですから。\nでも、あなたにわがままを言うのは、ここを逃げ切ってからです。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/347, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/8, /*description*/"夜が更けてからが、私たちのチャンス。\nあちらもずっと動けるわけありませんし、\n機をうかがいましょう。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/348, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"私はまだまだ動けますよ？\n昔……月に何回夜勤が入ったか……ふふ、ふふふ。\nおっといけない、秘密にしてくださいね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/349, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/6, /*description*/"あちらが、私たちをか弱いものと思ってくれていれば\nいるだけ、意表をつけますから。\nちょっと斧を振り回しすぎてるかもしれませんけど。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/350, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/4, /*description*/"黒服がいないところを見つけたんですか？\nさすがです…… ! 目がいいんですね !\n体力のあるうちに、突破しないと…… !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/351, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/6, /*description*/"遊園地はいいですね、食料があって、\n私たちも栄養補給ができますもの。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/352, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/4, /*description*/"とはいえ遊園地の食材だと栄養素に偏りが……\n普段なら許せませんけど、非常事態ですし……\nこら、愛海ちゃん。チュロスの食べすぎよ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/353, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/2, /*description*/"帰ったらみんなでゆっくり、お祝いのごはんを食べましょう。\n私はさっぱりしたものがいいわ。冷たければなおよしです。\nいくら水着でも、やっぱり暑いですから。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/354, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/3, /*face_id*/5, /*description*/"私たちに分けようとせずに、あなたも食べてくださいね？\nかたくなな人は……はい、あーん。\n食べるまで、ずっとこのままですから。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
