INSERT INTO `meetup_memory_data` VALUES (/*id*/359, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(来ちゃった山頂)⑤", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/7, /*description*/"静かですね。黒服たちも、\nそろそろ諦めてくれたのなら\nいいのだけれど……。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
