INSERT INTO `meetup_memory_data` VALUES (/*id*/363, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)①", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/1, /*description*/"ん……なんて澄んだ空気。\n爽やかすぎて、斧が震えていないかしら？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/364, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)①ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/7, /*description*/"なになに……「日差しが強すぎて日焼けする」？\nやだ、ごめんなさい。\nそうよね、レディだものね。気をつけます。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/365, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)①グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/3, /*description*/"もうっ、プロデューサーが揺らしてたんですか？\nてっきり斧が反応してるのかと……\nびっくりさせないでくださいね？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/366, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)①パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/2, /*description*/"大丈夫、怖くないわ。\nほら、ぎゅー……安心してね。\n……プロデューサーも、します？ ぎゅー♪", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/367, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)②", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/3, /*description*/"さっき変な黒い影を見たんですよ。\nでも人ではなくて、あれはいったい……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/368, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)②ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/4, /*description*/"怖がっていても仕方ないわ。見に行かないと。\nきゃっ ! へび ! ？\n……じゃないわ。コード？ こんなところに？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/369, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)②グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/8, /*description*/"あ……斧が、引き寄せられてる !\nこの先にあるのは、出口か、それとも……\n見に行きましょうか。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/370, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)②パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/3, /*description*/"ぷ、プロデューサーだったの？\n水を汲みに行っていた？ そうですか。\n離れるときは、教えてください。……驚くじゃないですか。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/371, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)③", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/1, /*description*/"「あなたは……この人を……大事にするの……」", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/372, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)③ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/7, /*description*/"「私をここまで運んでくれた彼女を……\n不幸にしたら……許さないわ……」", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/373, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)③グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/2, /*description*/"「あなたは……違うのね、あの人とは……\n世界には……優しさもあるんだわ……。\nあなたたちと彼女の行く末は……どこまでも、輝いて……」", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/374, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(来ちゃった山頂)③パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/6, /*description*/"「埋もれていた私を引きずり出したの……\n責任……取ってもらわないと……光の、もとへ……」\n……あら？ 私、今何を……？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/375, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/4, /*description*/"何か地響きが聞こえませんか？\n斧じゃないし、愛海ちゃんでもない……\n肩車した黒服の大群？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/376, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/8, /*description*/"はぁっ…… !\n気合で吹き飛ばします ! 斧、力を貸して !\n寺生まれ？ いえ、ごく一般的な家ですけど……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/377, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/6, /*description*/"もうここまで？ いいえ、諦めたくないわ…… !\nふたりだけでも、私が守るっ !\n……？ 素通り、していく？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/378, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/2, /*description*/"アライグマの構えは解いて、愛海ちゃん。\nこういうときは、同化するのよ、地面と。\n低く伏せて……静かに、ふぅ。行ったわね。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/379, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/8, /*description*/"ねぇプロデューサー。\nこの山から無事に下りられたら……あの人たちから\n逃げ切れたら、伝えたいことがあるんです。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/380, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/7, /*description*/"今は言いません。\n気になるなら、絶対、逃げ切ってくださいね。\nひとりだけおとりなんて、認めませんよ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/381, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/2, /*description*/"もちろん、愛海ちゃんにも言いたいことはありますよ。\nやだ、怖がらないで。\n良いことも悪いことも、両方ですからね♪", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/382, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/5, /*description*/"フラグ……？\nふふ、たしかに、そういうこともあるかも。\nでもあなたなら、そんなフラグ、へし折ってくれるでしょう？\n", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/383, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/6, /*description*/"映画やドラマなら、逃げ切れた3人はハッピーエンド。\n斧の無念も無事に昇華して、\n明日への第一歩を踏み出すエピローグが待っているわ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/384, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/7, /*description*/"夢オチも、なくはないですね。\n3人で同じ夢を見て、私たちだけが覚えている真夏の逃避行。\nそれはそれで面白いですから。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/385, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/8, /*description*/"そのエピローグは、新たなプロローグの始まり。\nもちろん、今度こそ暑い夏の、\nたのしーいバカンスが始まるはずです♪", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
