INSERT INTO `meetup_memory_data` VALUES (/*id*/397, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"こんなに夕日って眩しかったかしら？\nでも今までで一番、暖かい光……。\nスマホがあれば写真を撮れたのに、残念ね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/398, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"あぁ、安心したら……足が。\nごめんなさい、少しだけ座らせてくれる？\n……顔は、緩みきっているから、見ちゃダメです。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/399, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"逃げ切れたあとでも、やはり気になるわ。\nはぐれたスタッフ……現れた黒服……\n電源の切れたプロデューサーのスマホ……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/400, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"狙いすまされているのよ、何から何まで。\nこれは、でも、あなたが何も知らないのなら……\n答え合わせは、そろそろできるのかしら。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/401, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"ただひとつ不可解なのは、あの斧。\n愛海ちゃんが言うように、本当に、女性が……？\n愛海ちゃん、興奮しすぎないように、ね？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/402, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"プロデューサーは、何か知っていますか？\nいえ、答えられないのならそのままで。大人のしがらみもありますから。\nあなたはずっと私たちの味方だった。それだけが、今の事実です。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/403, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"ふふ、愛海ちゃんがじっと見てますよ？\n労ってあげてください、一番頑張ったんだから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/404, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"そうやってプロデューサーとおしとやかにしてると、\n可愛い女の子なのだけれど……。\nでも、あの元気も愛海ちゃんらしさ、愛海ちゃんの良さですから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/405, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"愛海ちゃんの明るさには、私もたくさん助けられたわ。\nプロデューサーも、ですよね？\nじゃあ、ふたりでよしよししてあげましょう♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/406, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"私も、頑張った……？\nそんな、私はするべきことを……\nよしよしは、子ども扱いすぎますよ。もう……ふふっ♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/407, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"プロデューサー、斧を見なかった？\nおかしいわね、さっきまで手にあったのよ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/408, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"ついてこなかったのなら、それが斧の意志ということね。\n少しだけ、寂しいかもしれません。\n斧がここまで、助けてくれたわけですから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/409, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"怨念がこもった斧だなんて愛海ちゃんは言っていたけれど、\n私にはそれほど悪いものには感じなかったんです。\n寂しいけれど、きっと優しい女性だったんだわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/410, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"大団円を迎えられたなら、\nきっとあの斧も、満足して帰ったのね。\n一緒にいたい人のもとへ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/411, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"スリリングなバカンスは、一生分過ごしたわ。\n私はプロデューサーとは、もっと……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/412, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"のんびり過ごしたいのよね。\nお仕事で忙しくしている分、\n喧騒を離れて英気を養いたいじゃないですか、大人だもの。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/413, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"ドキドキ、したいわ。\n恐怖と隣り合わせの刺激じゃなくて、\nもっと熱く、もっと身を焦がす、そんな夜を……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/414, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"まぁ、これからいくらでも機会があるから、いいのかしら。\nあなたが私のプロデューサーである限り、\nこれからも夏の時間はくれるのよね、もちろん♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/415, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/1, /*description*/"海に始まり、海に終わった、ひと夏の逃避行。\n愛海ちゃんがいてくれたのは、ある意味救いだったわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/416, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"大人として、ふたりとも頑張れたでしょう？\n守るべきものがあると強くなれた。\n子どもがいる夫婦って、こういう気分なのかしら。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/417, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"私ひとりなら、甘えて、寄りかかって、プロデューサーの\n負担になっていたかもしれないから。\nかっこいい私に、惚れ直してくれたでしょう？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/418, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"いなかったら、何もかもを投げ出して、\nそれこそすべてから逃げ出していたかもしれないの。\nあなたさえいれば、それでいい……って。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/419, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"早起きですね。\n……私は、あなたと\n会える気がしたので。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/420, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)②", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"お疲れのあなたを癒します。\nあなたが、逃走中の私の\n支えだったんですよ。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/421, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)③", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"日常へ帰る前に少しだけ、\n浸らせてくださいね。\n夢みたいな日々だったわ♪", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/422, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/7, /*description*/"ドッキリだとわかって、ほっとしたような、\nあまりの大掛かりさにどっと疲れたような……\nでも、ようやく帰れるんだわ。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/423, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサー、本当にドッキリだって知らなかったの？\n予想くらいはしてたんじゃないかしら？ だってあなたには、\nこちらの予想を超えた行動力があったもの。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/424, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"ただ、帰る前に……あなたにお疲れさまの癒しを。\nこんな朝早く目が覚めてしまうくらいだもの、\nまだ緊張が抜けていないんでしょう？ わかりますよ。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/425, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"非日常を過ごしたあとだと、\n日常がどれだけ尊いものか実感しますよね。\nあなたといられる日常へ帰れる奇跡に、感謝を。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/426, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"はい、膝へどうぞ。\nまだみんなが起きるまで時間があるから、\nプロデューサーも、もうひと眠りしてください。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/427, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/8, /*description*/"こんなこともあるかもって、さっきホテルで汗を流して、\n肌のケアもしたんです。逃げている間はあまりできなかったから……。\nきっと枕として、すべすべのもちもちですよ。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/428, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"あら、すぐ寝てしまったわ。\n穏やかな寝顔……ふふ、これは、\n私だけの頑張ったご褒美ね。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/429, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"私も一緒に寝るのは……難しいですよね。\nじゃあ、交代でお願いできます？\n私は、腕枕でお願いしようかしら♪", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/430, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"あら、疲れが顔に出ていますよ。\nここまで休みなく、本当に頑張ってくれていましたから。\nでは失礼して……、私が癒してさしあげます♪", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/431, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"きちんと水分補給はしていますか？\nこの先まだまだ長いですからね、油断せず、\n休めるときに、私と一緒に休養をとりましょう。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/432, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"ふぅ……あ、ごめんなさい。\nプロデューサーのそばにいると、気が抜けてしまって。\n少しだけ……甘えても、いいですか？", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/433, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"ゴールが見えない逃走は、こうも疲弊するんですね。\n独りだったら、耐えられなかったかもしれません。\nよかった……。プロデューサーがいてくれて。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/434, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/10, /*description*/"絶対に逃げ切れる、今はそんな気持ちしかありませんよ。\nだってプロデューサーが、そういう目をしていますから。\nあなたが信じているのだから、絶対、大丈夫なんです。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/435, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/18, /*description*/"ドキドキしてるの、伝わってます？\nあるのは、恐怖じゃないですよ。ともに、苦難を乗り越える興奮……\nそして、あなたと誰よりも深い関係になれるかもという期待です。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/436, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"チャンスタイムセリフ", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"プロデューサーは、私の後ろに……いえ、私たちは\nアイドルとプロデューサー。どこまでも一緒だと誓った仲。\nならば、あなたと力を合わせて、どんな困難も乗り越えましょう。", /*memory_category_type*/5, /*memory_category_value*/11, /*talk_chara_name*/"柳清良", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/437, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"水着美女のお山は……？\n「うはうは☆ ヘブン」は？\n出てきてプリーズ !", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/438, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"うひひっ、この廃ビル、\n薄暗くて狭くて……\nどさくさに紛れちゃえ……♪", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/439, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"逃げて逃げて逃げきって、\n絶対たどり着くんだ !\n海に……解き放たれた海に !", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/440, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)④", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"ねえ、プロデューサー、\n亡霊さんのお山にもね、\nつまってると思うんだ。……ロマンが !", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/441, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑤", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"えっと……プロデューサーの\n裾を掴んでるのは……。\nべ、べつに怖がってないよ？", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/442, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑥", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"鬼に金棒 ! 清良さんに斧 !\nあ、いや、その、鬼って\nいうのは例えでですね……。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/443, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑦", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"亡霊さんも黒服たちも\n災難だよね。相手が清良さん\nじゃなければ……ぶるるっ。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/444, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑧", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"亡霊さんは未練があるんだ。\nわかるよ。あたしにもまだ、\n眺めるだけのお山たちが……うっうっ。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/445, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"このビル、建設途中のまま\nずっと放っておかれてたっていうけど……\n何があったんだろ？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/446, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"大人の事情……？\n大人って、理不尽ばっかりで大変そう……。ま、大人のことは、\n大人なプロデューサーと清良さんに任せよっと。よろしくね♪", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/447, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"や、やっぱり、怨念が……？\nグッドなお山でむよんむよんと迫りくる美女の怨念が !\n……「むよんむよん」より「ほよんほよん」がよかった？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/448, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"何があっても、プロデューサーがいるから大丈夫だよねっ。\n暗くてじめじめした場所での探険も、逃走劇も、\nわきわき……じゃなくて、わくわくするっ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/449, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"はっ……どこからか聞こえてくる、謎の声……？\nあつみちゃんを呼んでいる……？\n聞き耳、聞き耳……。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/450, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"「お昼休憩は12時から交替で……」\nん？\nこれって何かの業務連絡？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/451, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"ひっ…… ! 寂しそうな女の人の声…… ! ？\nわかる……あたしにはわかる……この人はきっと……\n素敵なお山の持ち主だよ ! わきわきセンサーが大反応 !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/452, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"なになに、うんうん……え？ 「うらめしや」？\nダメだよ、欲求不満はためこんじゃ !\n欲望のままに、てっぺん目指さなきゃ ! よ～し、愛海に任せて !\n", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/453, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"清良さんには、立派な斧 !\nじゃあ、あたしの武器は……？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/454, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あたしの武器……そうだっ、\n美少女あつみちゃんでいこっ♪\nの・ぼ・ら・せ・て？ きゅるん☆", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/455, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"あたしには、この生身の両手がある !\nやわらか～い夢もふよんふゆんと掴み取る、\n疾風の右手、神速の左手が !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/456, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あたしの最大の武器、それは、踏まれても蹴られても立ちあがる\n不屈の闘志、そして欲望パワーなのですっ !\n3人で生き残るよ ! プロデューサーも一緒に……それがあたしの欲望 !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/457, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"黒服サングラスの人たちも、斧に未練を残した亡霊さん\nなのかなぁ……ああっ ! 見て、あの亡霊さん……\n普通の亡霊と何かが違うよ ! ？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/458, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"グラサンに気を取られてたけど、足があるよ ! !\nあれっ、他の亡霊さんたちもよく見たら……。\n……あっ。そういうロケ……えっと、あつみ、何も見てないよ？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/459, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"そう、お山 ! あの亡霊さんには、かわいいお山が見えるよ !\nお山だってわかれば、もう怖くないっ。\nだってあたしはお山登りのエキスパートだからね !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/460, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"あの亡霊さん……水着？ だったら、極上のお山めがけて……\nいっただっきまーす♪ あああ～……極楽っ……ごくら……って、\nなんでそっちが逃げるの～ ! 待～て～ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/461, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"はぁ……はぁ……。\n右手からも左手からも追っ手の影が……。\nどこへ行けばいいのぉぉぉ ! ？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/462, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"もうだめ……へたり。\n右手も左手も、お山成分切れです……。\nああ……。ふわ……ふわ……。わき……わき……。はっ、幻覚？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/463, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"どこへ行ってもいい……？ 最近の愛海はいい子だったから、\nどんなお山に登ってくれても……？ やったー !\nえ、言ってない？ 言って ! あたしのために言ったことにして !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/464, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"ここは任せてふたりで先に……？ ぷっ、あははっ、\nあたしたちの前では、そんなにかっこつけなくていいよ、プロデューサー !\n一緒に逃げよっ ! この手は離さないから ! がしぃっ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/465, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"フッ。戦いのなかで、マウンテン戦士・愛海の魂は\n覚醒したみたいだね。\n今なら、敵の動きも止まって見える…… !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/466, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"……と思ったけど気のせいでした !\nぎゃああぁぁ、逃げろーっ !\nプロデューサーも ! 早く ! ぐいっ、ぐぐいっ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/467, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"いくよプロデューサー ! フォーメーションＷ !\n右と左からはさみこんで……\nいや、右も左も、いまのあたしはどっちも譲れない ! !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/468, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あっ……プロデューサーを思いっきり掴んじゃった…… !\nあの……プロデューサーも、もしかしてドキドキしてた？\nその、手のひらがね……これ、あたしの汗かな？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/469, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"夜の遊園地って、\nロマンチックで\n甘えやすくて……グッド !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/470, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"どこかから声が ! ？\n「遊び……たい……」。\nならあたしについてきて !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/471, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/4, /*description*/"いつまでも捕獲される側じゃ\nないっ ! 掴む力なら\nこっちが上 ! 反撃開始 !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/472, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)④", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"3人で手をつなげば……\n湧き上がる無敵感は\n八甲田連峰級っ !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/473, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)⑤", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/7, /*description*/"プロデューサーと手を繋いで\n夜の遊園地を逃走中……。\n映画のワンシーンみたい。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/474, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)⑥", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"ああ、清良さんの水着姿っ !\nふくよかな尾根のライン\n……グッド !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/475, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)⑦", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"清良さんのお山に\n最初に指をかけるのは、\n黒服たちなんかじゃない ! このあたしだーっ !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/476, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)⑧", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"「そっちを探せ」だって !\nまずいよ、静かに息を\nひそめて……はくちゅんっ。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/477, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/4, /*description*/"ちらっ、ちらっ。黒服の追っ手が隠れてるかも……。\nジェットコースターの入り口？\nそれともあっちのコーヒーカップ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/478, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"ところでプロデューサーはさっきから何見てるの？\n清良さん？\n清良さんは強いから大丈夫だよ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/479, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"そうだ、あのメリーゴーランドに乗ればいいよ !\nあのお人形さんたちみたいに、\nふたり乗りして、じっとしてればバレないよ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/480, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"いっそ、こっちから襲っちゃうっ？\nプロデューサー、せーので一緒に飛びだそっ !\nあははっ ! 今のあたしたち、とっても無敵っ ! !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/481, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"夏だけど、夜風が吹き始めたら寒くなるのかな？\nこんなときは……？ そう !\n肌と肌を寄せ合って、温め合うんだよ ! ……だめ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/482, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"なんの ! 愛海流イメージトレーニング !\nあたしはいま包まれてる……。ふかふかで……やわらかくて……\nあったかいものに…… ! うひ……うひひひ……♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/483, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/0, /*description*/"えっ、上着貸してくれるの？ ありがと～♪\nくんくん……プロデューサーの匂いがする……。\nあたし、動物みたい？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/484, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"上着と、あったかい飲み物と……まだまだあるの？\nあのね、プロデューサーがあたしのこと心配してくれる、\nその気持ちがあったかいんだよ。だから大丈夫、ぜんぜん平気っ♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/485, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"あれっ、灯りが見えるよ……？\nあの売店、開いてる ! ？\nよしっ、くたくたの体にエネルギーを補給しよう !\n", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/486, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"ふわふわの風船をゲット♪\n食べられない？ いいんだよ、これがあたしの補給だもん♪\nふわふわ……ぷにぷに…………ふおおおおお ! ! きたーー ! !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/487, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"補給といえばもちろん、お・や・ま・成分、だよねっ♪\n売店のおねえさ～～ん ! がばーーっ !\nわわっ、プロデューサーに捕まった ! 離してぇ～、ぐすん。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/488, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"チュロスとドリンクと……あっ、この抱きついてる\nぬいぐるみ、可愛いねっ ! え？ あたしとちょっと似てる？\n抱きつくポーズが？ それとも……可愛いから？ えへへ♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/489, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"ぎゃー ! きたーっ !\nあっちの陰から、黒服の集団がばたばたと…… !\nひぃ～っ、プロデューサー、た～す～け～て～ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/490, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"えっ、今回だけは見逃してくれるの？ 今回だけ……なるほど。\nプロデューサーでもあの人たちには敵わないなんて……。\nうわ～ん、怖かったよ～っ。ひしぃっ♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/491, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"あたしの秘められし力…… ! 握力覚醒 ! ほああぁぁぁぁっ ! !\n「よくぞ倒した……。しかし覚えておけ、\n第2、第3の私が必ず現れる…… ! 」？ えっ、い、いやだ～っ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/492, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"す、すごい、プロデューサー、武術の達人かも ! ？\nありがとう、今ちょっと本気だったよね。\nあたしのピンチに、プロデューサーが本気になって……覚醒した ! ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/493, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/7, /*description*/"あっ、観覧車だよ !\n乗っても大丈夫そう……？\nこれなら中に隠れられるよ ! えっと、ドアを開けて、と……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/494, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"わっ、黒服がぞろぞろ出てきた～っ !\nまさか、この中で待ち伏せしてたの ! ？\n回れ右 ! 逃げ足はいつも事務所で鍛えてるっ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/495, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/0, /*description*/"遊園地に来たときはね、みんなで絶対乗るんだよっ !\n……あれっ？ ふたりで乗るって……今まであったっけ？\nこ、こういうのって……な、なんか……指が緊張する……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/496, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)②パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"プロデューサー、はいっ、ここ。あたしの横に座って !\n目を離すといなくなっちゃうもん。\nだめだよ、手だって離さないのっ。ぎゅっ♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/497, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"わぁっ、パレードが来たよ ! すごい……キラキラだね…… !\nいまなら追っ手もいないし……。\nねっ、ちょっとだけ立ち止まって、眺めていっても大丈夫だよねっ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/498, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"トロッコの上や周りでくるくる踊ってるのは……。\nみんな黒服だー !\nぎゃー ! こっちきたっ ! 逃げろ～っ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/499, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"音と光に包まれて、なんだか別の世界にいるみたい……。\nもうしばらくここにいよっ♪\n現実からエスケープ ! っていうのも、たまにはいいでしょ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/500, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)③パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"じ～っ。……えへへ、\nパレードを見てる、プロデューサーを見てたんだ♪\nプロデューサーの瞳の中も、キラキラしてるなあって !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
