INSERT INTO `meetup_memory_data` VALUES (/*id*/506, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(来ちゃった山頂)⑥", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"清良さんにも、怖いものって\nあるのかな？ 後ろから\n脅かし……たら怒るよね。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
