INSERT INTO `meetup_memory_data` VALUES (/*id*/508, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(来ちゃった山頂)⑧", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"雲ってどんな感触なんだろ。\nきっととっても……\nグッドな……ふひひ……♪", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/509, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)①", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサー !\nいろんな場所で、黒服から逃げて、逃げて、逃げまくり……\nあたしたちはいま、お山にいるよ ! !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/510, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)①ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/4, /*description*/"そうやって言い聞かせないとやってられないよ……。\nああー、柔らかくないよー、ごつごつしてるよー。\nこんなのぜんぜんグッドじゃないよぉ～ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/511, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)①グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/0, /*description*/"あたしのいるところが、すでにお山 !\nあたしくらいになればお山のほうからやってくる ! むしろお山があたし !\nえ？ もう休憩したほうがいい……？", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/512, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)①パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"いつのまにか、ほら ! お山のてっぺんだよ !\nけっこう登れちゃうもんだよね。\nお山アイドルといえば棟方愛海 ! 今後もみなさんよろしくでーす !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/513, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)②", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"ぜー……はー……。\nも、もう登れないよぉ～。足が限界です……。\nうぅ、倒れるあつみを、誰か柔らか～く受け止めてぇ…… !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/514, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)②ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"はうあっ ! ？ め、目の前に隆々たる清良山がっ…… !\nもう登れないと言ったけど、あれは嘘っ !\n登るよ ! いまやる ! すぐやる ! 力がわきわき湧いてきたーっ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/515, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)②グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"プロデューサー、おんぶして～。\nいいでしょ、いいよねっ。えっ、ホントにいいの ! ？\nそれじゃ……ぎゅ。肩、あったか～い。ほっぺすりすり♪", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/516, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)②パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"自分の足で登るのがアイドル……とか言いながら、\nちゃんと手をひっぱってくれるんだよね、プロデューサーは♪\nうん、なんか疲れは吹っ飛んだ ! 歩こうっ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/517, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)③", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/7, /*description*/"うっうっ……。ひたすら歩きつづけてると、\n今までの長い道のりを思い出して……。\nうっうっうっうっ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/518, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)③ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"アイドルになれば、お山に登り放題っ♪\nなんて思ってた頃もありました。それが……まさかの勝率の乏しさ……。\nあたしの右手と左手がかわいそう……うっうっうっうっ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/519, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)③グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"なんのっ ! 踏まれても蹴られても、いま目の前のお山のために !\nんぬぁ～～っ ! あっ、どこかで水着お山美女も笑ってる気配♪\nよーし、もっと !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/520, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(来ちゃった山頂)③パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"毎日、全力でチャレンジして、追って追われて、笑って笑われてっ♪\nあたしがあたしを全開にして、走っていける道だったよ。\nさ～て、しんみりはおしまい ! 歩こうっ ! 逃げようっ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/521, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"グオォォォォン……？ この声って……で、出たっ !\nこんなとこにも追っ手が ! いや、オバケ ! ？ モンスター ! ？\nううん……肩車して走ってくる黒服だっ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/522, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"ひぃ～っ、ごめんなさいごめんなさい、\nもうお山には立ち入りません～っ。\nお山登りは1日1回……いや2回……3回くらいで我慢しますから～っ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/523, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/4, /*description*/"あたしがみんなを守るよっ !\nお山にいるあたしは無敵 !\n必殺 ! アライグマの構え ! シャー ! クルルルルル !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/524, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)①パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"あっ、プロデューサー…… !\n清良さんも、さって動いて……ふたりであたしを背中の後ろに……。\nえへへ……あたし……守られてる…… !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/525, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/1, /*description*/"ふぅ……♪ いい眺めだねっ。空気も美味し～っ。\nこんなに胸がすっきり晴れ晴れしてるのって、\nひさしぶりかも……。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/526, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/7, /*description*/"よし、叫ぼうっ !\nあたしはー ! おやまがー ! ! すきだー ! ! !\n……すきだー……すきだー……すきだー……。……セルフこだま。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/527, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"あたし、何から逃げてるんだろう？ ……抑圧から？\nこうしなきゃとか、ああしなきゃとか、アイドルだからとか、\nそんなのどうでもいいんだって思ったよ。あたしはあたしだもん !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/528, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"逃げた先になんにもなくたってさ、あたしは独りぼっちじゃないよ。\n人はみんなで大きな輪になってて、あたしもそこにつながってるって\n感じられるようになったから。いまも、ほらっ。隣に……ねっ♪", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/529, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/3, /*description*/"あ、あれっ？ 誰もいない？\nもしかして……あたしはぐれちゃった ! ？\n清良さ～ん ! プロデューサーっ ! !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/530, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③ノーマル", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/4, /*description*/"き、消えちゃった ! ？\nうそっ。どこ、どこっ？\nいじわるしないでよぉ～っ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/531, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/7, /*description*/"ほっ。いた、よかったぁ……。\nそれじゃあ早速……がしぃっ !\nひっついたら離れないの ! あたしの握力をなめないようにっ !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
