INSERT INTO `meetup_memory_data` VALUES (/*id*/537, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(山越えて海)⑤", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"ゆっくり歩こっか、\nプロデューサー。夕日を見て\n波の音を聞きながら♪", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
