INSERT INTO `meetup_memory_data` VALUES (/*id*/541, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)①", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"波打ち際を歩いて……ぴちぴち、ちゃぷちゃぷ♪\nえへへ、寄せたり引いたりする波って、いい感触♪\nわっ ! いきなりおっきな波がきたよっ ! ？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/542, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)①ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサー、ずぶ濡れ～。あはははっ !\n笑ってたら気分変わっちゃった。\n帰って乾かそっ ! 一緒に♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/543, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"プロデューサーの首に腕を回して……とうっ ! 掴まりっ !\n……よしっ。あつみちゃん回避成功♪\nいつもありがと、プロデューサー ! 助かってるよっ♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/544, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"わひゃっ ! つ、冷たい～ !\nうぅ、プロデューサーにも……えいえいっ !\nおそろいのずぶ濡れにして、帰れなくしちゃうもんね♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/545, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/4, /*description*/"ねぇ、プロデューサー、\nさっきからずっと、清良さんとお話してるね？\n…………いいなぁ。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/546, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"あたしも清良さんとお喋りしたーい !\nはっ。そうだ……何かいたずらすれば……。\nむこうからすぐに飛んできてくれて……うひひ、よしっ……♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/547, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"プロデューサーは、みんなのプロデューサーだもんね。\nいろんな人とたくさんおしゃべりしなきゃっていうのはわかるよ。\nでも……あつみちゃんにも、いっぱい構って？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/548, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"うんうんっ、清良さん美人だもんねっ。お山も素敵だし……。\nでも、あたしとプロデューサーは登山仲間なんだよ？\n寂しがりやの仲間を置いて、登山しちゃだめだからねっ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/549, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)③", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"もう追っ手は来ないね !\n長い逃避行の果て、あたしたちはついに逃げきったんだ !\nそれじゃあ次は……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/550, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"お仕事……？ 撮影がまだあるの……？\nそう……すぐじゃなきゃだめ？\nもうちょっと……海にたくさんあるお山を、満喫したいの……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/551, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"実はプロデューサーが追っ手のラスボスってオチ\nだったりして ! なんというどんでん返し…… !\nえへへ、じゃあ追いかけて……ほらっ ! つかまえてみて♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/552, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"がばっ ! プロデューサー、つかまえたーっ♪\n油断大敵、だよ♪ あたしのプロデューサーでしょ？\n無防備なお山してると、登っちゃうからね !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/553, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)①", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/1, /*description*/"あ、あのねっ、プロデューサー。……そ、そのっ……。\n……どうしよう、伝えるだけなのに……すっごくドキドキしてる……。\nでも今勇気を出さなきゃ、きっと言えなくなっちゃう…… !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/554, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)①ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"プロデューサー、ほっぺにパンくずついてるよ……？\nえっとね……実は、遊園地からずっと……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/555, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"んー……でも、今はないしょっ♪\nえ？ どうしても聞きたい？\nじゃあ、あたしが大人になったら言おっかな、約束♪\n", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/556, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"目を閉じて？ 大丈夫、わきわきしないから♪\nぎゅ～～っ。……えへへ。伝わったよねっ？\nじゃ、じゃあちょっと海で遊んでくるっ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/557, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"ねえ、プロデューサー。\nあたしと同じ手の形、作ってくれる？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/558, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"ためらわない ! おのれの欲望に素直に !\nほら、こうだよ、こうっ。\n指をもっと曲げて……わきわきの極意、一緒に掴むの !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/559, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"上手上手♪ さっすがあたしのプロデューサー !\nいつもあたしのこと見てるもんねっ。\nじゃ、次は、指の動きをこう……せーの、いち、にっ♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/560, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"指と指をからめて……ぎゅってつなぐの。\nあったかいでしょっ。仲良しのつなぎ方なんだって。\nだから、初めてはプロデューサーとやってみたいなって♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/561, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)③", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/1, /*description*/"ふ～～っ。\n逃げて逃げて逃げまくって、\nすっごく楽しかったね、プロデューサー !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/562, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"やっぱり、落ち込んじゃったり辛いときに逃げるのは、\n悪いことじゃないんだよ。逃げればどんどん元気が出るっ。\nだって自分を丸出しにして、ありのままの自分に返れるんだもんっ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/563, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"元気がわきわきみなぎったら、くるっと回って、次は追っかけようっ !\nああ、次なるお山が呼んでいる…… !\n大好きにまっしぐら ! あたしの毎日、とってもグッド !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/564, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"手をひいて、一緒に走ってくれる人がいる。\nそれだけで、あたしはいつも頑張れるのっ。\nこれからもときどき、ふたりで……ないしょのエスケープしようねっ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/565, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/5, /*description*/"記念だし、叫んどこっ。\nお～っきな～、うみ～～っ !\nはぁぁ……すっきり☆", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/566, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(日常の朝)②", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/6, /*description*/"見ちゃった。清良さんに\n膝枕されてるの……。愛海は\nそっと立ち去るのであった !", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/567, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(日常の朝)③", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/5, /*description*/"な、なんか顔が熱い……？\n昨日みたいに、普通に\n笑えてるかな……えへへ。", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/568, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/7, /*description*/"実は、プロデューサーが寝てる間にですね……\nあたし、棟方愛海は……うひひ……♪", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/569, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/2, /*description*/"大丈夫……♪ なにもしてないよ……♪\nうひひ……なにも……♪", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/570, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/6, /*description*/"清良さんと、美味しいもの食べちゃった♪\nアヒージョにマリネに、サルサ……？\nえっと、なんだったっけ……女の子同士のヒミツで～す♪", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/571, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/5, /*description*/"寝顔、じっと見てたんだ。可愛かったな……♪\n今回、いちばん頑張ったのはプロデューサーだもんね。\nあたし、ちゃんと知ってるよ。お疲れさま、プロデューサー。よしよし♪", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/572, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/1, /*description*/"もうすぐ、帰っちゃうんだよね……？\nだったら、プロデューサーにわがまま、言っていいかな……？\nあたしがずっと言いたくて、言えなかったこと……。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/573, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサー……だ、だ、だ……だーいぶっ♪\n隙ありっ ! 健気可愛いニューモードのあつみに騙されたね ! ？\nあっ、ちょっ、そんな簡単に止めないで……っ !", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/574, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/7, /*description*/"寂しくなったら、いつでもぎゅってさせて……？\nこんなこと、プロデューサーにしか頼めないんだもん。\nあ、清良さんには内緒で ! お願いだよ？", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/575, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_oyamatope", /*pose_id*/2, /*face_id*/5, /*description*/"いつも一緒にいてくれて、ありがとーっ ! ! ! 特大級のこの言葉、\nずっと胸にしまっておいてね。あたしも、言ったことを覚えてる。\nずっとずーっと、何年先も、あたしたちのおそろいの宝物♪", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/576, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"ねぇ、そろそろじゃない？ ご・ほ・う・び・ターイム !\nやわらか～い、アレとかソレとか……うひひ……♪\nじゃ、まずはあたしから。コレで指の幸せを感じて、プロデューサー !", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/577, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"これ、あつみからのプレゼント♪\nプロデューサーに元気になってほしくて、とっておきを選んだよ。 \nだから、あつみにも……だめ？ 必殺・上目づかいっ。きゅるん☆", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/578, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"プロデューサーも、がまんなんてしちゃだめ !\n正直にっ ! 貪欲にっ ! 自分自身をフルオープンにっ !\n構えたら、さあご一緒に……いただきま～すっ ! !", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/579, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"も、もう歩けないよぉ……。プロデューサー、おんぶして？\nと見せかけて、後ろから～……隙あり♪\nな、な～んちゃって ! ごめ゛ん゛な゛ざ～い゛ ! えへへ♪", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/580, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/10, /*description*/"プロデューサーがとなりにいると、勇気がわきわき湧いてくるよっ !\nさあ、行こう ! あのお山のむこうにある天国へ !\nこの手と ! その手を ! がしっとつないで !", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/581, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/18, /*description*/"寂しがりなところも、プロデューサーには見せちゃったけど……。\nでも、やっぱりあたしはこうでなきゃ ! 何があっても、何度だって\n手をのばすもんね ! お山 ! お山 ! ! やわらか～い、お山っ ! ! !", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/582, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"チャンスタイムセリフ", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"ねぇ、あたし、もっと欲望全開にしていいっ？\nプロデューサーのこと、もっとぎゅ～って掴むんだ !\nあたしを置いて、どこにも行かないように♪ うひひっ♪", /*memory_category_type*/5, /*memory_category_value*/11, /*talk_chara_name*/"棟方愛海", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/583, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)①", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"光と闇が司る時を知り、\n山と海の恵みを享受し生きる\n……人は、ちっぽけね。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/584, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)②", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"釣りは、海との駆け引き。\n辛抱強く待つ者が、成果を\n……得られないときもある。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/585, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)③", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"海の機嫌は……良好。\n鳥の飛び方を見ていれば、\n自然とわかるものよ。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/586, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)④", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"……空腹にも耐えましょう。\n軽食は持参しているけれど、\nどうせなら……刺身がいい。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/587, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)⑤", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/7, /*description*/"最初は止められたわ。\nけれどリスクを伴わなければ\n求めるものは、得られない。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/588, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)⑥", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"「愛」は、理解したつもり\nだった……給仕だけでなく、\n料理にも応用できるとはね。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/589, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)⑦", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"……「アンコウ」。\nしりとりよ、続いて。\n……「浅瀬」からが良い？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/590, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(まずは海へ)⑧", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"貴方がいてくれてよかった。\n早朝の船出がひとりだと、\n……寂しいもの。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/591, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)①", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"まずは海へ出ましょう……。\n命が始まる場所には、きっとすべての答えが眠っているわ。\nさあ、船出のときよ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/592, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)①ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"この海……荒れるかもしれない。\n錆止めを持ってきておいて正解ね。\n……冗談よ。持ってきたのは……酔い止め。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/593, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)①グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"船に乗るのは久しぶり……。\n……そう得る機会ではないでしょう。\nよい体験にしましょう、貴方にとっても。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/594, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)①パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"海について解明されていることは、宇宙と同じくほんの僅か。\nある意味、ここもまた宇宙。\n……大丈夫、恐れないで。私がついているわ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/595, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)②", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/7, /*description*/"今頃、時子はどうしているかしら。\n乗り気ではないでしょうけれど、彼女もプロ……。\nきっとやり遂げる……若いのに、しっかりしているから。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/596, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)②ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"ところで……あれは趣味？ それとも生き様？\n跪いて、鞭で打たれて……豚の鳴き真似をするのが。\n……毎回、驚きと戸惑いが生じる。私も、躾をすべきかと。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/597, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)②グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"時子は厳しい人だけれど、\nそれでもアイドル活動を楽しんでいることは事実。\n……今回の仕事に関しては……解析不可能ね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/598, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)②パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"彼女と過ごしていると、歳の差を感じないわ。\nいえ……事務所の子たち、みんなそう。年の功など霞んでしまう。\n誰もが星のように、いっとう眩しいのだから。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/599, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)③", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"…………。\n……眠ってはいないわ。\nただ、この穏やかな揺れに、身を任せていただけ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/600, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)③ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"………………ええ、少し、酔ったの。\n横になって……楽な姿勢を……。\n膝を貸してくれるの……？ ……ありがとう。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/601, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)③グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"…… ! 波が高くなってきたかしら……。\nプロデューサー、私の手を。\nこうしていれば、多少は安定するでしょう。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/602, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(まずは海へ)③パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"っ…… !\n……ありがとう、高波に気づいていたのね。\n貴方はいつも、私を支えてくれる。身も……心さえも。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/603, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)①", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"獲物がかかるまで、気長に待ちましょう。\n……ずっと昔の世界、祖先が生きていたような時代にも、\nこんなふうに緩慢な時が流れていたのかしら……。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/604, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)①ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"もしも、潮に流され知らぬ島に辿り着いても……\n私が星を観るから安心して。\n不安にはさせない。ふたりなのだから。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/605, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)①グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"旅には、懐かしい感覚もある……浸らないようにしていたのに。\nこれが緩やかに過ごすことの、弊害。\nあるいは……貴方と過ごす、特別。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/606, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)①パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"……緩やかな時の過ごし方は、忘れかけていた。\n貴方がいつも、連れ出してくれているから。\n目まぐるしく、忙しない……満ち足りた日々へと。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/607, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)②", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"これは……アタリね。\n予定通りに展開して。釣り上げるわよ。\nさあ、私の隣で、タモを。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/608, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)②ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"……スカね。そういうときもあるわ。\n気を取り直して、新しい仕掛けを用意しましょう。\n顔を上げて。まだ狩り……いえ、漁は始まったばかりよ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/609, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)②グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"タコ……？\n漁のやり方が異なる生き物も、釣れるのね。\n人生はロードマップに縛られない……興味深い。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/610, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)②パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"これは……深海魚かしら。\nアブラボウズ、という名前だったような。\n美味だけれど、食べすぎには要注意の魚らしいわ。試してみる？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/611, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)③", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/4, /*description*/"見て。私たちの観測史上、一番強いアタリよ。\n望むものと対面できるかもしれない。\n……覚悟はいい？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/612, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)③ノーマル", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"……驚いた。岩に引っかかっていただけみたい。\nこれは、地球を釣り上げたと言っても過言ではないわね。\n………………ジョークよ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/613, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)③グッド", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"ふむ……標準程度のマグロかしら。\n今回必要な食材としての要件は十分に満たしている。\nこれが私たちの成果。残すは凱旋と、試作ね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/614, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(まずは海へ)③パーフェクト", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"……大きなマグロね。とても私たちだけではいただけない。\n漁師のみなさんと分け合いましょう。ご近所の猫にも。\n喜びは分かち合うものだと、知ったから。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/615, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)①", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/1, /*description*/"寿司は一日にしてならず。\n……万物に言えるわね。\n輝くために、磨かなければ。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/616, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)②", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"甘い醤油もいいけれど、\n私はやっぱり、わさび醤油。\n追いわさびも忘れない。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/617, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)③", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"通常、つけ場に立つには\n何年もかかる。仕事としての\n好待遇に、感謝しなければ。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/618, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)④", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/0, /*description*/"老舗のアナゴの漬けだれは\n長年継ぎ足しているそうね。\n人の歴史と同様、か……。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/619, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)⑤", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/3, /*description*/"新鮮なネタを、新鮮なうちに\n……つまり、本番用にまた\n海に出る必要が？", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/620, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)⑥", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"ロボットのように精密……\n大将に言われたの。\nそれでは、温もりがないと。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/621, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)⑦", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"少し、手を借りても？\n物理的によ。そう、重ねて。\n……これが、人の温もりね。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/622, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(寿司屋)⑧", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"炙りは時子と学んだわ。\n応用を……火力が強すぎる？\n大将、そう慌てないで。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/623, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)①", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/6, /*description*/"魚の捌き方なら心得ているわ。\n貴方は、観測していて。\n大丈夫よ……これは寿司に至るための、私の仕事。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/624, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)①ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/3, /*description*/"握る際の手数は少なく。一流は5手以下で行うらしいわ。\n数えていて。1、2…………。2手。\n……これは、握れて……いない？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/625, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)①グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"酢飯は温かく、ネタは冷たく。\nこれが合わさったとき、素晴らしいハーモニーとなる。\n職人の腕がもっとも問われる料理……まるで芸術ね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/626, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)①パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/1, /*description*/"……ある程度の料理なら、作れる。\n自然と覚えたの。両親の後ろ姿を見て、真似をして……。\nいつ包丁を持つようになったかは、忘れたわ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/627, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)②", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/4, /*description*/"大将に弟子入りしたときは、付け焼き刃の知識しかなかった。\nもっと、知らなければ。\n職人たちが指先から込める……まごころという魔法を。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/628, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)②ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/1, /*description*/"おにぎりも、握るという点では同じなのに。\n試作の一環として握ってみたら、カチコチになってしまった。\n……想いを込めすぎてもいけないのね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/629, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)②グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/6, /*description*/"日本人だもの、お寿司くらい食べるわ。\nけれど、普通の店ではわさびの辛み、深みが足りない……。\nすり下ろし方にもこだわる必要がある……存外難しい薬味よ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/630, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)②パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"どうして寿司を選んだのかは……そうね。\n記憶にある、お祝いごとの料理は……お寿司だったから。\nわさび抜きの甘い記憶、とでも飾ってみましょうか。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/631, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)③", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/3, /*description*/"貴方も、よく人と食卓を囲むでしょう。\n事務所の子や、仕事相手と。\nひとりで食べるときとは、違う感覚があるのかしら。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/632, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)③ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"誰かととる食事は、普段よりも美味しい。\nよくそう言ったものだけれど……事実ね。\n心因性で、定義が難しい現象……人が織り成す、興味深い事象。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/633, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)③グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/0, /*description*/"私も、誰かと食卓をともにすることが増えたわ。\nほとんどは、貴方と。\n……得難く、手放し難いひとときね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/634, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(寿司屋)③パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"……実を言うと、私もよ。\nうっかり香辛料をかけすぎてしまうくらいには。\nその場の空気と会話に……感じ入っている。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/635, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)①", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"この間、大将が私にお弁当を持たせてくれたの。\n女将さんが作ったという話だったけれど。\n見習いの私にも作ってくれるなんてね……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/636, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)①ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"お弁当は、楽屋弁当以外にあまり馴染みがない。\nスーパーやコンビニでも買うことも。\n手作り弁当なんて……なおのこと。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/637, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)①グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/0, /*description*/"料理は作りたてでこそ、と思っていたけれど……\nお弁当ならば、好きなときに、好きな場所で食べられる。\n彩りに個性を添えることだって……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/638, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)①パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/5, /*description*/"蓋を開けたときは、驚いた。\nハート型だったわ、ニンジンが。\n……ええ、大将に渡すものと間違えたらしいの。……ふふ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/639, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)②", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/7, /*description*/"ひとまず、握ってみたわ。\n……試食をお願いできる？\n忌憚なき意見を聞かせて。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/640, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)②ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/0, /*description*/"……普通。そう、ありがとう。\n気を遣ってくれないほうが、私と仕事のためになる。\nやはり貴方は、できるプロデューサーね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/641, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)②グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/6, /*description*/"……追加したければ、わさびも用意しているわ。\nもちろん、ちゃんと茎側からすり下ろしたものを。\n風味と辛さを、最大限に引き出せるように。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/642, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)②パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"ネタが少し余っているの。これも食べたい？\nじゃあ……口を開けて。運ぶわ。\nさあ「あーん」を。……親鳥の気分ね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/643, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)③", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/1, /*description*/"この企画の趣旨は、まごころ。\n愛しい人へ伝える、想い……。\n……愛しさ。とは。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/644, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)③ノーマル", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/3, /*description*/"……猫を見たときにこみ上げる、\n撫でたくなるような……気持ち……？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/645, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)③グッド", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/6, /*description*/"好き、とは……また異なる感情？\n「好き」ですら、うまくつかめていない気がする。\n貴方と仕事をするのは好き……だけれど。……違う？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/646, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)③グッド？", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/0, /*description*/"恋人のそれ？\n………………。\n…………………………？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/4, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/647, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(寿司屋)③パーフェクト", /*bg_id*/6741, /*bgm_name*/"bgm_commu_tension", /*pose_id*/1, /*face_id*/2, /*description*/"愛しいとは、離れがたいという意味も含むのかしら。\nそうなのだとしたら……。\nこの感情は…………いえ、なんでもないわ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/648, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"ようやく、本番……。\n大丈夫、緊張はしていない。\nこれは当然……武者震いよ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/649, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"私が学んだ「心」……\n確実に、届けるわ。\n五臓六腑に染み渡らせる。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/650, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"時子の料理を見る機会は\n貴重よ。目に焼きつけて\nおくといい。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/651, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)④", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"これは、振る舞う際の\nおまじない……「おいしく\n食べてね。きゅんきゅん」。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/652, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)⑤", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"あの司会……どうして\nアンコウ頭なのかしら。\nアンコウ鍋……ふむ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/653, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)⑥", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/7, /*description*/"あの大きさでは、鍋には\n入らない……。安堵の表情を\n浮かべられたわ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/654, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)⑦", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"得たものを総動員……\n私が思い描く、最上の愛。\nそれを込めるお寿司は……。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/655, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(調理中♡)⑧", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"刺激と完璧を追求する\nアイドル……あの司会は\n私をそう評したわ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/656, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"試作品は完成させた……けれど、今は本番。\nさらに完成度を上げて、まごころの答えを出さなければ。\n期待していて。まごころ♡愛妻料理を。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/657, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)①ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"トライアンドエラーは何事においても同じ。\n数多の失敗のなかから、輝ける成功が生まれる。\n……散っていったお寿司たちの犠牲は、無駄ではないわ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/658, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)①グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"貴方に試食してもらえたおかげで、改善点も炙り出せた。\n大切なのは、柔軟さね。正しい在り方にこだわる必要はない。\n導きに感謝を、プロデューサー。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/659, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)①パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"結婚……あまり考えたことはないわ。\n想像ができない。私が、誰かほかの人間の半身になるなんて。\nいつかの未来で、そうなっているとしても……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/660, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"時子も、少しは乗り気になっているみたいね。\n彼女……どんな料理を作るのかしら。\n同じ料理人……ではないけれど、やはり、気になる。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/661, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)②ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"……勝負はしないのね。\nいかにスタジオを盛り上げ、かつ愛情に迫れるか……。\n人間味あふれる試みだわ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/662, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)②グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"やはり、豚。\nまるで海と大地ね。異なる視点から、異なる場所から、\n私たちは、まごころを込める……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/663, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)②パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"用心して。また凄まじい火柱を上げるかもしれない。\n防護マスクは貴方の分も用意してもらったわ。\nともに、生き延びましょう。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/664, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/7, /*description*/"食事は刹那の快楽ではなく、喜び。\n……いまいちピンとこない事実。\n今なら、わかるのかもしれない。提供する喜びも。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/665, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)③ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"メイド以外の提供といえば、主に香辛料の共有。\nただし、それに伴う反応は……とても笑顔とは呼べない。\n仕方ないわ。刺激とは、耐えるものだから。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/666, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)③グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"料理を振る舞ったこと自体は、何度か。\nけれど、ここまで深く考えたことはなかった。\n必要なのは……「おいしくなーれ」？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/667, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(調理中♡)③パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"「愛しい貴方」へ向けての手料理よ。\n香辛料はいったん封印しましょう。\n刹那的なものではなく、素材を活かした、心に残る味を……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/668, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"握りには職人の磨かれた腕が必要。\nゆえに敬意を払って、あえて握らずに。\n……私は、ちらし寿司でいくわ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/669, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)①ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"一説によると、まかないとして生まれたらしいわね。\nそこには同僚を労う、まごころがある……。\n今の私に、ぴったりだと思ったの。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/670, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)①グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"ただのお弁当箱では、味気ない。\n包みでも彩りを添えて……。\nこういうところに、個性が出るのね。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/671, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)①パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"ハートを作っているの。心を込めるのだから、必要でしょう。\nこれまでの道程に感謝と、これからの日々に願いを……。\n……まるで、貴方宛てのお弁当ね。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/672, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/7, /*description*/"寿司とは、寿を司るもの。\n貴方の一日に幸いが訪れるように……\nそんな祈りと、心を込めて。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/673, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)②ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"もちろん、新しいお寿司も試作済み。\nバナナ寿司……これは二番煎じだった。\n麻婆寿司……これは、味が濃かったわ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/674, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)②グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"神秘も伝承も、眉唾ものだとは言えない。\n人が無意識に星を仰ぐように、刻まれている……\n日本人の魂には、お寿司の幸が。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/675, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)②パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"握りに心が必要なら、握らない私は……ちらしに、想いを。\n具材のひとつひとつに、「愛情」を込めて……。\n…………日が暮れてしまう？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/676, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"愛情というものは、未だに不明瞭。\nけれど、それが正しいのかもしれない。\nうまく言葉にできないもの……そんな想いを称しているのだから。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/677, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)③ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"長い……長い、戦いだった……。\nこのちらし寿司弁当には、私の知識を総動員したわ。\n完璧な味わいになっているはず……さあ、ご賞味あれ。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/678, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)③グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"言葉にできないのなら、せめて形で。\n包みはピンクにしてみたの。\n……愛を表す撮影セットも、ピンクでしょう？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/679, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(調理中♡)③パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/5, /*description*/"迷走した末に、ようやく納得のいくものが完成した。\n刺身でかたどったハートは、私の心そのもの。\nこれは、貴方に。ええ……「心」を込めたわ。受け取って。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/680, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"共同生活……女子寮で\n学ぶのも手だったわね。\n大丈夫、データはあるわ。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/681, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"本当に一緒に住むのなら、\n夜は、ともに星を仰ぎたい。\n……いつもと、同じ？", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
