INSERT INTO `meetup_memory_data` VALUES (/*id*/683, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡団地)④", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"貴方、靴下が脱ぎっぱなし。\n……なんて。散らかして\nいても、問題はない。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/684, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡団地)⑤", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"掃除の勢いは\nバショウカジキ……？\n褒められて……いる……？", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/685, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡団地)⑥", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"住居を同じくして、\n何が大きく変わるのか……。\n今はまだ、不明瞭ね。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
