INSERT INTO `meetup_memory_data` VALUES (/*id*/688, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"ここでVTRの撮影をするのね。\n私は新婚の女性で、「愛しい貴方」と暮らしている。\n……ロールプレイはつまり、演技。エミュレートに問題ないわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/689, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)①ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"料理と清掃。私に備わっている機能は……\n……こうでは、ない？\n少し待って。インプットし直すわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/690, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)①グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"貴方、今度のお休みは食器を見に行きたいわ。\n……この間、うっかり割ってしまったの。\n大丈夫、貴方のお気に入りのものではないから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/691, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)①パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"カーテンを替えてみたわ。遮光よ。\nこれで私も貴方も、存分に昼寝ができる……。\nふふ、すぐにでも微睡んでみる？ 膝なら貸すわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/692, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"掃除なら任せて。\nあら……？ これは機材のコードかしら。\n安心していい、場所は突き止めるから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/693, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)②ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"くっ……まさか躓くとは。\n少し、緩んでいる……？\nこの場所の、柔らかな雰囲気が……そうさせているのかしら。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/694, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)②グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"そこと、そこと、そこ。\nこれで完了ね。もう自由に歩いて大丈夫よ。\n……ダメね、メイドの域を脱せていない……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/695, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)②パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"スタッフが片づけてくれたみたいね。\nでは、再開しましょうか。\n私たちは、夫婦。誰にも邪魔されない、愛し合う人間……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/696, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)③", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"ちらし寿司弁当は、大将も気に入ってくれたみたい。\n厳しい人だけれど、無言で食べて頷いていたわ。\n……ここで他人の話は、しないほうが？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/697, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)③ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"ごめんなさい。うっかりしていたわ。\n私が愛しているのは、貴方だけ。\n……なるほど。愛というものは、ひどく強欲なのね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/698, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)③グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"気にされないのも……それはそれで、一抹の寂しさを覚える。\n……これも演技の一環よ。\nふふ……驚かせられたのなら、上々ね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/699, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡団地)③パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"それも含めて愛すると言ってくれるのなら、私は幸せ者。\n……これがおしどり夫婦、というもの？\n口にしてみただけ。意味は、あまり理解していないわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/700, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"新婚らしいこととは、なにかしら。\n一応、女将さんから入手したデータがあるわ。\n……インストール完了。少し、相手をしてもらえる？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/701, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"こら。洗濯物を出し忘れていたわね、貴方。\nまた泥だらけで、いったいどこへ遊びに行っていたの？\n……これは、どちらかというと……母？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/702, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"選びなさい。「食事」か「入浴」か。それとも……\nここに、「私」が並ぶ……？ 行動と人は同列ではないのに……\nパートナーは、喜びや癒しに繋がる存在……ということ？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/703, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"貴方、随分とお疲れね。ここに座って。\nさあ、だんだん眠くなる。よしよし。よしよし。\n……眠くなってきた？ なら、正解みたいね。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/704, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"今日は貴方が料理を作ってくれるの？\nふふ……何を食べられるのかしら。\n楽しみにしているわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/705, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"麻婆豆腐……一緒に食べに行ったことを、覚えていてくれたのね。\nあの日と同じ山椒は、ここに。\n……ええ、思い出だから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/706, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"キムチを、手作りで？ ふふ、なかなか凝るのね。\n私も何かお返しをしなくては。\n……愛おしいとは、こういうこと？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/707, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"ちらし寿司……私が貴方に振る舞った、最初の……。\n嬉しいわ、貴方と同じ道を歩いているのだと、実感できる。\n……照れくさい？ そう、これが、そうなの……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/708, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)③", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"このVTRも、じきに終わる。\n私たちの新婚生活も……。\n……少し、名残惜しい気もするわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/709, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)③ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"愛は、つかめた？\n私も……なんとなく、だけれど。\nどんな形であれ、貴方に伝わっているのなら、それでいい。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/710, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)③グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"また演じてみればいい……ええ、そうね。\n貴方とは、ずっと一緒にいるのだから。\nそもそもが、同棲……に近いのかもしれないわ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/711, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)③パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"私の帰る場所は、貴方のいる事務所。\nあそこが、私のいるべき場所。\n私たちの生活は、これからも変わらず、続いていく。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/712, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡住宅街)①", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサーと暮らすなら\n悪い気はしない……それだけ\n評価しているの。人として。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/713, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡住宅街)②", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"愛とまごころが、誰かのため\nだというのなら……そうね。\nそばにいてくれる、貴方へ。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/714, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"お仕事(VTR♡住宅街)③", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"穏やかに続く日々……。\n少し物足りないと感じるのは\n今が満たされているから？", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/715, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡住宅街)①", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"これが最後のロールプレイね。任せて。\n貴方、忘れ物はない？ \nじゃあ、いってらっしゃい。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/716, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡住宅街)①ノーマル", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"私も私の仕事をしなければね。\n家のことだって、大変だもの。\n……でも、もう少しだけ。背中が、見えなくなるまで……。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/717, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡住宅街)①グッド", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"今日の帰りは何時くらいになるのかしら。\n忙しい貴方のことだもの、それまで……起きている。\nひとりの食卓は、刺激が足りない。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/718, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"コミュイベント(VTR♡住宅街)①パーフェクト", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"するべきか迷ったのだけれど、やってみるわ。\nいってらっしゃいの……ちゅ。\n…………投げキッスよ。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/719, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡住宅街)①", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"おかえりなさい。お仕事、お疲れさま。\nこれは本当に、本心からの言葉よ。\n毎日、プロデュースを……ありがとう。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/720, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡住宅街)①ノーマル", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"改めて口にすると、少し奇妙な感覚を覚えるわね。\nこれが……新婚夫婦が抱いている感情？\n……なんて。エミュレートを解除しましょう。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/721, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡住宅街)①グッド", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"愛おしい日々とは……この生活のようなものを指すのかしら。\nたしかに、胸の奥があたたかくなった。\nこれが……愛情。そして、まごころ……？", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/722, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"スペシャルコミュイベント(VTR♡住宅街)①パーフェクト", /*bg_id*/3721, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"……どうしたの？ これは夢ではない、紛れもない現実。\n呆けている暇があるのなら、私に言うべき言葉を。\n「おかえり」には……「ただいま」でしょう？", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/723, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"少し休息に入りましょう。\n働きすぎても毒というのは、貴方に教わったこと。\n……そう。労いも、一種の愛、だったわね。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/724, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"まごころ……その過程にあるものを、貴方に。\n……途中経過を見せるのも、必要なこと。\n貴方は私のパートナーなのだから。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/725, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/7913, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"おかえりなさい、貴方。……新婚エミュレートの一環よ。\nけれど、「新」の意図がわからない。新鮮さ、なのかしら。\n…………ダーリン、というのも、いいのかもしれない。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/726, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"貴方は、その目で私の深層を覗こうとしているのね。\n食材調達の方法、調理の手際、そして、心を介して。\n捉えるのが貴方ならば……悪い気はしないわ。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/727, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/10, /*description*/"原因は不明……でもどうしてだか、過去に浸ってしまう。\nこの先も、予測不能な事象が発生するかもしれない。\nそれも含めて、観測していてほしい。零れ落ちる、私を。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/728, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/18, /*description*/"可変で不確かな「愛」は、ひとつの概念で表せない。\n私が得てきた感覚をもってしても、不明瞭なまま……。\nいつか、貴方とともに到達できるのかしら……真実の愛とやらに。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/729, /*event_id*/9003, /*memory_group_id*/5, /*chara_id*/196, /*title*/"高峯のあ", /*sub_title*/"チャンスタイムセリフ", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"速度を上げ、試行回数を増やしましょう。\n「食事」、「入浴」、「私」。究極の三択において、貴方が\n躊躇なく「全部」と答えられるよう……私は完璧を目指すだけ。", /*memory_category_type*/5, /*memory_category_value*/11, /*talk_chara_name*/"高峯のあ", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/730, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)①", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"一流の料理には一流の素材と\nそれを見抜く目が必要。\nだから私自ら足を運ぶのよ。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/731, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)②", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"疲れた？\n……ククッ、そういうこと。\n健気な下僕。休んでいいわ。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/732, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)③", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/7, /*description*/"一流は他人の手も借りるわ。\nただし、任せる他人は\n見極める必要があるけれど。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/733, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)④", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"……何故私が狩猟を行うと？\nここにはただ、\n伝手があって来たのよ。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/734, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)⑤", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"お金を持っているだけが\n一流の条件ではないの。\n見極め方を知るべきね。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/735, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)⑥", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/4, /*description*/"徒歩はあえてよ？\nふざけた仕事を持ってきた、\nどこかの誰かを躾けるため。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/736, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)⑦", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"よく鳴く豚ね。額を地面に\n擦りつけるくらいの体力は\n残しておきなさいな。", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/737, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(まずは国外へ)⑧", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"使える豚が手に入るといい\nのだけれど……その顔は何？\n自分が使える豚だと？", /*memory_category_type*/1, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/738, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)①", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/7, /*description*/"振る舞いには気をつけなさいな。\n躾の成果を主人にも見せず恥をかく……\nそんな趣味、貴方にはないと思うのだけれど。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/739, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)①ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/10, /*description*/"はぁ……呆れるほどに普段通りね。\n尻尾を振って待てすらできない。主人の躾が疑われるわ。\n愚図なら自覚を持って大人しくしているべきよ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/740, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)①グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/8, /*description*/"……周囲を警戒していた？\nそう、身を挺して守る覚悟があると。見上げた心がけね？\nああ、そこに水たまりがあるわ。絨毯になりなさい。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/741, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)①パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"相応しい振る舞いを主人に聞く……妥当なところね。\n無駄に知ったかぶりをしない素直さは、評価してあげるわ。\n己の無知と愚かさを認められるのは、美徳よ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/742, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)②", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"現地の牧場が丹精込めて飼育している豚は高級食材よ。\n……ああ、そう。仕事をしたいの。\nなら交渉してみなさい。私の役に立ちたいのでしょう？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/743, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)②ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"付け焼き刃で言語を教えても、挨拶しかできないでしょう。\n私は無様な背伸びを見せろと言っているわけではないの。\n豚にそんな芸を教えた覚えはないわ。せめて鳴くだけになさい。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/744, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)②グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"まさか、私にはふざけた企画をふっておいて、\n高みの見物が許されるとでも？ これは躾よ。\n貴方が、自分の立場を弁えられるようになるための。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/745, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)②パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/8, /*description*/"安心なさい、温厚な人々だから。\n誠意さえ持てばボディランゲージで通じるわ。\n得意でしょう？ 誠意を示すために惨めな姿を晒すのは。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/746, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)③", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"使える手札はあればあるだけいい。\n自分に有用な人間を見極めるのは、\nそれなりに得意なの。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/747, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)③ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"敵？ そんなもの、そもそもいないわ。\n気づけば跪いてくる下僕ばかりよ。それに……\n向かってくる人間は、可愛がってあげればすぐに媚びを売る。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/748, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)③グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"……貴方については、ノーコメントよ。\nようやく使える豚に仕上がったと思ったら、\nとんでもない愚行をおかすもの。今回のようにね。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/749, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(まずは国外へ)③パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"光栄に思いなさい。傍に置くというのは、それなりに\n有用な証なのだから。もっとも……どうしようもない\n豚であっても、それはそれで躾け甲斐があるのだけれど。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/750, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)①", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"スタッフは貴方だけ、ね……。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/751, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)①ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"フン、弁えた番組だと思っただけよ。\n数だけいても役に立たなければ意味がないもの。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/752, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)①グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"全員を一から躾ける手間は省けたわね。\nいくら優れた統率能力があれど、\n無駄に振るうのは三流のすることと覚えなさい。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/753, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)①パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/8, /*description*/"つまり貴方は異国の地で、私しか頼るものはない。\nククッ、いい顔をしているわね。縋りついて鳴きなさい、\n私が満たされるまで。嗚呼、楽しい……♪", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/754, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)②", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"のあも連れてきたらよかったかしら。\n彼女ならこの国の人にも気に入られると思うわ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/755, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)②ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/3, /*description*/"……いえ、しなくて正解だわ。\nそうすると、私も海に付き合わないといけないでしょう。\n無駄に汗をかく趣味はないの。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/756, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)②グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/6, /*description*/"旅行なら、別の機会に誘うわ。\n彼女の邪魔をするのは望むところではないし。\n……誰も、貴方についてこいなんて言ってないのだけれど？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/757, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)②パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"以前、のあとつかさを行きつけの寿司屋に連れて行ったの。\n……たまに誘われるのよ。「ともに卓を囲みましょう」と。\nのあは寿司の味にいたく感動したようね。お礼を言われたわ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/758, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)③", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/1, /*description*/"この国は、以前父に連れられて来たのよ。\nその時に移住を勧められたわ。ビジネスの才があるとかなんとか……\nもちろん断ったわ。あくせく働くのは趣味じゃないの。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/759, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)③ノーマル", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/10, /*description*/"ま、貴方は何も言われないでしょうけれど。\n……まさか、その未熟さで選ばれる自信があったと？\n呆れた。挑戦って、身の程を知っていないとできないのよ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/760, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)③グッド", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/0, /*description*/"己を磨くために様々な可能性に目を向けるのは有益だけれど。\n貴方が私に有益な可能性になりたいのならば、\nもっと精進することね。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/761, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(まずは国外へ)③パーフェクト", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"貴方もやってみたいなら、好きにすればいいわ。\n貴方は私じゃないもの。趣味も違うでしょう。\nもっとも、どこへ行っても貴方は私の豚で、下僕のままよ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/762, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)①", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"……………………。\nこの、口に出すのも\n忌々しい衣装は、何。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/763, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)②", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/1, /*description*/"練習なんて、今さら必要ない\nのだけれど……のあが自分と\n対話している間の戯れね。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/764, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)③", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"……さっきちらっと見えた\n茶番劇の収録セットよりは、\nマシな調理場ね。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/765, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)④", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"ほら、餌の時間よ。\n……ああ、盛りつけるのは\n床に直接がよくて？", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/766, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)⑤", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"野次馬なんて品がないわ。\n私の下僕になりたいという\n豚だけ残りなさい。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/767, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)⑥", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"なかなかの炎ね。焦がさない\nためにも、中華鍋は勢いよく\n振るうの。鞭と同じだわ。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/768, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)⑦", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"三ツ星中華のシェフ？\n私に中華を教えたいと\n泣いて乞う、ただの豚よ。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/769, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(試作中)⑧", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"一度味を知ったら、\n求めずにはいられない……\n過度な美食も、毒ね。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/770, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)①", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"私が作るのは酢豚。\nさて、調理といきましょうか。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/771, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)①ノーマル", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"選んだ理由は、特別好みというわけではないわ。\nのあの寿司と傾向を分けたかったから。\nただそれだけよ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/772, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)①グッド", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"家庭料理も高級料理も関係ないでしょう。\n何を調理しても、品性は滲み出るものよ。\n関係あるのは、育ち以上に料理人の人格ね。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/773, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)①パーフェクト", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"いつぞやの焼豚に豚が喜んでいたのを\nふと思い出したの。滴った肉汁を舐める時の愉悦の顔……\nよかったわねぇ。今度はカメラもあるわよ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/774, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)②", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"私の目がおかしくなったのかしら。\nこの、言葉にするのも憚られるセンスのエプロン……\n着用するのは本番だけなのではなくて？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/775, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)②ノーマル", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/10, /*description*/"無言の土下座、ね……。\n土下座は偉い人が行うから価値があるの。その上、何度も\n同じことを繰り返せば……芸がない上に、なけなしの価値も減るわね。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/776, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)②グッド", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"…………貴方にする仕置きの内容を考えてるの。\n社会人としての確認事項を怠った末に、この屈辱でしょう。\n褒美になってしまうようなお仕置きじゃ生ぬるいわ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/777, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)②パーフェクト", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"……この世に存在する全てのくだらない仕事を潰すわ。\nそのためには下僕として、プロデューサーとして、\n貴方もすべきことがあるわよね？ きりきり働きなさいな。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/778, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)③", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"……誰がいつ、私から離れてもいいと言った？\n包丁を持ったからといって、切り刻む豚くらいは選ぶわ。\n暴力と躾は違うの。それくらい、理解できていると思っていたけれど。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/779, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)③ノーマル", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"アァン？ 指を切りそうで怖い？\n誰に向かって物を言っているのかしら。\n私にできないことなんてありはしないのよ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/780, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)③グッド", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"もう一度躾けて、違いを味わわせてあげようかしら。\n……もしかして、それが望みだったの？\nククッ、どこまでも卑しい豚ね。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/781, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(試作中)③パーフェクト", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"躾は、主人と下僕という関係があって成立するものよ。\nその上で問うわ。貴方がいるべき場所は？\n……そう、私の足元で這いつくばるのがお似合いよ。エクセレント。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
