INSERT INTO `meetup_memory_data` VALUES (/*id*/787, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)②", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"特別に、私の手料理を試食する\n栄誉をあげるわ。\n頭を垂れて享受なさい。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/788, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)②ノーマル", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/10, /*description*/"……パイナップルを、入れる？\n一流の食材を集めた私の酢豚に、\n不要な提案をしないでちょうだい。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/789, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)②グッド", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"がっつくのはおやめなさい。\nテーブルマナーは教えたはずだけれど……豚に日本語は難しいのね。\n根気よく、何度も躾けなかった私のせいかしら。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/790, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)②パーフェクト", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"あら、顔を真っ赤にして、みっともない。\n唐辛子を倍増させて、のあが満足するレベルの辛さに調整したのよ。\n水が欲しい？ ククッ、その前足で取ってみなさいな、ほぅら♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/791, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)③", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/1, /*description*/"今日の分の食材、結構残ったわね。\n別のものを調理するか、明日の試作に回すか……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/792, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)③ノーマル", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"……ドーナツ？\n誰からの言伝かは想像つくけど、作らないわよ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/793, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)③グッド", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"肉寿司？ のあからのメッセージが来てたのね。\n……寿司に合わせる椀物や一品料理を作ってもいいわよ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/794, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(試作中)③パーフェクト", /*bg_id*/8431, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"貴方に決定権はないわよ。\n私は、試作で膨れ上がった豚の胃に、\nさらに物を詰め込もうなんて思わないの。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/795, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"暇潰しにもならない\n浅ましい企画よ。\nさっさと終わらせましょう。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/796, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"調理道具の品のよさを、\n背景セットにも分けた方が\nいいのではなくて？", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/797, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"宮保鮟鱇に\nすべきだったわね、題材。\n失敗したわ。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/798, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)④", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/8, /*description*/"あの魚頭、火柱を見て\n逃げたわ。干上がるとでも\n思ったのかしらね。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/799, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)⑤", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"魚頭も、躾けたらうるさく\n鳴きそうだわ。……安心\nなさいな。貴方が先輩よ。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/800, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)⑥", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"ん……まあこんなものね。\nは？ 隠し味の「愛」を\n入れろ？ くだらないわ。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/801, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(調理中♡)⑦", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"下劣。低俗。厚顔無恥。\nこの品のないハートの器に、\n私の料理を盛れ、と？", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
