INSERT INTO `meetup_memory_data` VALUES (/*id*/803, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/10, /*description*/"…………何度見ても、理解できない趣味のエプロンね。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/804, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)①ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"チープな服は着ない主義なの。\nシンプルかつ上品な物を。\n私のファッションは、それだけよ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/805, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)①グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"……わざわざ主義主張を否定して回るほど、\n他人に興味はないわ。私に害を及ぼさない限りはね。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/806, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)①パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/8, /*description*/"これが私に似合うと思ったのなら、\nその頭をどうにかする必要があるわね。\nまずは、そこの氷水につけてみなさいな。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/807, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"いい表情をしているわね、のあ。\n意外とわかりやすいのよ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/808, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)②ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"今、彼女が考えていることは……\n「早く試食したい」でしょうね。\n代謝がいいのか、すぐお腹が空くと以前ぼやいていたわ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/809, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)②グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"まさか、本当に「おいしくなーれ」をやるなんてね。\n……何、その顔は。私がやるわけないでしょう？", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/810, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)②パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/5, /*description*/"食べるのが楽しみね。\n試食した時よりも、私の舌を満足させるに値する\n料理が作れたのだと……期待しているわ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/811, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"なんなの、あの魚頭は。\nマイクを通さなくとも声がスタジオに響いているじゃない。\n司会も大げさ。まさにバラエティ番組向けね。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/812, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)③ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"ちょうちんを引っこ抜いたら静かになるかしら。\n……いえ、そのちょうちんから声がしそうだわ。\n触らないのが吉ね。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/813, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)③グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"あれに比べたら、貴方はずいぶんと静かだったのね。\n……今、張り合おうとしたのかしら？\nああ、そう。仕置きされたいのかと思ったわ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/814, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(調理中♡)③パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"もしかして、のあに捌かれにきたの？\n彼女も私と同様、誰かの主人になるというのなら、\n手ほどきくらいはしてあげるわ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/815, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)①", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/7, /*description*/"酢豚はね、古老肉……ふるくは傷みかけた豚肉を\n処理するために、このかたちをとった。この程度の知識はあるわよ。\n物を知らないただのお嬢様でいるつもりはなかったから。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/816, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)①ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/0, /*description*/"古い豚肉がそうであったように、何事にも利用価値はある。\n常人には感じ取れない価値をすくいあげるのも、\n優れたものの資質だわ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/817, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)①グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"真面目な大学生なのかって？ 私はね、大口を叩くだけの学生や、\n遊びとバイトにしか興味のない人間と一緒にいるつもりはないの。\n私が学ぶに値する講義は受けているけれど。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/818, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)①パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"私は天才でも勤勉でもないわ。\nただ、女王でいるための矜持はある。それだけよ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/819, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)②", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"レシピ通りに作ることは、基礎中の基礎。おざなりにはしないわ。\nけれど、私は私だけにしか作れない酢豚を作るの。\n真心？ 誰も茶番劇をするとは言っていないでしょうに。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/820, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)②ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/6, /*description*/"料理は味だけでなく、目でも楽しませるもの。\n一流なら、そこまで計算して作るのが当然よ。\nそして、私が一流の道をとらないはずがないでしょう。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/821, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)②グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/8, /*description*/"さあ、仕上げのパフォーマンスよ。\n貴方はただ、目を輝かせて楽しめばいい。\n普段の厚顔無恥さを思い出せばできるでしょう？", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/822, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)②パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/2, /*description*/"下僕の好みに合わせるつもりはないわ。\n貴方を何度か行きつけのお店に連れて行ったでしょう？\nその舌が飾りではないのなら、合わせる必要もない。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/823, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)③", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"………………はぁ。\n隠し味の注入を、ですって？", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/824, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)③ノーマル", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"合成でなんとかなさい。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/825, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)③グッド", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/1, /*description*/"…………この屈辱、何百倍にして返そうかしら。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/826, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)③グッド？", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/4, /*description*/"何度でも言うわ。ふざけるのは止めなさい。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/4, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/827, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(調理中♡)③パーフェクト", /*bg_id*/10211, /*bgm_name*/"bgm_commu_pleasure", /*pose_id*/2, /*face_id*/3, /*description*/"「ラブラブキュン」………………不愉快よ。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/828, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"庶民の住まいは\nペット禁止なんですって。\n豚から玩具になってはどう？", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/829, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"床に這いつくばって\n舐めようとする……貴方、\nセットを汚すのが趣味なの？", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/830, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)③", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"そう……貴方が\n床になりたいの。ククッ……\n踏み心地、最低ね。", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/831, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)④", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"カカア天下、ですって？\nあの魚頭、中身の脳は\n干からびているのかしら。", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/832, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)⑤", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"誰が人間と同じテーブルに\nついていいと言った？\nまずはお座りからでしょう？", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/833, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)⑥", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/9, /*description*/"さっきから愚行ばかり……。\n安心なさいな。調教道具は\n隣の部屋に。躾けてあげる。", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/834, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)⑦", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"服装のことは考えない\nようにしていたの。\n……思い出させるな。", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/835, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡団地)⑧", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"主人として、許可を\n出してあげるわ。\n早く寝なさいな。", /*memory_category_type*/1, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/836, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"アパート、ね……中はこうなっているの。\nもちろん足を踏み入れたのは初めてよ。\n経験があると思って？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/837, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)①ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"意外と利便性は担保されているようね。\nいいんじゃない、相応の生活を営めるのなら、\n外野が口を出すものではないわ。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/838, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)①グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"貴方は、ここで満足するの？\n場所よりも、誰と暮らすかを重視していると？\nおめでたい頭ね。愚かな高望みをしていないだけマシだけれど。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/839, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)①パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"この広さなら、貴方の掃除も行き届くんじゃないかしら。\nよかったわね、家事の時間が短くて済むわよ。\n何？ 私がするわけないじゃない。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/840, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/10, /*description*/"何回もうなずいて、なんだというの。\n……考えていた？ 私と、新婚になるなら？\n…………そう。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/841, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)②ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"今回のお仕事として、ふたりの疑似的な共同生活を想像しただけ\nというのなら、言及しないであげる。\n卑しい妄言の域にまで達したなら……わかっているわね？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/842, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)②グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"いつから私と貴方が対等だと思っていたのかしら。\nまだそんな反骨精神を抱けているなんて、\n最近はぬるすぎたのね。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/843, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)②パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"なら、私の居心地が良いように整えなさい。\n私に一秒でも長く、この場所に留まってほしいのなら、\nそのための仕事をこなすくらい、できるでしょう？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/844, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)③", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"じっとして。\n……やっぱり、貴方、少し変わったわ。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/845, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)③ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"試作も結局残さなかったのだから、体重も増えて当たり前よ。\nまったく、健康の管理まで主人にさせるつもり？\n仕事を増やさないで。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/846, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)③グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"「そういう時子も」？\n……ふざけていないで、様をつけなさい。\n間違えた日本語はすぐに覚えるのね、貴方は。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/847, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡団地)③パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"私からの施しも残さず、忠誠の示し方もわかるようになった。\n見上げた下僕根性を身につけたなら、ろくでもない仕事を受けた\n甲斐もあったかしら。……二度とやるつもりはないけれど。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/848, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"何、あのカンペは。\n「お風呂にする、ご飯にする、それとも」……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/849, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"何も見ていないわ。\nいい、貴方も何も見ていない。\n記憶から消しなさい、今すぐに。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/850, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"そういえば、ケータリングをとっていたのよ。\n素人料理ではなく、一流の味を知れるもの。\n風情がない？ まさか、私に労働をさせるつもりだったの？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/851, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)①パーフェクト", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"お風呂は貴方好みの熱さにしておいたの。\nええ、きっと気に入るわ、すぐに冷めたりもせず、\nギリギリ茹で豚にもならない……ククッ、調教の時間ね。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/852, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"これまでの労いで貴方が料理を……？\n突然何を言い出すかと思えば。\n貴方に、私を満足させる料理が作れて？", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/853, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②ノーマル", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/9, /*description*/"私が求める基準の高さは知っているでしょう？\nそれでもなお提供するというのなら、\n貴方、自分でハードル上げてるわよ。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/854, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡団地)②グッド", /*bg_id*/9211, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"いいでしょう。\n私も空腹なの。判定してあげるわ。\n新婚に相応しいかどうかじゃなく、私に相応しいかどうかよ。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
