INSERT INTO `meetup_memory_data` VALUES (/*id*/861, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡出迎え)①", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"オカエリナサイ……早く\n上がりなさいな。ブヒブヒと\n鳴くのは近所迷惑よ。", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/862, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡出迎え)②", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"生憎と、平凡な日々に価値を\n見出す人間ではないの。\nせいぜい楽しませなさい。", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/863, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"お仕事(VTR♡出迎え)③", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"帰ってきてまで惨めな扱いを\n受ける貴方を、皆が\n見ているわ。嬉しい？", /*memory_category_type*/1, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/864, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡出迎え)①", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"おかえりなさい。\n報告のあった帰宅時刻から、10分遅れね。\n時計の読み方をまた忘れてしまったの？", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/865, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡出迎え)①ノーマル", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"私は時計の読み方を覚えているか聞いているの。\n床に這いつくばる前に、主人の問いに答えるのが\n先ではなくって？", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/866, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡出迎え)①グッド", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"サプライズのお土産……？\nそれは、相手が喜んで初めて価値があるものよ。\n私が要求したのは、早く帰ってくること。言い訳にもならないわ。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/867, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"コミュイベント(VTR♡出迎え)①パーフェクト", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"そう、躾をされたかっただけ……。\n気づかなかったなんて、私も主人としてまだまだね。\n特別よ、どんな鞭がいいか選ばせてあげる。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/868, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡出迎え)①", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"ようやくこのふざけた衣装が脱げる……いい気分だわ。\n貴方のくだらない要求にも、耳を傾けてあげてもいいくらい。\nねぇ、ダンナサマ？", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/869, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡出迎え)①ノーマル", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"……やっぱりなし。\nだめ、言ったそばから寒気がしたわ。\n普段しないことはするものではないわね。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/870, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡出迎え)①グッド", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"ふぅん、ずいぶんと要望が多いのね。\nあまり、調子に乗らないことよ。\n耳は傾けるけれど、叶えるとは一言も言ってないのだから。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/871, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"スペシャルコミュイベント(VTR♡出迎え)①パーフェクト", /*bg_id*/9212, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"奉仕するのはあくまで自分……。\nククッ、わかってるじゃない、そうよ、それでいいの。\nなら、約束のご褒美をあげましょうか。", /*memory_category_type*/2, /*memory_category_value*/10, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/872, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"疲れたわ。\n……何をしているの、貴方も少しは落ち着きなさいな。\nうるさく動き回るだけが、豚の役割ではないの。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/873, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"押しつけがましい献身は、無能と同じ。\n主人の言葉を理解して動けるのが、一流の下僕なのよ。\nその点……今回の貴方の働きは、及第点といったところかしら。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/874, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/3611, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"愛妻料理、ね……。給仕する側だからといって、私は媚びない。\nいつだって、跪くのは貴方の方よ。涎を垂らした浅ましい顔で、\nいつまで待てができるのか……ククッ、見ものだわ。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/875, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/5, /*description*/"私のためにキリキリと働き、それ以外では傍に控える。\n以前を考えれば、随分と考えて動ける下僕に成長したわね。\n無能には鞭を。有能には褒美を。今回ばかりは、後者を望むのかしら。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/876, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/10, /*description*/"ふざけた企画に出演させた上、私が手ずから調理したものを、\n貴方は口にする権利を有している。それだけで、ただの豚には\n随分と過ぎた褒美になっていると思うのだけれど。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/877, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/18, /*description*/"主人の「愛」までも欲しがるなんて、不遜にもほどがあるわ。\n主人は豚を時には躾け、時には褒め、何があっても見捨てない。\nその事実が、私と貴方の……いえ、何でもないわ。", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/878, /*event_id*/9003, /*memory_group_id*/6, /*chara_id*/261, /*title*/"財前時子", /*sub_title*/"チャンスタイムセリフ", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/1, /*face_id*/2, /*description*/"私ともっと話したい？ それを決めるのは、貴方じゃない。私よ。\n……でも、いいわ。興が乗ってきたもの。ククッ、豚でありながら\n私の言葉に耳を傾けられる幸福に、打ち震えなさい。", /*memory_category_type*/5, /*memory_category_value*/11, /*talk_chara_name*/"財前時子", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/879, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)①", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサーさんは先輩\n……まぁ普段通りですね。\n攻めていくんで、よろしく !", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/880, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)②", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"エモには自信あるんですよ。\n高校生って時点でエモさ\nあるじゃないですか。違う？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/881, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)③", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"好きな科目は、そりゃ音楽 !\n選択だから授業少ないけど、\n一番成績いいんですよ♪", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/882, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)④", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/9, /*description*/"この時期はコンポタとか\nよく自販機で買ってますよ。\nすぐ売り切れちゃうけど。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/883, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)⑤", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"プロデューサーさんの\n学生時代って……\nやっぱ、できる人でした？", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/884, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)⑥", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/4, /*description*/"雑談くらいは演技外してても\nいいじゃないですかー。\n……だって、照れるし。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/885, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)⑦", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"卒業までの残り数か月を\nショートムービーで……。\n本当に、思い出みたいです。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/886, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、学校生活)⑧", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"役名もないし、自己投影？\nしちゃうなぁ……。\n先輩と、私……か。", /*memory_category_type*/1, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/887, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)①", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"プロデューサーさんが先輩で、みくちゃんが先輩の同級生……。\nつまり、みくちゃんも私の先輩 ! ？ 年下なのに ! ？\n……私って、そんなに後輩属性あります？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/888, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)①ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"みくちゃんのほうがちゃんとしてるっていうのは……\nそれは、そうかも。うーん、そうですね、そうすぎる。\nもしかして……いつも年下っぽいと思われてるのかな。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/889, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)①グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"こういうシチュエーションも悪くないか。\n年齢って絶対にひっくり返らないですからね。\nドラマの中の設定だし、楽しんだもん勝ちってことで !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/890, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)①パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"可愛がってもらえるのは……そりゃ、好きですよ？\nちょっと頭固いところもあるけど、頼りがいもあるし。\n……しょうがない、今日は後輩になりきってあげよっと♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/891, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)②", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"先輩、いま時間いいですか？\n……もう3年フロアの常連になっちゃいましたね。\n悪い気はしません。むしろ、けっこー嬉しいかも。へへっ♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/892, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)②ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"……あれ、もしかして目立っちゃってます？\nこれだと邪魔になっちゃうかな。\nじゃあ、ちょっと喋ったら帰りますね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/893, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)②グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/9, /*description*/"クラスの人に紹介……？\nや、いいです。ハズいし……。\n卒業まであと少しなんだから、先輩だけでいいんです。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/894, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、学校生活)②パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"相談があるんですけど……そ、その。\n……勉強、見てもらいたくて。私、成績けっこーヤバいんですよ。\n放課後、テスト勉強付き合ってくれますか……？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/895, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、学校生活)①", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"みくちゃんには絶対に負けられないんですよねー。\nでも、ときめき力か……ときめきって、なんだろ。\n私らしく、かつズギュン ! とさせるような……ロックさ……。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/896, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、学校生活)①ロック", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"オリジナルソング作っちゃうとか？\n短いフレーズだったらできるかも !\n降ってこーい、恋のインスピレーション !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/1, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/897, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、学校生活)①ロッカー", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"やっぱ……歯ギター、ですか。\nズギュンっていうか、ドキッとさせちゃいますかね。\nえ、別の意味のドキッ？ じゃあ違うかー。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/5, /*result_effect_position*/2, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/898, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、学校生活)①ロッケスト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"こう、プロデューサーさんにわーっと近づいて……\n壁に追い詰めて、足を壁にドンッ !\n……とか。どう？ ときめきました？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/6, /*result_effect_position*/3, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/899, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"昔っから人懐っこいほうではあったんですよねー。\nそりゃ、褒められると嬉しいじゃないですか。\nあ、べつにチョロいとかじゃないですからね？", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/900, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"そもそも褒められ慣れてないっていうか……\n勉強もアレだし、ギターも弾けなかったし。\nただ好きって気持ちを持ってるだけじゃ、ダメだったんですよ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/901, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"素直って言ってほしいなー、素直で純粋で、ロック !\nほら、何色にも染まらないとか言うじゃないですか。\n私、たぶんそのタイプだと思うんですよね。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/902, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"でもほんと、事務所のみんなもファンも、優しいですよね。\nプロデューサーさんだっていつも褒めてくれるし。\n離れがたい場所ってやつになっちゃいましたよ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/903, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"青春っていうかラブロマンスですよねこれ？\nま、まあ撮影の経験はあるし？ どーんと任せてくださいよ !\n……うひゃ～～……。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/904, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/0, /*description*/"ちょいちょい顔、熱くて……。\nでも迫る側なら自信ありますよ ! 崖に追い詰める感じですよね !\n……あれ、そんなサスペンスじゃない？ おかしいな。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/905, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/7, /*description*/"甘いセリフ、一部「アドリブで」ってあるんですけど。\nえ ! ？ いやいやいや、でき……でき…… ! ？\n……た、助けてみくちゃーーん ! !", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/906, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"先輩のこと見てたら、なんだか胸が痛くて……。\nこれって、恋の病……ですか？\n……べ、ベタすぎる。カットでお願いします。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/907, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"あ、そろそろ授業始まっちゃいますね。\n朝から顔見られてよかったです !\nやっぱほら、先輩見てると癒されるんで。へへっ♪", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/908, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/8, /*description*/"朝一で見て癒されるものー……ロックっぽい雲、とか。\n岩じゃないですよ、ギターっぽいの !\nピックっぽいのはよく見ますねー。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/909, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/1, /*description*/"役柄はやりやすいですよ、わりと。\n清楚系っていうか、少女漫画っぽいのよりは感覚わかるから。\nそういうのはみくちゃんに任せましょ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/910, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"そういえば今日、現場来る前に野良猫見たんですよ。二匹。\n片方が、もう片方を叱ってるみたいな雰囲気で。\nもう片方はあくびしてて……謎に既視感あったなー、あれ。", /*memory_category_type*/2, /*memory_category_value*/1, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/911, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"よかった……撮影でも本当に\n勉強しろって言われたら、\nおかしくなるところでした。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/912, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"一夜漬けじゃないと、逆に\n頭に入らないんですよねー。\n難儀な頭脳ですよ、ホント。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/913, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)③", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"ロックっぽい授業だったら\n私にもできるんじゃ……\nあ、ロック史じゃなくて。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/914, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)④", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"急接近されてドキッ ! とか\nあんまわかんない感覚かも。\nさせるほうが得意ですし？", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/915, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)⑤", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"テスト返却のときはドキドキ\nしないんですよ。ヤバイの、\nもうわかりきってるんで。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/916, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)⑥", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"みくちゃんのノートめっちゃ\n綺麗でさ、真似できないな。\n私、すぐ落書きしちゃうし。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/917, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)⑦", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"ここにいると、顎クイのこと\n思い出しちゃうな……。\nいま思うと、猫扱い ! ？", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/918, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、試験勉強)⑧", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/9, /*description*/"んー、勉強が嫌いってよりは\nその時間、ロックに浸って\nいたいって感じ……ですね。", /*memory_category_type*/1, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/919, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/8, /*description*/"図書室が空いてたらよかったんですけどねー。\nでも放課後なら人もあんま来ないし、逆に良かったかも。\n……ふたりっきりだし。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/920, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)①ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"図書室とかほとんど入ったことないんですよね、実際。\n音楽関連の本何冊かまとめ借りして……読まずに延滞して……。\nちょ、ちょっと、そんな目で見ないでください ! !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/921, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)①グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"最近はちゃんと勉強法も編み出したんですよ !\nその名も、睡眠学習 ! 読み上げ音声を録音しておいて、それを\n聞きながら寝るんです。起きたときには、ばっちり丸暗記ってね !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/922, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)①パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"放課後に勉強ってシチュエーションがもう新鮮かも。\n私ほら、一夜漬けが多いじゃないですか。あとは事務所でくらい？\n寝ないように、見張っててくださいね♪", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/923, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"わかんないとこは……これと、これと、あと……。\nな、なんですか。思った以上でした？ でも、前は何が\nわかんないかわかんなくて。前進はしてます、絶対 !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/924, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)②ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/9, /*description*/"ちょっとずつでも成長して、先輩に近づきたいんです。\nまだぜんぜん遠いけど、いつか……肩を並べられるように。\nなんて、届かない夢……かな？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/925, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)②グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/11, /*description*/"これまでも赤点回避はギリ……だけど。\nもしここで留年でもしたら、また先輩に一年遅れちゃうじゃん。\nだから……これ以上、遠くなっちゃわないように、頑張らないと。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/926, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、試験勉強)②パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"ずっと考えてられるのは、音楽と……あと、誰かさんのこと。\nふふっ……誰でしょうね？ 当ててみてくださいよ。\n案外、すぐそばにいるかもしれませんよ？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/927, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/8, /*description*/"テストの点数予測……します？\n過去に私が本当に取った点数を当てる……でも、いいですよ？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/928, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、試験勉強)①ロック", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"赤点は、まあ、そうですね……半分正解です。\nふふ……半分の科目、という意味でね。\nだ、大丈夫ですって ! 昔の話だから ! 信じて ! !", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/1, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/929, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、試験勉強)①ロッカー", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/9, /*description*/"一桁……そうですね。\n科目はトップシークレットです。\nここから先は、さすがにロックすぎるんで。ちょっと。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/5, /*result_effect_position*/2, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/930, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、試験勉強)①ロッケスト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"100点は……あ、取ったことありますよ !\n小学生のときの漢字テストで。\n何年生かは……ひみつです。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/6, /*result_effect_position*/3, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/931, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"ん～、わりと普段の感じだからやりやすいんだけど。\n自然体ってのが、余計にこう……ね。わかるでしょ ! ？\nせめて優等生だったらなー ! みくちゃんと役チェンジしません？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/932, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"試しに同級生の委員長で……やあやあ、プロデューサーくん !\n今日も美しいネクタイだね ! 課題はきっちりやってきたのかな？\nはっはっは、さすがだよ ! ……なんか違うな。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/933, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"あんまり遅くまで残っちゃダメだからね ! りーなとの約束 !\n……改めて思いますけど、みくちゃんってけっこうオカンですよね。\n年下なのにな……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/934, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサーくん、また遅刻～ ! ？ も～ !\nノート取ってあげたから、テストはちゃんとやるにゃ !\n……うん、私のみくちゃんイメージです。いい子なんですよね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/935, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/8, /*description*/"ふー……ちょっと休憩しません？\n甘いのもあるんで。糖分補給って大事ですよ。\nあと、聴いてほしい曲もあって……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/936, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/0, /*description*/"これ、最近見つけたオルタナのインディーズ。\nめっちゃロックでよくないですか？\n……うおお。なんか、それっぽい。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/937, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"覚えてます？ 先輩が最初に教えてくれた、好きな曲。\nまだ完璧には弾けないけど、実は練習してて。\n今度、聴いてもらいますね。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/938, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"……へへ、気づきました？ うん、私の声。新曲の仮歌です。\nだから音もまだガタガタなんですけど、絶対完璧に披露するんで。\n卒業LIVE、楽しみにしててくださいね、先輩。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/939, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"……んぁ？ んー……ちょっと、うとうとしてきた……。\nせっかく、先輩との勉強会なのに……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/940, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"いっつも一夜漬けだから……夜以外の勉強、眠くなる……。\n……これ、演技じゃなくてガチです。\nすみません、15分だけ休憩くださ……ふぁ。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/941, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/9, /*description*/"文字と向き合ってたら、だんだん眠くなってくるんですよね……。\n作詞だったらこうはならないんですけど……。\nそうだ、この気持ちを詩に……すぅ……。", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/942, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"ん……起きます……1秒だって無駄にしたくないし……。\n先輩との時間、いま、一番大事なんで……。\nあ…………ねえ、なんか口走っちゃいました？", /*memory_category_type*/2, /*memory_category_value*/2, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
