INSERT INTO `meetup_memory_data` VALUES (/*id*/949, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、ふたりで)⑦", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"プロデューサーさんは……\nうん、ドラムですね。\nどっしり支えてくれる感じ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/950, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君と、ふたりで)⑧", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/9, /*description*/"静かだと聴こえてきません？\nドキドキする音。\n……な、なんてねーっ。", /*memory_category_type*/1, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/951, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)①", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"終わった……テスト期間、長かったぁ……。\nやっぱロッカーは自由じゃなきゃダメなんですよ !\nさっそく練習しましょう。先輩もなんか触ってみます？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/952, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)①ノーマル", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"せっかく遊びに来てくれたのに、見てるだけでいいの？\nまー、それだけリーナロックが好きってことか。\nいいですよ、聴けなかったぶん聴かせてあげます。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/953, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)①グッド", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"常連なんだし、先輩ももう部員みたいなもんでしょ。\n一心同体？ 一蓮托生？ ファミリー的な。\nハートとソウルがつながってるんですよ♪", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/954, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)①パーフェクト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"最初に先輩見たときから、ビビっときてたんですよ。\nこの人熱いソウル持ってるなー、って。\nこれ、プロデューサーさんにも言えますね。ロッケスト !", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/955, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)②", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/10, /*description*/"プロデューサーさん。校舎前にあるアレ、知ってます？\nロッカーと猫の像。\n学校の子に聞いたら、なんか曰くつきらしいんですよ、実は。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/956, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)②ノーマル", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"……ていうかあの像、ほんとになんなんだろ？\n10年前に建てられた偉大なロッカーと、猫らしいですけど……\n猫、長くないですか？ あとなんか、ロッカー私に似てない？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/957, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)②グッド", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"ロッカーと猫って、私とみくちゃんみたいじゃないですか。\nプロデューサーさん、それ知ってて仕事請けたのかと思った。\n違うんですか？ すっごい偶然なだけ？ すご…… !", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/958, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君と、ふたりで)②パーフェクト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"あの像の前で告白すると、むこう10年は約束されるんですって。\n絆とか、想いとかの……いろいろないろいろが。\nそういう伝説っていうか、ウワサですよウワサ。ははは……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/959, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、ふたりで)①", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"結婚式あるあるみたいに\n卒業阻止ー ! ってできたらロックなんですけどね。\n演出とか、考えてみちゃいます？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/960, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、ふたりで)①ロック", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"卒業証書を破っちゃうとか !\nさすがにダメか。じゃあ、もらうとか？\nんー、それはちょっと重いかな。大事なものだし。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/1, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/961, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、ふたりで)①ロッカー", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"あっ、名案思い付きましたよ !\n留年したら同級生にもなれるし、一石二鳥 ! すごい !\n……じょ、冗談じゃないですかー。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/5, /*result_effect_position*/2, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/962, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君と、ふたりで)①ロッケスト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"一生残る思い出を作って、いつでも思い出せるようにする !\n実質在校生のままだぜーっ !\n……青春が恋しくなるって、こういうことですか？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/6, /*result_effect_position*/3, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/963, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)①", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"一回でいいから、先輩とセッションしてみたかったな。\n……ねえ、ちょっと弾いてみません？\n教えますよ、どんなコードだって。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/964, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)①ノーマル", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/9, /*description*/"FコードとBコード、しょっちゅう引っかかったな～。\nあ、役モード抜けちゃった。てへへ。\nぜんっぜん弾けなかったころが懐かしいです。成長したでしょ？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/965, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)①グッド", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"このコード進行覚えれば、私の新曲も弾けちゃうんで。\nへへっ、刻んじゃった？ 先輩に、私の音。\n…………このセリフ、キザすぎません？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/966, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)①パーフェクト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"うーん、正面からじゃ教えづらいな。ちょっとすみません。\nこうやって、左手の指先で弦を押さえて、右手はピックを……。\n……ち、近いな。かなり。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/967, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)②", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"こんな毎日も、もうすぐ終わっちゃうんですね。\nやだなー……なんで私、一年遅く生まれちゃったんだろ。\nせめて一緒に卒業したかった、なぁ……。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/968, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)②ノーマル", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/1, /*description*/"そりゃ、寂しいですよ。新学期から、先輩いないとか。\n……遊びに来てくださいね。\nそれで、私の音……聴きにきてください。いつでも。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/969, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)②グッド", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"……大丈夫です。寂しいけどさ、今生の別れじゃないし。\nっていうか、卒業して大人になった先輩に会うの、楽しみ。\n連れてってくださいね。高校生じゃ行けなかった、遠いとことか。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/970, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)②パーフェクト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"……決めた。先輩と同じ大学、目指してみます。\nやればできるってこと、知ってるでしょ？\n音楽以外に、夢中になれること……増えたから。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/971, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)③", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"なんか一瞬、アイドル卒業ーって頭によぎっちゃった。\n全然考えたことないのに、連想しちゃったみたいです。\nぜんぶ、いつかは終わっちゃうんですもんね。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/972, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)③ノーマル", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"ぜんっぜん想像つかないや。高校卒業した後だって。\n未来って、やっぱわかんないですねー。\nいまのことだけで精一杯なんですもん。そりゃそっか。", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/973, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)③グッド", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"って、しんみりしてたらロックじゃないですね。\n卒業式とか、派手に壊してこそロックでしょ !\nその卒業待ったーー ! ギュイーーン ! ! みたいな ! ？", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/974, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)③パーフェクト", /*bg_id*/6781, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"まあでも、アーティストの魂百までって言うし。言わない？\nとにかく前人未踏のご長寿ロックアイドルになってやりますよ !\nリーナロックは時代だって創る ! 待て、来世紀 !", /*memory_category_type*/2, /*memory_category_value*/3, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/975, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)①", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"菜々ちゃんに借りた恋愛\nゲーム、だいぶ古いんだけど\nレトロゲーム好きなのかな？", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/976, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)②", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/4, /*description*/"うー、ラブレター……\n歌詞書くより難しい……\n恋ってなんなんだぁ～……。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/977, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)③", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/1, /*description*/"プロデューサーさん宛てに\nお礼の手紙を書けば……\nそ、それはそれでハズいか。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/978, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)④", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/7, /*description*/"もしかして相手役なしのが\nまだ恥ずかしくなかった？\nくーっ、どうにでもなれー !", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/979, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)⑤", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"ん……ちょっと風冷たいな。\nこっち、来てください。\n近いほうがあったかいんで。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/980, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)⑥", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"ここで叫んだら気持ちいい\nだろうなー。ね、一緒に\nやってみません？", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/981, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)⑦", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/9, /*description*/"屋上で授業サボるのとか、\n憧れだったな。実際は授業中\n寝……ね、寝てませんよ？", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/982, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君の背を眺めて)⑧", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/5, /*description*/"恋も、ロックみたいに\n人生の全部を奪っていく\nものなんですかね。", /*memory_category_type*/1, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/983, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)①", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/3, /*description*/"……卒業式なんか、参加したくない。\nあーあ、この気持ち……結局、伝えられないのかな。\n伝えなきゃ、意味……ないのに。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/984, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)①ノーマル", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/7, /*description*/"……もうすぐ告白シーンだって思うと、汗かいてきました。\nで、でも、クールにキメますし？\nそんでロックに ! 任せてください、これがリーナラヴですよ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/985, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)①グッド", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/0, /*description*/"伝えないで終わるよりは、当たって砕けたい。\nこの子の気持ち、わかるな。それでこそロッカーですよ !\n想いは言葉にしなきゃ、伝わらないんですから。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/986, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)①パーフェクト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/11, /*description*/"寂しい気持ちを押し込めて、笑顔でバイバイもいいけどさ。\nそれじゃロックじゃない。カッコ悪いことだって叫ばなきゃ。\nたぶん、私だってそうします。伝えたい想いがあるなら。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/987, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)②", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"先輩の卒業式サボって屋上って、\nこの子もなかなかロックですよね。\n私も同じ状況になったら、どうするのかな。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/988, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)②ノーマル", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/0, /*description*/"でも、憧れの人が手の届かないとこに行っちゃうって\n確かに寂しいのかも。\nみくちゃんが言ってたの、これかー。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/989, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)②グッド", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"っていうか、屋上とかほとんど来たことないから、新鮮ー。\n屋上LIVEも、開放感あっていいですよね。\n音がどこまでも響いていきそう。空なんて超えちゃってさ。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/990, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君の背を眺めて)②パーフェクト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/5, /*description*/"もし私にも好きな人がいたら……引き留めちゃいそうです。\nどこにも行かないでって、無茶なわがまま言っちゃったりしてさ。\n……勉強に恋愛ゲームやりすぎちゃいましたかね？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/991, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君の背を眺めて)①", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/11, /*description*/"考えたんですよ、卒業式で何が一番ロックになるのか。\nただの卒業式じゃ、伝説のロッカーにはなれません。\nいちロックアイドルとして……私の答えを見せます !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/992, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君の背を眺めて)①ロック", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"やっぱ王道に、自分の想いを歌に乗せて届ける !\n合唱だけじゃなくて、ソロで ! ビートに身を委ねて !\nそれこそが、キング・オブ・ロックだぜ !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/4, /*result_effect_position*/1, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/993, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君の背を眺めて)①ロッカー", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/6, /*description*/"ステージに上がって、想いの丈を叫んじゃうんですよ !\n校長先生と一緒にマイク持ったっていいし♪\n言いたいこと言えないまま、大人になんかなってやるもんかーっ !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/5, /*result_effect_position*/2, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/994, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"ロックイベント(君の背を眺めて)①ロッケスト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/5, /*description*/"やっぱりぶっ壊しちゃうべきですよ、卒業式 !\nや、物騒な話じゃなくて、常識を壊すみたいなさ？\n私たちにしかできない卒業式、ここの子たちに届けたいです !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/6, /*result_effect_position*/3, /*appear_effect_type*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/995, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)①", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/3, /*description*/"……このままじゃ、だめだ。\n明日からはもう高校生じゃないんだし、あの人は……。\n手紙なら……読んでくれるかな。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/996, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)①ノーマル", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/7, /*description*/"……ねえ確認なんですけど、このラブレターって小道具ですよね？\n私が書くわけじゃ、ないですよね？\n…………え、書くんですか。私が。え？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/997, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)①グッド", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/1, /*description*/"手紙にオリジナルの詩とか書いてもいいですよね～。\nそれで告白のときに歌う ! どう ! ？ いや伴奏もほしいか。\nでもそこまですると、ちょっとおもしろくなっちゃうかな……。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/998, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)①パーフェクト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"でもラブレターってあんまりロックじゃなくないですか？\nなんていうんだっけ……ややこしいじゃなくて、奥ゆかしい？\n……そうだ、ちょっと台本変えていいですか？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/999, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)②", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/6, /*description*/"ところで、みくちゃんとの対決の件……。\nいまのところ、私がリードってことでいいのかな？\nこーんな後輩に迫られたら、ときめきまくりでしょ？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1000, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)②ノーマル", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/4, /*description*/"まだ足りない？ まいったなー、もう全力なのに……。\nい、いやいや、リーナラヴの本番は、これからですよ !\n……っていうか、なんで対決してるんだっけ？", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1001, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)②グッド", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"もっとこう、ときめき ! って音声鳴ったらいいのになー。\nプロデューサーさん、口で言ってもいいですよ。\nピロリンって、恋愛ゲームみたいに♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1002, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)②パーフェクト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/3, /*description*/"……カウンター食らうこともちょいちょいあるんですけどね。\nプロデューサーさん、いつも以上にスマートだし……\nいや、私が役に入り込みすぎなのかも……う～っ !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1003, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)③", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/9, /*description*/"あの銅像の下で、告白すれば……。\nけど、あんな人目につく場所でか……。\n……ウワサはウワサ、だけど。もし、叶うなら。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1004, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)③ノーマル", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"このウワサ、本当に学校に伝わってるらしくて。\nプロデューサーさんと、これから10年の……ううん、永遠の絆、\n試しちゃいます？ ……でも、告白はしなきゃなのかー。", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1005, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)③グッド", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/6, /*description*/"みくちゃんにも、撮影のついでに告っておこうかな。\nま、私たちの友情は既に不滅なんですけど。\n案外好きそうだし、びっくりしそうだし。顔見るの楽しみー♪", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1006, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君の背を眺めて)③パーフェクト", /*bg_id*/8421, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"さすがに願掛けだってのはわかってますよ。\nでも本当に伝説とかおまじないがあるんなら、\n試したくなりますよ。やれること全部やりたいのが、私なんで !", /*memory_category_type*/2, /*memory_category_value*/4, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1007, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君に、この声を)①", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/5, /*description*/"そりゃ、緊張しますけど、\nちゃんと告白するから。\n……受け止めてくださいね。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1008, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君に、この声を)②", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/6, /*description*/"あ。桜の花びらついてる。\nほら。プロデューサーさんも\n可愛いとこありますよね♪", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1009, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"お仕事(君に、この声を)③", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/11, /*description*/"プロデューサーさん。\n私の本気の全力で……\n魂、震わせてやるんで。", /*memory_category_type*/1, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1010, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君に、この声を)①", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/8, /*description*/"先輩、待ってください。\n……伝えたいことがあるんです。\n私の、声で。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1011, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君に、この声を)①ノーマル", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/9, /*description*/"……ちょっとは、緊張してますよ。でも、伝えたいから。\n先輩。絶対に、私のこと……忘れないでください。\nあなたは一番、魂が近いところにある……私の、相棒だから。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1012, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君に、この声を)①グッド", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/10, /*description*/"あなたのことを想って書いた詩も、\nあなたのことを考えて奏でた旋律でも、足りない……。\n私の想いは、歌にだってできないんだよ……っ !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1013, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"コミュイベント(君に、この声を)①パーフェクト", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/5, /*description*/"……好きです。\n最初に先輩を見た日から、私のなかにメロディがあふれて。\nこの人が、ロックそのものなんだって……思ったんです。", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1014, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君に、この声を)①", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/6, /*description*/"撮影もそろそろ終わりかぁ、なんか名残惜しいな……。\nせっかくだし、プロデューサーさんにも告白しちゃう？\nも、もちろん演技ですよ。どんなテイストでいこっかなー !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1015, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君に、この声を)①ノーマル", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/0, /*description*/"こういうのはやっとくだけ得じゃないですか？\n10年の絆ってのが、なんかだいぶリアルですけどね。\nこれからもよろしくってことで♪ ……こ、これだって告白だし !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1016, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君に、この声を)①グッド", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/2, /*description*/"リーナロックで胸張れるのも、ロックアイドルになれたのも。\n私をにわかだって切り捨てずにアイドルにしてくれた、\nプロデューサーさんのおかげだもん。ありがとうございます !", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1017, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"スペシャルコミュイベント(君に、この声を)①パーフェクト", /*bg_id*/7862, /*bgm_name*/"bgm_commu_sparkinggirl", /*pose_id*/2, /*face_id*/5, /*description*/"何十年でも、何百年でも、私と一緒にいてください。\nプロデューサーさん。私の……世界で一番、ロックな人。\n……どう、ドキっとしました？ へへへっ♪", /*memory_category_type*/2, /*memory_category_value*/5, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1018, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/3, /*description*/"プロデューサーさん、ここらへんでちょっと休憩しときません？\nえ、さっきもした？ いいじゃないですか～。\nだってこまめに入れないと……もう、ハズくてぇ……。", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1019, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/2, /*description*/"こーんな甘酸っぱい学生生活、普通ないですよ。\nそれを体験できるうえに、相手役がプロデューサーさん……。\n改めて考えると、どうしてこうなったんだろ。……ま、いっか♪", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1020, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/6241, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/6, /*description*/"そろそろあげますね、心がときめくようなご褒美 !\nわかってますよ、プロデューサーさんが喜ぶものくらいはね。\n私たち、ロックっていう名の固い絆で結ばれてるんですから !", /*memory_category_type*/3, /*memory_category_value*/11, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1021, /*event_id*/9004, /*memory_group_id*/7, /*chara_id*/178, /*title*/"多田李衣菜", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_regular", /*pose_id*/2, /*face_id*/5, /*description*/"耐性ついてきたと思ったんですけどね、なんで勝負なんか……。\nでも、絶対負けるつもりはないんで !\nプロデューサーさん、私にときめく覚悟、しといてくださいよ？", /*memory_category_type*/4, /*memory_category_value*/11, /*talk_chara_name*/"多田李衣菜", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
