INSERT INTO `meetup_memory_data` VALUES (/*id*/1047, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/2, /*description*/"おおお～っと、みくの唇は簡単には奪わせないっ !\nこんなこともあろうかと、たい焼きくわえて走ってきたのにゃ～ !\n……え？ 李衣菜チャン、「おもしれー女」ってなに？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1048, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)①パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/3, /*description*/"にゃ、にゃ～っ ! み、みくのファーストキスなのに～っ !\n……あれ？ これは……キミのネクタイ？\nう、うう……騒いじゃって……恥ずかしい……にゃ……。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1049, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/8, /*description*/"ねえ、その……た、たいしたことじゃないんだけど……。\nさっき誰とお話してたの？\n後輩の李衣菜チャン？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1050, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/10, /*description*/"次の移動の打ち合わせ？\nいや……そうじゃなくて……。\nPチャンもドラマの演技に乗ってほしいのにゃ……。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1051, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/0, /*description*/"べ、べつに、ちょっと気になっただけで……深い意味は……。\nその、どんなことしゃべってたのかな～って……。\n……にゃふふ♪ いまの演技どう？ ジェラシー感、出てたでしょ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1052, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)②パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/3, /*description*/"そ、そんなに慌てて言い訳しないでっ !\nそれじゃあ演技じゃなくて、みくがホントに李衣菜チャンに\n嫉妬してるみたいでしょ……も～～っ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1053, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/1, /*description*/"ここは青春のときめきでいっぱいの、特別な学校なんだよね。\nみくの学校生活と違って、新鮮なのっ。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1054, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③ノーマル", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/0, /*description*/"Pチャンの学校生活はどんなだった？\n思い出話、いっぱい聞かせて ! 参考にするにゃあ♪\nもしかして、ときめきでいっぱいだったり……？", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1055, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③グッド", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/2, /*description*/"だから、ドラマに入り込むなら、こうやって……腕を組んだりっ !\n学校でやったことないこと、Pチャンといっぱいやりたい。\nさあ、ときめきの学園生活の始まりにゃあ !", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1056, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、学校生活)③パーフェクト", /*bg_id*/6241, /*bgm_name*/"bgm_commu_relax", /*pose_id*/2, /*face_id*/5, /*description*/"まあ、みくにとっては、青春イコール、アイドルだからっ。\nアイドルやりながら、いつだってときめいてるよ。\nライバルのみんなにも、ファンのみんなにも……Pチャンにも。", /*memory_category_type*/2, /*memory_category_value*/6, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1057, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/10, /*description*/"窓から見えるあの銅像……\nやっぱり気になる……\nというか気が散るにゃ……。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1058, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"あっ、消しゴムが落ちて……\nそれで拾おうとした指と指が\n触れあうの ! ドキドキでしょ？", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1059, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)③", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"日直のおしごとが終わったら\n……い、一緒に帰る？\n駅まで同じ道だしっ !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1060, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)④", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"も～ ! 黒板にみくとPチャン\nの相合傘書いたの誰～ ! ？\nみ、みくじゃないしっ？", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1061, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)⑤", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/9, /*description*/"あ……ほら、あそこ。手を\nつないで帰ってるカップル\nがいる。いいな……。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1062, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)⑥", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/1, /*description*/"まだ撮影はこれからだし、\nうんっ、きっとチャンスは\nあるはずだモン。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1063, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)⑦", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"空き時間に勉強教えてよ、\nって……。李衣菜チャン\nのほうが先輩なのにゃ……。", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1064, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、試験勉強)⑧", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"みくが呼べば、友だちの\nねこチャンたちがいつでも\n集まってくるのにゃあ !", /*memory_category_type*/1, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1065, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"今日の日直、キミとみくだよ。\nキミは頼りない相棒だけど……しょうがないにゃ～。\nみくと一緒で感謝することっ。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1066, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)①ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"みくは黒板ふき、キミはお花の水やり……でもいいけど、\nその……分担するより、\nい、一緒にやったほうが、早いんだよ。でしょっ？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1067, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)①グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"あっ、学級日誌 ! みくにも読ませて !\nへ～、みんないろんなこと書いてるね。そっちのページは……\nあ、ゴメン、みくの髪の毛、くすぐったかったかな……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1068, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)①パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"みんなさっさと帰っちゃうし、みくたちも……。\n……え、えっと……。も、もうっ、なんかしゃべってよっ。\nふ……ふたりきりなの……意識しちゃう……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1069, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"うう……ふたりして先生に怒られちゃった……。\n宿題を忘れるなんて、今まで絶対なかったのに～っ。\n悔しいっ…… !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1070, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)②ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"昨日の夜、キミから電話かかってきたのが嬉しくって、つい……\nにゃ ! ？ にゃにゃにゃにゃんでもないにゃ !\nと、とにかく、反省にゃあ……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1071, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)②グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"でも、悔しいだけじゃないかも。\n怒られたのも、キミとおそろいの思い出だもんねっ。\n反省したら、帰りに美味しいもの食べて、すっきりしよっ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1072, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、試験勉強)②パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"みく、わかってたよ。みくがひとりで怒られないように、\nキミもわざと忘れたふりしてくれたんだよね。\nまったくもう……そういう優しいとこ、ズルいのにゃ♪", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1073, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/9, /*description*/"みくはここからどかないもーん。\nノート広げてるPチャンの邪魔になってたって、\n知らないし。つーん !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1074, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、試験勉強)①キャット", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"だってPチャン、みくのこと忘れてるし。\nさっきだって李衣菜チャンによそ見してたでしょ。\nふーん、否定しないんだ……してくれたっていいのに。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/7, /*result_effect_position*/1, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1075, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、試験勉強)①ニャンと ! ？", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"そのノート……もしかして、\nみくのために企画を練ってくれたとこ ! ？\nごめんねっ、自販機で缶ジュース買ってくるにゃ～ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/8, /*result_effect_position*/2, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1076, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、試験勉強)③ニャンダフル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"ちょ、なでなでは反則にゃ～ ! もう、ちょっと拗ねてみただけ。\nPチャンがいつもみくのこと考えてくれてるのは、\n瞳を見ればわかるにゃ。ねこチャンだモン。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/9, /*result_effect_position*/3, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1077, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/7, /*description*/"あっ♪ もしかして、また教科書忘れたの？\nまったくキミは、しょうがないにゃあ～。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1078, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/6, /*description*/"先生に予備を借りたの……。\nあ、そう……。\nべ、べつにフキゲンじゃないにゃ ! これは……おなかすいた顔 !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1079, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"ほら、机をくっつけるっ。\nそんな遠くで、ちゃんと見えてるの？\nもっと椅子も寄せないと。ふふっ、ぼんやりなんだから !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1080, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)①パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"一緒に覗いてるからって……固くならないでってば……。\n近すぎて集中できないのは……みくも同じ……。\n……う、うう～、みくだって、顔に出さないようにしてたのに～ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1081, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/3, /*description*/"試験も近いしねっ、残って勉強するなら、\nつ、つきあってあげてもいいけど……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1082, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/4, /*description*/"へえ、後輩の李衣菜チャンとする約束してるんだ……。\nふーん……相変わらず、仲いいんだね。\nじゃ ! みくは帰るから ! !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1083, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"出題範囲はここからで……今回のポイントは……。\nにゃふふっ、まかせなさいっ♪\nお礼なら、駅前のパフェでいいよ !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1084, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)②パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"お礼してくれるの？ じゃあ、あの、あのね？ 授業じゃ\n習わないコト、みくに教えてほしい……え？ 聞こえないって……\nちょっと ! そんなに顔を近づけたら……だ、ダメ～ ! どーん !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1085, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/5, /*description*/"ね、Pチャン。\nみくと李衣菜チャンのときめき勝負、\nいまのところ、どっちが優勢？ ねっ、ねっ、どっち？", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1086, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③ノーマル", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/0, /*description*/"李衣菜チャンは純真無垢で、いい子だよね。\nそれに比べて、みくはズルい子かもしれない。\nでも、負けたくないのっ。Pチャンの判定ならなおさら…… !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1087, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③グッド", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/2, /*description*/"みくと一緒にいるほうがドキドキする、でしょ？\nにゃふふっ、あたり？ Pチャンを見ててそう思ったの !\nでも、ずっと見てると……今度はみくがドキドキしちゃう……。", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1088, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、試験勉強)③パーフェクト", /*bg_id*/6761, /*bgm_name*/"bgm_commu_job", /*pose_id*/2, /*face_id*/8, /*description*/"決められないなら、もっと頑張らなきゃだよね。\n今のままのみくじゃダメなの。ちょっとでも背伸びしなきゃ。\nだって、もう……時間がないからっ…… !", /*memory_category_type*/2, /*memory_category_value*/7, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
