INSERT INTO `meetup_memory_data` VALUES (/*id*/1094, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"お仕事(君と、ふたりで)⑥", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"演奏は技術じゃないって\nわかるよね。李衣菜チャンの\n音はキラキラしてる。", /*memory_category_type*/1, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/0);
