INSERT INTO `meetup_memory_data` VALUES (/*id*/1100, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、ふたりで)①パーフェクト", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"ど、どうしようっ、キミがうつむいたから、みくも\nうつむいちゃったよ～っ。こ、こんなのもう言い訳できないしっ。\nうう、今のみく、どんな顔してるの……ほっぺが熱いにゃ……。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1101, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、ふたりで)②", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"そうだ ! 駅前に新しいたい焼き屋さんができたのっ。\nよっていかない？\nその……もちろん、時間あったらでいいんだけどっ……。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1102, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、ふたりで)②ノーマル", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/10, /*description*/"そっか、用事があるんだ。じゃあしょうがない、よね……。\n……もうっ……みく、どんだけ勇気出したかも知らないで…… !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1103, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"コミュイベント(君と、ふたりで)②グッド", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"クリームに抹茶、イチゴもあって……キミが好きって言ってた味も\n……はっ、みく、しゃべりすぎ……？\nべ、べつに……今日ならすいてるかなって思っただけだから !", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/1);
