INSERT INTO `meetup_memory_data` VALUES (/*id*/1106, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、ふたりで)①キャット", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/1, /*description*/"飴？ くれるの？\n嬉しいけど、あれはごはんが欲しいときの\n鳴き声じゃなくて……。ま、まあ、いただくにゃ……おいし～。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/7, /*result_effect_position*/1, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1107, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君と、ふたりで)①ニャンと ! ？", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"そうそう ! 遊んでほしいときの鳴き声にゃ !\nえへへ、そのねこじゃらし、どこから持ってきたの？\nほっ ! えい、ねこぱんち ! すきありっ、Pチャンに……えいっ♪", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/8, /*result_effect_position*/2, /*appear_effect_type*/4);
