INSERT INTO `meetup_memory_data` VALUES (/*id*/1109, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君と、ふたりで)①", /*bg_id*/9242, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"……夕暮れで、ちょっといい雰囲気……？\nも、もしかして、今なら……手をつないじゃったりとか、\nできちゃう……？ さりげなく……みくの手を……ちょんちょん。", /*memory_category_type*/2, /*memory_category_value*/8, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
