INSERT INTO `meetup_memory_data` VALUES (/*id*/1139, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君の瞳を見て)①ニャンと ! ？", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/1, /*description*/"Pチャンの匂いがある場所は、安心できるのっ。\nそこはみくの居場所だし、\nお家みたいなものなのにゃ♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/8, /*result_effect_position*/2, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1140, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"キャットイベント(君の瞳を見て)①ニャンダフル", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"Pチャンはこれで、ずっと、いつまでも、\nみくの香りでみくのことを思い出してね。\nもし卒業して、遠くへいったとしても……にゃ～んてっ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/9, /*result_effect_position*/3, /*appear_effect_type*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1141, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)①", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/10, /*description*/"もしもだよ。李衣菜チャンがキミのことが好きで、\n今日、告白しようって思ってるなら……。\nみくは……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1142, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)①ノーマル", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/3, /*description*/"キミはそのときは……そのときは李衣菜チャンのほうを……。\n……どうしよう。なんで言えないの。\n「李衣菜チャンを選んで」って。大切な後輩なのにっ…… !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1143, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)①グッド", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/0, /*description*/"きっとね、お似合いのカップルだって思う。\n李衣菜チャンはいい子だし、\nキミは……どこか抜けてるとこあるから、隣にいる人が必要だよ♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1144, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)①パーフェクト", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"決めなきゃいけないんだ……みくの気持ち。\nみくももう逃げないって。\n勝負だよ、李衣菜チャン。みくにとっては、そう、ホントの勝負 !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1145, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)②", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"解散にゃ !\n……って、もう何回くらい言ったかにゃあ、\n李衣菜チャンとユニットになってから。ふふっ♪", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1146, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)②ノーマル", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/0, /*description*/"言えば言うだけ、ずっと一緒にいられるような気がするから、\n不思議だね。えへへっ。\nだからまた叫ぼっ。Pチャンもいっしょに……解散にゃ～☆", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1147, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)②グッド", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/1, /*description*/"最後に余った同士って思ったことはあったけど、理由なんて\nどうでもいいのっ。仮にそうだったとしても、\n運命って思うから。Pチャンが作ってくれた運命なんだ、って !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1148, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)②パーフェクト", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/5, /*description*/"これまでみたいに、これからもふたりで過ごしたいな。\n10年だって、20年だって、大活躍してやるの !\nそして事務所に、みくと李衣菜チャンの銅像を建てるのにゃー !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1149, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)③", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/8, /*description*/"もしね。もしもだよ。\nアイドルとプロデューサーじゃなくて、別の形で出会っていたら、\nみくとPチャン、どんな仲になってたんだろうね。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/0, /*result_effect_position*/0, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1150, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)③ノーマル", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/3, /*description*/"たとえば……ペットショップの店員さんとお客さんとか。\nって、そういう話じゃないのっ、そうじゃなくて…… !\nむ～……上手く言えないにゃ……。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/1, /*result_effect_position*/1, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1151, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)③グッド", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/9, /*description*/"ドラマみたいに同級生だったら、こんなに悩むこともないのにね。\nみくはもともと、自分の気持ちに一直線なタイプだし。\n壊れるのが怖くて進めなくなるなんて、思ったことなかったの。", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/2, /*result_effect_position*/2, /*appear_effect_type*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/1152, /*event_id*/9004, /*memory_group_id*/8, /*chara_id*/127, /*title*/"前川みく", /*sub_title*/"スペシャルコミュイベント(君の瞳を見て)③パーフェクト", /*bg_id*/3561, /*bgm_name*/"bgm_commu_tension", /*pose_id*/2, /*face_id*/2, /*description*/"ううんっ、なんでもないよっ。今の出会いが、ベストなのっ♪\n一番輝きたいみくと、みくを一番輝かせてくれるPチャンだモン。\nアイドル前川みくの恋人は、ファンのみんなにゃ～ !", /*memory_category_type*/2, /*memory_category_value*/9, /*talk_chara_name*/"前川みく", /*wipe_chara_id*/0, /*wipe_pose_id*/0, /*wipe_face_id*/0, /*result_effect_type*/3, /*result_effect_position*/3, /*appear_effect_type*/2);
