INSERT INTO `meetup_memory_data` VALUES (/*id*/156, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"ううん、だめよはぁと……現状を嘆いて吠えるんじゃなくて、\n突破することを考えるの ! そう、例えば可愛くおねだりして……\nプロデューサー、おんぶして？ ……おい、無視すんな☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/157, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)①パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"ま、ただの温泉ロケじゃないのはちょっと予想してたけど。\nプロデューサーは、獣道も突っ走るはぁとたちが好きだもんな♪\n期待はば～っちり超えてあげる。覚悟しとけ☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/158, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"な、菜々パイセン ! ？ どうした ! ？\n脚が小鹿のようにがくがくだぞ ! ？\n……え？ 脚よりも腰のがヤバめ？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/159, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"無茶振りは上等でも、無理はノンスウィーテイー !\nちょい休憩しよっか。はぁ……どっこいせっ☆\nプロデューサー、湿布あったらくれ♪", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/160, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"ピンチのときも、支え合うのがユニットだもん !\nパイセン、気合の掛け声いくぞ♪\nしゅがしゅが～？ み～ん ! !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/161, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)②パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/9, /*description*/"うんうん、キッツいときはスウィーティーな気分になりたいよね。\n休憩する今がチャンスだぞ、プロデューサー。ほら、言って？\n「頑張ってるふたりは、超々々々可愛いよ」って♪", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/162, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/6, /*description*/"見て見て、プロデューサー !\nはぁと、謎の祠発見しちゃった♪\nかなり昔のっぽいけど、ちゃんと果物とか供えてあるんだよね。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/163, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"つまり～……近くに人が住んでるってこと♪\nよっしゃ、行くか ! 『しゅがしゅが☆み～ん』を\nここぞとばかりに売り込んで、熱烈なファンにしちゃる☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/164, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"おやつのチョコバーを供えて……\nしゅがーはぁとの可愛さを、全人類に届けてみせる☆\nお願いっていうより、宣誓になっちった♪", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/165, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(山中)③パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"崖の近くでも、しっかり支えてくれる人がいる。\nどっかで聞いた話で、親近感わいちゃった♪\nふふっ、元気回復したし、このまま、山を制覇すっぞ☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/166, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"パイセンと……プロデューサーと……一緒だもん……。\nぜぇ……はぁ……これくらいのサバイバル……平気だっつうの !\n…………いっけなーい ! 乱暴な言葉出ちゃったメンゴ☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/167, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/4, /*description*/"今のはカット ! カットな？ こら、プロデューサー、\nカメラを回さない ! ちょっ、菜々パイセンまで ! ？\nいい撮れ高？ ダメダメ ! ノンスウィーティーだし !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/168, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/7, /*description*/"……たしかに、スウィーティー切れかもな。\n崖登ったり茂みかきわけるのに必死で気づかんかった。\nプロデューサーは、いつもはぁとのことをよく見てくれてるよね。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/169, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)①パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"人間、必死になると本音出るっていうよねー。\nそ、今のはふたりへの素直な気持ちだから。\nちょい言葉は乱暴でも、忘れてとまでは言わないぞ♪", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/170, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/10, /*description*/"あーん、脚が棒になったー ! もう動けないかもー !\nそしたらずっとここにいて……\n3人でなっちゃうかー？ アダムとイヴ☆", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/171, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/7, /*description*/"でも、それはらめぇ……☆\nだって、はぁとはみんなのアイドルだから……♪\nよっし、あともうちょい根性出してけスウィーティー !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/172, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/3, /*description*/"そうそう、しゅがみんなら、どんな山の中でも\n生きていけるし ! ……ってそれ褒め言葉だよな？\n褒め言葉として受け取っとくぞ？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/173, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)②パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/2, /*description*/"ま、まずはアイドルとして、目の前のお仕事を\nスウィーティーにこなさないとだし？\n……アダムとイヴは、もうちょい先までお預けな？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/174, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/11, /*description*/"……っ ! あー、今、脳内ぶっ飛んでたわ。スウィーティー\nキングダムで水飴入り練乳がぶ飲みする白昼夢見てた……\n山を登るには、ちょーっとキツくなってきたかも……。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/175, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③ノーマル", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/8, /*description*/"はぁとは甘～い夢を見るだけじゃ満足しない……\n夢は現実で叶えてこそ、だもんな。どんなにキツくても、\nここは3人4脚で乗り越えてくとこ !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/176, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③グッド", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/12, /*description*/"そのとき、空から助けの手が伸ばされたのです……\nサンキュ、パイセン ! この恩はあとで絶対返すんで !\n手を掴んで……しゅがしゅが……ふんぬぅううううぅう !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/177, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(山中)③パーフェクト", /*bg_id*/7581, /*bgm_name*/"bgm_commu_regular", /*pose_id*/1, /*face_id*/5, /*description*/"はは……プロデューサーってば、\nこの手でどこまでも引っ張り上げてくれるんだよな。\n佐藤ならまだやれるだろって、力強くさ。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/178, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"わざわざ村長みたいな人が挨拶に来たんだよね。\n久々のお客さんだからって。律儀☆", /*memory_category_type*/1, /*memory_category_value*/2);
