INSERT INTO `meetup_memory_data` VALUES (/*id*/181, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)④", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"風きもちー、空気もうめー !\nパイセンも一緒に深呼吸♪\nすー、はー☆", /*memory_category_type*/1, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/182, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑤", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/1, /*description*/"お腹いっぱーい♪\n大皿いっぱい料理出てきて、\nちょい実家を思い出した☆", /*memory_category_type*/1, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/183, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑥", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/3, /*description*/"そっちの部屋、行かない方がいいかも。\nなんかお札がいっぱい貼ってあったし？", /*memory_category_type*/1, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/184, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑦", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"温泉への道は聞けたけどさ、\n立ち入り禁止の場所があるから、あとで案内するって。", /*memory_category_type*/1, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/185, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(村)⑧", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"お祭りではお面つけるんだって。\nでも急だから、はぁとたちのはないみたい。", /*memory_category_type*/1, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/186, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/0, /*description*/"びっくりしたな。村に入った途端、めっちゃもてなされたもん。\n村に若者が来たから安泰とか言われたし。\nなんか村の神様？ も満足するんだとか。", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/187, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"やっぱ山の奥なだけあって、外から観光に来る人も\n少ないらしくってさ。久々のお客さんだからって\nめっちゃ美味しい料理用意してくれたわ。スウィーティー♪", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/188, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"聞いた？ プロデューサー、聞いた？ 若者だって !\n若くてきれいなお嬢さんだって ! やっぱわかる人には\nわかっちゃうなー。はぁともまだまだぴちぴちだって☆", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/189, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)①パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/12, /*description*/"最初は遠慮があったけど、今はいろんな人と仲良くなったし。\nおもてなしのお礼に、しゅがみんから一曲どーよ？\nよし、プロデューサーからOK出たし、歌うか、パイセン !", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/190, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/7, /*description*/"さっき、この家の外れで看板見つけてさ。\nどこに案内する看板か気になったんだけど、\n文字がかすれてて読めなかったわ。", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/191, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②ノーマル", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"で、村の人に聞いたら、ここの名所は温泉と\nたくさんある祠くらいなんだってさ。\n小さい村だから、すぐ見て回れるらしいし、あとで行くか !", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/192, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"はぁとにはわかる ! あの看板が指し示すのは……\nデートスポットになる絶景☆ ってことで～、\nプロデューサー、このあとふたりで行ってみる？", /*memory_category_type*/2, /*memory_category_value*/2);
INSERT INTO `meetup_memory_data` VALUES (/*id*/193, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)②パーフェクト", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/9, /*description*/"村の人に聞いてみたんだけど、知らないって言われたんだよね。\n誰も知らない、まだ見ぬ場所……これ、番組的にチャンスじゃん？\nあとで菜々パイセンも誘って行ってみよーっと !", /*memory_category_type*/2, /*memory_category_value*/2);
