INSERT INTO `meetup_memory_data` VALUES (/*id*/196, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(村)③グッド", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/2, /*description*/"たくさんもらったから、事務所のみんなの分はあるよ。\n裁縫道具もちゃんと持ってきてるし、ひとりひとりの\n個性に合わせて、はぁとが可愛～くデコっちゃう♪", /*memory_category_type*/2, /*memory_category_value*/2);
