INSERT INTO `meetup_memory_data` VALUES (/*id*/198, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(村)①", /*bg_id*/6752, /*bgm_name*/"bgm_commu_relax", /*pose_id*/1, /*face_id*/6, /*description*/"秘境っていうだけあって、ちょっと変わった場所ではあるよね。\nここにしかない伝統とか、言い伝えとかいっぱいあって。\nレポも兼ねて、パイセンとばっちり調査してたの。", /*memory_category_type*/2, /*memory_category_value*/2);
