INSERT INTO `meetup_memory_data` VALUES (/*id*/265, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)①パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/5, /*description*/"もしかして、湯上がりはぁとに見惚れて……ぶぇっくしょん☆\nあー、湯冷めしないように、上着かけてくれんの？\nありがとう……そんなに優しいと、惚れるぞ☆", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/266, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/12, /*description*/"村を出たいっていう子がいてさ、色々話したんだよね。\n夢を叶えたいからってことだったけど、不安そうな顔してて……\nはぁとを見てみろ、大丈夫だからって伝えといた。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/267, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/3, /*description*/"上京するときって、不安と期待で胸いっぱいになるんだよね。\n無邪気だった少女時代のはぁとだって……あ、今も\n無邪気で純粋な可愛い少女だったわ。てへっ☆", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/268, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"はぁとを見てたら、わかるはずだもん。いつでもどこでも、\n夢は突っ走るパワーをくれる。それがあれば、どんな壁も\nぶち壊せるって。物理的なパワーじゃなくてな？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/269, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)②パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"村を出た先で、あの子も出会えるといいよね。自分の夢を\n肯定してくれる、唯一無二の大切な人に……おい、\nプロデューサーの話をする真面目なはぁとだぞ。よ～く聞いとけ？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/270, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/7, /*description*/"夜、誰かと一緒にいる時間って好きなんだよね。\nいつメンと飲んでるときもそうなんだけどさ、\nこうやってプロデューサーといるときも、大好き☆", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/271, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③ノーマル", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/6, /*description*/"お祭り、けっこう静かだったなー。何かの儀式？ の準備でみんな\n忙しいらしくて。ま、綺麗な着物を着たスウィーティーな\nはぁとをプロデューサーにお披露目できたし、大満足♪", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/272, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③グッド", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/2, /*description*/"独りでめげそうな夜も、明日はどうしても頑張りたいって夜も、\nプロデューサーのことを思い出してパワーもらってんの。\nつまりー、なんていうかー、ずっと一緒☆ みたいな？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/273, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(岬)③パーフェクト", /*bg_id*/8042, /*bgm_name*/"bgm_commu_shine", /*pose_id*/1, /*face_id*/5, /*description*/"毎晩、一緒にいる時間があるといいよね。\n出迎えたり出迎えられたり、家族みたいにさ。\nあ、ちょい待ち、実家から電話……プロデューサーも話す？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/274, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"お仕事(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/9, /*description*/"くぅっ……プロデューサー、\nやっぱかっけぇ……。\nはぁともヘリで駆けつけたーい !", /*memory_category_type*/1, /*memory_category_value*/5);
