INSERT INTO `meetup_memory_data` VALUES (/*id*/277, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/9, /*description*/"ふふっ、あははっ、なにこれ♪ 菜々パイセンとダッシュで\n村を出て、そしたら迎えのヘリが来てさ、こんなとんでも展開、\n一周回って笑うしかないっしょ ! あーっ、楽しい☆", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/278, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/7, /*description*/"っていうかプロデューサー、これ、村人も込みで全部ドッキリだな？\nいや～、村の様子が変だなって途中から思ってたし。\nあ ! ドッキリだったら可愛～い反応見せつければよかった !", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/279, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/12, /*description*/"マジで今、どうにかなるモードだわ !\n全速力で必死に駆けて、手を取り合って縄はしごのぼって !\nパイセン、加速するよ ! しゅがしゅが～？ み～ん ! !", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/280, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"コミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"このまま3人でどこまで行ける？ どこまで行っちゃう？\nこの3人なら、たとえ世界の果てだって !\nあははっ、言っててすっげぇ青春感あるわ☆", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/281, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/8, /*description*/"あばよ、謎の村♪ 懐かしくなったらまた来るから、\nその日までに、全員がはぁとのファンになってること !\n約束な☆", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/282, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①ノーマル", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/2, /*description*/"ヘリに乗ってもまだ見送ってくれてるみんなには、\n投げキッスをプレゼント♪ どんなときでも\nファンを想って行動するのがはぁとだもん☆", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/283, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①グッド", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/6, /*description*/"めっちゃもてなされたし、綺麗なお嬢さんって言ってもらえて\n嬉しかったけど、はぁとはまだ、どこかに留まるつもりはないの。\nまだまだプロデューサーのアイドルしなきゃだから☆", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/284, /*event_id*/9001, /*memory_group_id*/2, /*chara_id*/297, /*title*/"佐藤心", /*sub_title*/"スペシャルコミュイベント(上空)①パーフェクト", /*bg_id*/3813, /*bgm_name*/"bgm_commu_speed", /*pose_id*/1, /*face_id*/12, /*description*/"引き留めたい気持ちもわかるんだけどね……でも、はぁとには\nやりたいことがまだまだいっぱいあるの ! プロデューサーや\n菜々パイセンと一緒に、アイドル道、全速力で駆けていくとかな☆", /*memory_category_type*/2, /*memory_category_value*/5);
