INSERT INTO `meetup_memory_data` VALUES (/*id*/302, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/7, /*description*/"どこからか、胸を締めつけるような苦しみがあるんです。\n休みなく動いているからかしら。\nいえ、これはもっと、深いところから……。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/303, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/1, /*description*/"斧は渡しません。私が持ちます。\n前に立つのは危ないかもしれませんけど、\nそれでも……。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/304, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/3, /*description*/"そんなに心配なら持ってみます？ 重いですよ？\nでは、はい……ほら、言ったでしょう。\n私以外が持つには、少し重いみたいなんです。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/305, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/4, /*description*/"しっ……、話している余裕はないみたいですね。\n斧は、今はこのままで。\nどこかで別の武器が見つかるかもしれませんし。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/306, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"やらせてください。\nいつもはあなたが、私たちを守ってくれていますから。\n私だって、お役に立ちたいんですよ。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/307, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/8, /*description*/"いかにもなビルに、いかにもな人影。\n何も起きないわけがなかったんだわ。\n最近観た映画でもそうだったもの。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/308, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/6, /*description*/"セオリー通りにいくと、ここを脱出できても\n何かを解決するまで追いかけられるんです。\n解決する鍵は……斧の怨霊かしら？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/309, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/7, /*description*/"わかってます、現実だって。\nいけませんね、本当は夢だと思いたいのかもしれません。\nそれなら、楽しいハプニングというだけですから。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/310, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"映画ならヒロインが愛海ちゃんと\nプロデューサーになるのかしら？\n私はヒーローとして、活躍します♪", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/311, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/6, /*description*/"出口に向かっているはずなのに、\nどうしてたどり着かないのかしら……？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/312, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)①ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/3, /*description*/"ごめんなさい、目印を勘違いしていたみたい。\nやだ、焦りすぎですよね。\n恥ずかしいわ……正しい道へ行きましょう。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/313, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)①グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/0, /*description*/"ええ、冗談です。さすがプロデューサー、見破りますね。\n向こうから風が吹いてきていますから、ちゃんと進めていますよ。\nまだ、冗談が言えるくらいには大丈夫みたいです、私。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/314, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/8, /*description*/"私じゃ迷ってしまうみたい……。\nプロデューサー、先頭をお願いします。\n敵がきたら即座にスイッチ、連携していきましょう。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/315, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/4, /*description*/"はぁ……はぁ…… !\n大丈夫です、気にしないで……それよりも、左 !\nはぁっ !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/316, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/7, /*description*/"砂埃で、しばらくは目が見えないはずです。\nこの隙に…… !\n落ち着くのは、もう少し、お預け……ですね !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/317, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/6, /*description*/"手を汚すのは、私だけでいいんです。\nそれに、手伝ってくれているから。\n斧の、無念が……逃げるための力をくれます !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/318, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/8, /*description*/"傷つけるわけじゃない、目くらましのためです。\n私の手は、誰かを守り、癒すためのもの……。\n今は、ふたりの安全のために戦います !", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/319, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/3, /*description*/"最初から暴力に訴えるのはいけないわ。\n対話をしてわかりあえるのなら、\nそれに越したことはないでしょう？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/320, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/0, /*description*/"そういえば、歌で動物と交流した人もいたらしいですね。\nそれならここで愛海ちゃんとLIVEをすれば、\n全て丸く収まったり……？", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/321, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/7, /*description*/"いつも話し合いで解決したいと思っていますよ？\nたまに、その……聞いてくれない人がいたら、\n別の手段をとらないといけませんけど。", /*memory_category_type*/2, /*memory_category_value*/1);
INSERT INTO `meetup_memory_data` VALUES (/*id*/322, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"……難しいみたい。\n仕方のないことだけれど……それならもう、\n私も容赦しなくていいんだわ……ふふっ♪", /*memory_category_type*/2, /*memory_category_value*/1);
