INSERT INTO `meetup_memory_data` VALUES (/*id*/382, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)②パーフェクト", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/5, /*description*/"フラグ……？\nふふ、たしかに、そういうこともあるかも。\nでもあなたなら、そんなフラグ、へし折ってくれるでしょう？\n", /*memory_category_type*/2, /*memory_category_value*/3);
INSERT INTO `meetup_memory_data` VALUES (/*id*/383, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/6, /*description*/"映画やドラマなら、逃げ切れた3人はハッピーエンド。\n斧の無念も無事に昇華して、\n明日への第一歩を踏み出すエピローグが待っているわ。", /*memory_category_type*/2, /*memory_category_value*/3);
