INSERT INTO `meetup_memory_data` VALUES (/*id*/385, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(来ちゃった山頂)③グッド", /*bg_id*/7121, /*bgm_name*/"bgm_commu_girls", /*pose_id*/3, /*face_id*/8, /*description*/"そのエピローグは、新たなプロローグの始まり。\nもちろん、今度こそ暑い夏の、\nたのしーいバカンスが始まるはずです♪", /*memory_category_type*/2, /*memory_category_value*/3);
