INSERT INTO `meetup_memory_data` VALUES (/*id*/397, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"こんなに夕日って眩しかったかしら？\nでも今までで一番、暖かい光……。\nスマホがあれば写真を撮れたのに、残念ね。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/398, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"あぁ、安心したら……足が。\nごめんなさい、少しだけ座らせてくれる？\n……顔は、緩みきっているから、見ちゃダメです。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/399, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"逃げ切れたあとでも、やはり気になるわ。\nはぐれたスタッフ……現れた黒服……\n電源の切れたプロデューサーのスマホ……。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/400, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"狙いすまされているのよ、何から何まで。\nこれは、でも、あなたが何も知らないのなら……\n答え合わせは、そろそろできるのかしら。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/401, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"ただひとつ不可解なのは、あの斧。\n愛海ちゃんが言うように、本当に、女性が……？\n愛海ちゃん、興奮しすぎないように、ね？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/402, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"プロデューサーは、何か知っていますか？\nいえ、答えられないのならそのままで。大人のしがらみもありますから。\nあなたはずっと私たちの味方だった。それだけが、今の事実です。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/403, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"ふふ、愛海ちゃんがじっと見てますよ？\n労ってあげてください、一番頑張ったんだから。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/404, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"そうやってプロデューサーとおしとやかにしてると、\n可愛い女の子なのだけれど……。\nでも、あの元気も愛海ちゃんらしさ、愛海ちゃんの良さですから。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/405, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"愛海ちゃんの明るさには、私もたくさん助けられたわ。\nプロデューサーも、ですよね？\nじゃあ、ふたりでよしよししてあげましょう♪", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/406, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"私も、頑張った……？\nそんな、私はするべきことを……\nよしよしは、子ども扱いすぎますよ。もう……ふふっ♪", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/407, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/3, /*description*/"プロデューサー、斧を見なかった？\nおかしいわね、さっきまで手にあったのよ。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/408, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"ついてこなかったのなら、それが斧の意志ということね。\n少しだけ、寂しいかもしれません。\n斧がここまで、助けてくれたわけですから。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/409, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"怨念がこもった斧だなんて愛海ちゃんは言っていたけれど、\n私にはそれほど悪いものには感じなかったんです。\n寂しいけれど、きっと優しい女性だったんだわ。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/410, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)①パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"大団円を迎えられたなら、\nきっとあの斧も、満足して帰ったのね。\n一緒にいたい人のもとへ。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/411, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/7, /*description*/"スリリングなバカンスは、一生分過ごしたわ。\n私はプロデューサーとは、もっと……。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/412, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/0, /*description*/"のんびり過ごしたいのよね。\nお仕事で忙しくしている分、\n喧騒を離れて英気を養いたいじゃないですか、大人だもの。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/413, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"ドキドキ、したいわ。\n恐怖と隣り合わせの刺激じゃなくて、\nもっと熱く、もっと身を焦がす、そんな夜を……。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/414, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)②パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"まぁ、これからいくらでも機会があるから、いいのかしら。\nあなたが私のプロデューサーである限り、\nこれからも夏の時間はくれるのよね、もちろん♪", /*memory_category_type*/2, /*memory_category_value*/4);
