INSERT INTO `meetup_memory_data` VALUES (/*id*/416, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③ノーマル", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/6, /*description*/"大人として、ふたりとも頑張れたでしょう？\n守るべきものがあると強くなれた。\n子どもがいる夫婦って、こういう気分なのかしら。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/417, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③グッド", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/2, /*description*/"私ひとりなら、甘えて、寄りかかって、プロデューサーの\n負担になっていたかもしれないから。\nかっこいい私に、惚れ直してくれたでしょう？", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/418, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(山越えて海)③パーフェクト", /*bg_id*/3582, /*bgm_name*/"bgm_commu_shine", /*pose_id*/2, /*face_id*/5, /*description*/"いなかったら、何もかもを投げ出して、\nそれこそすべてから逃げ出していたかもしれないの。\nあなたさえいれば、それでいい……って。", /*memory_category_type*/2, /*memory_category_value*/4);
INSERT INTO `meetup_memory_data` VALUES (/*id*/419, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"早起きですね。\n……私は、あなたと\n会える気がしたので。", /*memory_category_type*/1, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/420, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)②", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"お疲れのあなたを癒します。\nあなたが、逃走中の私の\n支えだったんですよ。", /*memory_category_type*/1, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/421, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"お仕事(日常の朝)③", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"日常へ帰る前に少しだけ、\n浸らせてくださいね。\n夢みたいな日々だったわ♪", /*memory_category_type*/1, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/422, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/7, /*description*/"ドッキリだとわかって、ほっとしたような、\nあまりの大掛かりさにどっと疲れたような……\nでも、ようやく帰れるんだわ。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/423, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"プロデューサー、本当にドッキリだって知らなかったの？\n予想くらいはしてたんじゃないかしら？ だってあなたには、\nこちらの予想を超えた行動力があったもの。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/424, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"ただ、帰る前に……あなたにお疲れさまの癒しを。\nこんな朝早く目が覚めてしまうくらいだもの、\nまだ緊張が抜けていないんでしょう？ わかりますよ。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/425, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"コミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"非日常を過ごしたあとだと、\n日常がどれだけ尊いものか実感しますよね。\nあなたといられる日常へ帰れる奇跡に、感謝を。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/426, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/6, /*description*/"はい、膝へどうぞ。\nまだみんなが起きるまで時間があるから、\nプロデューサーも、もうひと眠りしてください。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/427, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①ノーマル", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/8, /*description*/"こんなこともあるかもって、さっきホテルで汗を流して、\n肌のケアもしたんです。逃げている間はあまりできなかったから……。\nきっと枕として、すべすべのもちもちですよ。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/428, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①グッド", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/2, /*description*/"あら、すぐ寝てしまったわ。\n穏やかな寝顔……ふふ、これは、\n私だけの頑張ったご褒美ね。", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/429, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"スペシャルコミュイベント(日常の朝)①パーフェクト", /*bg_id*/3586, /*bgm_name*/"bgm_commu_girls", /*pose_id*/2, /*face_id*/5, /*description*/"私も一緒に寝るのは……難しいですよね。\nじゃあ、交代でお願いできます？\n私は、腕枕でお願いしようかしら♪", /*memory_category_type*/2, /*memory_category_value*/5);
INSERT INTO `meetup_memory_data` VALUES (/*id*/430, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"あら、疲れが顔に出ていますよ。\nここまで休みなく、本当に頑張ってくれていましたから。\nでは失礼して……、私が癒してさしあげます♪", /*memory_category_type*/3, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/431, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/2, /*description*/"きちんと水分補給はしていますか？\nこの先まだまだ長いですからね、油断せず、\n休めるときに、私と一緒に休養をとりましょう。", /*memory_category_type*/3, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/432, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lvアップセリフ③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"ふぅ……あ、ごめんなさい。\nプロデューサーのそばにいると、気が抜けてしまって。\n少しだけ……甘えても、いいですか？", /*memory_category_type*/3, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/433, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ①", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"ゴールが見えない逃走は、こうも疲弊するんですね。\n独りだったら、耐えられなかったかもしれません。\nよかった……。プロデューサーがいてくれて。", /*memory_category_type*/4, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/434, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ②", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/10, /*description*/"絶対に逃げ切れる、今はそんな気持ちしかありませんよ。\nだってプロデューサーが、そういう目をしていますから。\nあなたが信じているのだから、絶対、大丈夫なんです。", /*memory_category_type*/4, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/435, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"絆Lv到達セリフ③", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/18, /*description*/"ドキドキしてるの、伝わってます？\nあるのは、恐怖じゃないですよ。ともに、苦難を乗り越える興奮……\nそして、あなたと誰よりも深い関係になれるかもという期待です。", /*memory_category_type*/4, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/436, /*event_id*/9002, /*memory_group_id*/3, /*chara_id*/145, /*title*/"柳清良", /*sub_title*/"チャンスタイムセリフ", /*bg_id*/0, /*bgm_name*/"bgm_commu_rock", /*pose_id*/3, /*face_id*/5, /*description*/"プロデューサーは、私の後ろに……いえ、私たちは\nアイドルとプロデューサー。どこまでも一緒だと誓った仲。\nならば、あなたと力を合わせて、どんな困難も乗り越えましょう。", /*memory_category_type*/5, /*memory_category_value*/11);
INSERT INTO `meetup_memory_data` VALUES (/*id*/437, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"水着美女のお山は……？\n「うはうは☆ ヘブン」は？\n出てきてプリーズ !", /*memory_category_type*/1, /*memory_category_value*/6);
