INSERT INTO `meetup_memory_data` VALUES (/*id*/440, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)④", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"ねえ、プロデューサー、\n亡霊さんのお山にもね、\nつまってると思うんだ。……ロマンが !", /*memory_category_type*/1, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/441, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑤", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"えっと……プロデューサーの\n裾を掴んでるのは……。\nべ、べつに怖がってないよ？", /*memory_category_type*/1, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/442, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑥", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"鬼に金棒 ! 清良さんに斧 !\nあ、いや、その、鬼って\nいうのは例えでですね……。", /*memory_category_type*/1, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/443, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑦", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"亡霊さんも黒服たちも\n災難だよね。相手が清良さん\nじゃなければ……ぶるるっ。", /*memory_category_type*/1, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/444, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(廃ビル)⑧", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"亡霊さんは未練があるんだ。\nわかるよ。あたしにもまだ、\n眺めるだけのお山たちが……うっうっ。", /*memory_category_type*/1, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/445, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"このビル、建設途中のまま\nずっと放っておかれてたっていうけど……\n何があったんだろ？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/446, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"大人の事情……？\n大人って、理不尽ばっかりで大変そう……。ま、大人のことは、\n大人なプロデューサーと清良さんに任せよっと。よろしくね♪", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/447, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"や、やっぱり、怨念が……？\nグッドなお山でむよんむよんと迫りくる美女の怨念が !\n……「むよんむよん」より「ほよんほよん」がよかった？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/448, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"何があっても、プロデューサーがいるから大丈夫だよねっ。\n暗くてじめじめした場所での探険も、逃走劇も、\nわきわき……じゃなくて、わくわくするっ !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/449, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/7, /*description*/"はっ……どこからか聞こえてくる、謎の声……？\nあつみちゃんを呼んでいる……？\n聞き耳、聞き耳……。", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/450, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/4, /*description*/"「お昼休憩は12時から交替で……」\nん？\nこれって何かの業務連絡？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/451, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"ひっ…… ! 寂しそうな女の人の声…… ! ？\nわかる……あたしにはわかる……この人はきっと……\n素敵なお山の持ち主だよ ! わきわきセンサーが大反応 !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/452, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"なになに、うんうん……え？ 「うらめしや」？\nダメだよ、欲求不満はためこんじゃ !\n欲望のままに、てっぺん目指さなきゃ ! よ～し、愛海に任せて !\n", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/453, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"清良さんには、立派な斧 !\nじゃあ、あたしの武器は……？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/454, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あたしの武器……そうだっ、\n美少女あつみちゃんでいこっ♪\nの・ぼ・ら・せ・て？ きゅるん☆", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/455, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"あたしには、この生身の両手がある !\nやわらか～い夢もふよんふゆんと掴み取る、\n疾風の右手、神速の左手が !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/456, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あたしの最大の武器、それは、踏まれても蹴られても立ちあがる\n不屈の闘志、そして欲望パワーなのですっ !\n3人で生き残るよ ! プロデューサーも一緒に……それがあたしの欲望 !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/457, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"黒服サングラスの人たちも、斧に未練を残した亡霊さん\nなのかなぁ……ああっ ! 見て、あの亡霊さん……\n普通の亡霊と何かが違うよ ! ？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/458, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/1, /*description*/"グラサンに気を取られてたけど、足があるよ ! !\nあれっ、他の亡霊さんたちもよく見たら……。\n……あっ。そういうロケ……えっと、あつみ、何も見てないよ？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/459, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"そう、お山 ! あの亡霊さんには、かわいいお山が見えるよ !\nお山だってわかれば、もう怖くないっ。\nだってあたしはお山登りのエキスパートだからね !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/460, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)①パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"あの亡霊さん……水着？ だったら、極上のお山めがけて……\nいっただっきまーす♪ あああ～……極楽っ……ごくら……って、\nなんでそっちが逃げるの～ ! 待～て～ !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/461, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"はぁ……はぁ……。\n右手からも左手からも追っ手の影が……。\nどこへ行けばいいのぉぉぉ ! ？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/462, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"もうだめ……へたり。\n右手も左手も、お山成分切れです……。\nああ……。ふわ……ふわ……。わき……わき……。はっ、幻覚？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/463, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/2, /*description*/"どこへ行ってもいい……？ 最近の愛海はいい子だったから、\nどんなお山に登ってくれても……？ やったー !\nえ、言ってない？ 言って ! あたしのために言ったことにして !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/464, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)②パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"ここは任せてふたりで先に……？ ぷっ、あははっ、\nあたしたちの前では、そんなにかっこつけなくていいよ、プロデューサー !\n一緒に逃げよっ ! この手は離さないから ! がしぃっ !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/465, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/6, /*description*/"フッ。戦いのなかで、マウンテン戦士・愛海の魂は\n覚醒したみたいだね。\n今なら、敵の動きも止まって見える…… !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/466, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③ノーマル", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/3, /*description*/"……と思ったけど気のせいでした !\nぎゃああぁぁ、逃げろーっ !\nプロデューサーも ! 早く ! ぐいっ、ぐぐいっ !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/467, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③グッド", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"いくよプロデューサー ! フォーメーションＷ !\n右と左からはさみこんで……\nいや、右も左も、いまのあたしはどっちも譲れない ! !", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/468, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(廃ビル)③パーフェクト", /*bg_id*/10061, /*bgm_name*/"bgm_commu_rock", /*pose_id*/2, /*face_id*/5, /*description*/"あっ……プロデューサーを思いっきり掴んじゃった…… !\nあの……プロデューサーも、もしかしてドキドキしてた？\nその、手のひらがね……これ、あたしの汗かな？", /*memory_category_type*/2, /*memory_category_value*/6);
INSERT INTO `meetup_memory_data` VALUES (/*id*/469, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"お仕事(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"夜の遊園地って、\nロマンチックで\n甘えやすくて……グッド !", /*memory_category_type*/1, /*memory_category_value*/7);
