INSERT INTO `meetup_memory_data` VALUES (/*id*/478, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"ところでプロデューサーはさっきから何見てるの？\n清良さん？\n清良さんは強いから大丈夫だよ !", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/479, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"そうだ、あのメリーゴーランドに乗ればいいよ !\nあのお人形さんたちみたいに、\nふたり乗りして、じっとしてればバレないよ !", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/480, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)①パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"いっそ、こっちから襲っちゃうっ？\nプロデューサー、せーので一緒に飛びだそっ !\nあははっ ! 今のあたしたち、とっても無敵っ ! !", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/481, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"夏だけど、夜風が吹き始めたら寒くなるのかな？\nこんなときは……？ そう !\n肌と肌を寄せ合って、温め合うんだよ ! ……だめ？", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/482, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"なんの ! 愛海流イメージトレーニング !\nあたしはいま包まれてる……。ふかふかで……やわらかくて……\nあったかいものに…… ! うひ……うひひひ……♪", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/483, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/0, /*description*/"えっ、上着貸してくれるの？ ありがと～♪\nくんくん……プロデューサーの匂いがする……。\nあたし、動物みたい？", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/484, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)②パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"上着と、あったかい飲み物と……まだまだあるの？\nあのね、プロデューサーがあたしのこと心配してくれる、\nその気持ちがあったかいんだよ。だから大丈夫、ぜんぜん平気っ♪", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/485, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"あれっ、灯りが見えるよ……？\nあの売店、開いてる ! ？\nよしっ、くたくたの体にエネルギーを補給しよう !\n", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/486, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/2, /*description*/"ふわふわの風船をゲット♪\n食べられない？ いいんだよ、これがあたしの補給だもん♪\nふわふわ……ぷにぷに…………ふおおおおお ! ! きたーー ! !", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/487, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③グッド", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"補給といえばもちろん、お・や・ま・成分、だよねっ♪\n売店のおねえさ～～ん ! がばーーっ !\nわわっ、プロデューサーに捕まった ! 離してぇ～、ぐすん。", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/488, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"コミュイベント(逃走、遊園地)③パーフェクト", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/5, /*description*/"チュロスとドリンクと……あっ、この抱きついてる\nぬいぐるみ、可愛いねっ ! え？ あたしとちょっと似てる？\n抱きつくポーズが？ それとも……可愛いから？ えへへ♪", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/489, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/3, /*description*/"ぎゃー ! きたーっ !\nあっちの陰から、黒服の集団がばたばたと…… !\nひぃ～っ、プロデューサー、た～す～け～て～ !", /*memory_category_type*/2, /*memory_category_value*/7);
INSERT INTO `meetup_memory_data` VALUES (/*id*/490, /*event_id*/9002, /*memory_group_id*/4, /*chara_id*/121, /*title*/"棟方愛海", /*sub_title*/"スペシャルコミュイベント(逃走、遊園地)①ノーマル", /*bg_id*/3203, /*bgm_name*/"bgm_commu_horror", /*pose_id*/2, /*face_id*/6, /*description*/"えっ、今回だけは見逃してくれるの？ 今回だけ……なるほど。\nプロデューサーでもあの人たちには敵わないなんて……。\nうわ～ん、怖かったよ～っ。ひしぃっ♪", /*memory_category_type*/2, /*memory_category_value*/7);
